insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (400, 'Cyclophosphamide: Cytoxan<br>Doxorubicin: Adriamycin', 'AC', '<p><strong>Hepatic Impairment</strong></p>

