insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (402, 'Cyclophosphamide: Cytoxan<br>Doxorubicin: Adriamycin, various<br>Paclitaxel: Taxol', 'AC/Paclitaxel (weekly) ', '<p><strong>Hepatic Impairment</strong></p>

<ul>
	<li><strong>Cyclophosphamide</strong>:<br />
	If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%<br />
	If bilirubin &gt; 5 mg/dL, do not use</li>
	<li><strong>Doxorubicin</strong>:<br />
	If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br />
	If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br />
	If bilirubin &gt; 5 mg/dL, do not use</li>
	<li><strong>Paclitaxel</strong>:<br />
	For 3-hour infusion:<br />
	If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m<sup>2</sup>)<br />
	If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m<sup>2</sup><br />
	If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m<sup>2</sup><br />
	If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br />
	For 24-hour infusion:<br />
	If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m<sup>2</sup>)<br />
	If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m<sup>2</sup><br />
	If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m<sup>2</sup><br />
	If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li>
</ul>

<p><strong>Renal Impairment</strong></p>

<ul>
	<li>Cyclophosphamide:<br />
	If CrCl 10-50 mL/min, reduce dose by 25%<br />
	If CrCl &lt; 10 mL/min, reduce dose by 50%</li>
	<li>Doxorubicin:<br />
	No dose reduction</li>
	<li>Paclitaxel:<br />
	No dose reduction</li>
</ul>
', 'High for AC, low for paclitaxel', '', 'Sparano JA, Wang M, et al. Weekly paclitaxel in the adjuvant treatment of breast cancer. N Engl J Med. 2008;358(16):1663-71.', 'ADJUVANT', '<p><strong>Doxorubicin</strong>: 60mg/m<sup>2</sup>&nbsp;IV on day 1<br />
<strong>Cyclophosphamide</strong>: 600mg/m<sup>2</sup>&nbsp;IV on day 1</p>

<p>Repeat every 21 days for 4 cycles.</p>

<p><strong>FOLLOWED BY</strong></p>

<p><strong>Paclitaxel</strong>: 80mg/m<sup>2</sup>&nbsp;IV on day 1</p>

<p>Repeat every 7&nbsp;days for 12&nbsp;cycles.</p>
', 15);
