insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (420, 'Cyclophosphamide: Cytoxan<br>Doxorubicin: Adriamycin, various<br>Paclitaxel: Taxol<br>Trastuzumab: Herceptin', 'AC/Paclitaxel/Trastuzumab', '<p><strong>Hepatic Impairment</strong></p>

