insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (473, 'Ipilimumab: Yervoy
Nivolumab: Opdivo', 'Nivolumab + Ipilimumab ', '<p><strong><u><span style="font-family:Times">Ipilimumab</span></u></strong></p>

<p><strong><span style="font-family:Times">Renal Impairment</span></strong></p>

<p><em><span style="font-family:Times">Prior to treatment:</span></em><span style="font-family:Times"> No dosage adjustment necessary.</span></p>

