insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (527, 'Cisplatin: CDDP<br>Paclitaxel: Taxol<br>Bevacizumab: Avastin', 'Cisplatin/Paclitaxel/Bevacizumab', '<p><strong>Hepatic Impairment</strong></p>

