insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (670, 'Temodar', 'Glioblastoma - Temozolamide + RT ', '', '', '', '', 'ADJUVANT', '<h4 style="margin-left:0px; margin-right:0px; text-align:left">Chemotherapy</h4>

