insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (498, 'Alpelisib: Piqray<br>Fulvestrant: Faslodex', 'Alpelisib/Fulvestrant', '<p><strong>Hepatic Impairment</strong></p>

