insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (285, 'Dexamethasone : various<br>Lenalidomide : Revlimid', 'Lenalidomide', '<b>Hepatic Impairment</b><ul><li>Lenalidomide:<br>Unknown </li></ul><b>Renal Impairment</b><ul><li>Lenalidomide:<br>Substantially excreted by kidney. Trials excluded patients with renal insufficiency </li></ul>', 'Low', '<b>Lenalidomide/Dexamethasone</b>', 'Dimopoulos M, Spencer A, Attal M, et al. Lenalidomide plus dexamethasone for relapsed or refractory multiple myeloma. $N Engl J Med. 2007;357(21):2123-2132.$', null, '<p><strong>Lenalidomide</strong> 10 mg/d PO, daily or days 1-21<br><br>Repeat every 28 days until disease progression</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (204, 'Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various', 'AD', '<b>Hepatic Impairment</b><ul><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'High', '<b>AD</b>', 'Antman K, Crowley J, Balcerzak SP, et al. An intergroup phase III randomized study of doxorubicin and dacarbazine with or without ifosfamide and mesna in advanced soft tissue and bone sarcomas. $J Clin Oncol. 1993;11(7):1276-1285.$', null, '<p><strong>Doxorubicin</strong> 15 mg/m2/d Continuous IV, days 1-4<br>Dacarbazine 250 mg/m2/d Continues IV, days 1-4<br>Repeat every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (207, 'Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'MAID', '<b>Hepatic Impairment</b><ul><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'High', '<b>MAID</b>', 'Antman K, Crowley J, Balcerzak SP, et al. An intergroup phase III randomized study of doxorubicin and dacarbazine with or without ifosfamide and mesna in advanced soft tissue and bone sarcomas. $J Clin Oncol. 1993;11(7):1276-1285.$', null, '<p><strong>Mesna</strong> 2,500 mg/m2/d Continuous IV, days 1-4<br><strong>Doxorubicin</strong> 20 mg/m2/d Continuous IV, days 1-3<br><strong>Ifosfamide</strong> 2,500 mg/m2/d Continuous IV, days 1-3<br><strong>Dacarbazine</strong> 300 mg/m2/d CIVI, days 1-3<br>Repeat every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (206, 'Cyclophosphamide : Cytoxan<br>Dactinomycin : Actinomycin<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'CAV-IE', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Dactinomycin:<br>If bilirubin > 3 mg/dL, reduce dose by 50%</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vincristine:<br>No dose reduction </li></ul>', 'CAV-High: IE-Moderate', '<b>VAC-IE</b>', 'Grier HE, Krailo MD, Tarbell NJ, et al. Addition of ifosfamide and etoposide to standard chemotherapy for Ewing''s sarcoma and primitive neuroectodermal tumor of bone. $N Engl J Med. 2003;348(8):694-701.$', null, '<p><strong>Cyclophosphamide</strong> 1,200 mg/m2 IV, day 1<br><strong>FOLLOWED BY</strong> mesna<br><strong>Vincristine</strong> 2 mg/m2 IV, day 1<br><strong>Doxorubicin</strong> 75 mg/m2 IV bolus, day 1<br><strong>Dactinomycin</strong> 1.25 mg/m2 IV, day 1 substitued for doxorubicin when cumulative doxorubicin dose reaches 375 mg/m2<br>Alternating with each cycle<br><strong>Ifosfamide</strong> 1,800 mg/m2/d IV, days 1-5 (given with mesna)<br><strong>Etoposide</strong> 100 mg/m2/d IV, days 1-5<br>Repeat cycle every 21 days for 17 total cycles</p>', null);
