insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (770, null, 'TIP', '<p><strong>Hepatic Impairment</strong></p><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li>Ifosfamide: No dose reduction needed</li></ul><p><strong>Renal Impairment</strong></p><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li><li>Ifosfamide:</li><li>If CrCl 46-59 mL/min, reduce dose by 20%</li><li>If CrCl 31-45 mL/min, reduce dose by 25%</li><li>If CrCl &lt;30mL/min, reduce dose by 30%</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin</strong> 60 mg/m2, day 1 and<br><strong>Paclitaxel</strong> 175 mg/m2 over 3 hours on day 1</p><p>Ifosfamide 1000 mg/m2 on day 1-3</p><p>Mesna 400 mg/m2 IV before ifosfamide and 200 mg/m2 IV 4 hrs after Ifosfamide</p><p><br>Repeat cycle every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (771, null, 'TIC', '<p><strong>Hepatic Impairment</strong></p><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li>Ifosfamide: No dose reduction needed</li></ul><p><strong>Renal Impairment</strong></p><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li><li>Ifosfamide:</li><li>If CrCl 46-59 mL/min, reduce dose by 20%</li><li>If CrCl 31-45 mL/min, reduce dose by 25%</li><li>If CrCl &lt;30mL/min, reduce dose by 30%</li></ul>
