insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (778, null, 'Paclitaxel', '<p><strong>Hepatic Impairment</strong></p><p>&nbsp;</p><ul><li>Paclitaxel:<br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Paclitaxel</strong> 175 mg/m2 over 3 hours on day 1<br>Repeat cycle every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (779, null, 'Vinorelbine', '<p>Hepatic Impairment</p><p>If bilirubin is &lt;2.0 mg/dL, no dose reduction</p><p>If bilirubin is 2.0-3.0 mg/dL, dose reduce by 50%</p><p>If bilirubin is 3.1-5.0 mg mg/dL, dose reduce by 75%</p><p>If bilirubin is &gt;5, omit the drug</p><p>Renal Impairment<br>No dose reduction needed</p>', 'Low', null, null, null, '<p><strong>Vinorelbine:&nbsp;30 </strong>mg/m2&nbsp;IV every week</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (780, null, 'VP', '<p>Am J Clin Oncol. 1995 Aug;18(4):293-6. doi: 10.1097/00000421-199508000-00004.<strong>Hepatic Impairment</strong></p><ul><li>Cisplatin:<br>No dose reduction</li><li>Vinorelbine</li><li>If bilirubin is &lt;2.0 mg/dL, no dose reduction needed</li><li>If bilirubin is 2.0-3.0 mg/dL, reduce dose by 50%</li><li>If bilirubin is 3.1-5 mg/dL, reduce dose by 75%</li><li>If bilirubin &gt;5 mg/dL, Omit</li></ul><p><strong>Renal Impairment</strong></p><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li>Cetuximab:<br>No dose reduction</li><li>Vinorelbine:<br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin</strong> 80 mg/m2, day 1 and</p><p><strong>Vinorelbine:&nbsp;25 </strong>mg/m2&nbsp;IV on day 1 and 8</p><p>Repeat cycles every 3 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (781, null, 'Afatinib', '<p><strong>Renal impairment&nbsp;</strong></p><p>eGFR 15 - 29 mL/min /1.73 m2 : reduce dose to 30 mg PO, once daily</p><p>&nbsp;</p><p><strong>Hepatic toxicity</strong></p><p>Monitor with periodic liver testing. Withhold or discontinue for severe or worsening liver tests.</p><p>&nbsp;</p><p>&nbsp;</p>', 'Low', null, null, null, '<p>40 mg PO once daily per FDA approval</p><p>OR</p><p>50 mg PO once daily&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (786, null, 'Erlotinib & Bevacizumab', '<p><strong>Erlotinib:</strong></p><p><strong>Renal Toxicity:</strong></p><p>For grade 3- 4 renal toxicity: Withhold Erlotinib until resolved and reinitiate with 50 mg dose reduction. Consider discontinuation.</p><p><strong>Hepatic toxicity:</strong></p><p>Total bilirubin &gt;3 times ULN: Use extreme caution.</p><p>&nbsp;</p><p><strong>Bevacizumab</strong></p><p><strong>Renal impairment:</strong></p><p><i>Renal impairment prior to treatment:</i> There are no recommended dosage adjustments.</p><p><i>Renal toxicity during treatment:</i></p><ul><li>Moderate Proteinuria: temporarily withhold bevacizumab</li><li>Discontinue in patients with nephrotic syndrome. &nbsp;</li></ul><p><strong>Hepatic Impairment: </strong>There are no recommended dosage adjustments.</p>', 'Low', null, null, null, '<p>Erlotinib 150 mg PO once daily</p><p>Bevacizimab 15 mg/kg IV once on day 1</p><p>21 day cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (783, null, 'Dacomitinib', '<p>&nbsp;<strong>Renal Impairment:</strong></p><p>Mild/Moderate Impairment: No dosing adjustment recommended.</p><p>Severe Impairment (CrCl &lt; 30 mL/min): No dosing adjustment established</p><p><strong>Hepatic Impairment:</strong></p><p>Mild/Moderate Impairment: No dosing adjustment recommended.</p><p>Severe Impairment (total bilirubin &gt; 3 -10 × ULN and any AST): No dosing adjustment established</p>', 'Low', null, null, null, '<p>45 mg PO once daily</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (784, null, 'Gefitinib', '<p>Renal Impairment:</p><p>&lt; 4% renally excreted. Has not been studied in severe renal impairment.</p><p>Hepatic Impairment:</p><p><i>Note: Obtain periodic liver function testing.&nbsp;</i></p><p>Grade 2 or higher for ALT and/or AST elevations: Withhold gefitinib&nbsp;</p><p>Severe hepatic impairment: Discontinue</p>', 'Low', null, null, null, '<p>250 mg PO daily</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (785, null, 'Osimertinib', '<p>Renal impairment:</p><p>No dosage adjustment recommended in mild, moderate, or severe renal disease. No recommendations available for end stage renal disease.</p><p>Hepatic Impairment:</p><p>No dosage adjustment recommended in mild, moderate hepatic disease. No recommendations available for severe hepatic impairment.</p>', 'Low', null, null, null, '<p>80 mg PO daily continue indefinitely&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (782, null, 'Erlotinib', '<p>For 150 mg dosing schedule:</p><p><strong>Renal Toxicity:</strong></p><p>For grade 3- 4 renal toxicity: Withhold Erlotinib until resolved and reinitiate with 50 mg dose reduction. Consider discontinuation.</p><p><strong>Hepatic toxicity:</strong></p><p>Total bilirubin &gt;3 times ULN: Use extreme caution.</p>', 'Low', null, null, null, '<p>150mg PO once a day continued indefinitely (FDA approved)</p><p>OR</p><p>25mg PO once a day continued indefinitely</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (787, null, 'Erlotinib & Ramucirumab', '<p><strong>Ramucirumab</strong></p><p><strong>Renal Toxicity:</strong></p><p>No dosage adjustment recommended</p><p><strong>Hepatic Toxicity:</strong></p><p>Child-Pugh B or C cirrhosis who received single-agent ramucirumab were noted to have clinical deterioration. Use only if risks outweight benefits.&nbsp;</p><p>&nbsp;</p><p><strong>Erlotinib:</strong></p><p><strong>Renal Toxicity:</strong></p><p>For grade 3- 4 renal toxicity: Withhold Erlotinib until resolved and reinitiate with 50 mg dose reduction. Consider discontinuation.</p><p><strong>Hepatic toxicity:</strong></p><p>Total bilirubin &gt;3 times ULN: Use extreme caution.</p>', 'Low', null, null, null, '<p>Erlotinib 150mg PO daily</p><p>Ramucirumab 10 mg/kg IV once on day 1</p><p>14 day cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (788, null, 'Etoposide + Doxorubicin + Cisplatin + Mitotane', '<p><strong>Renal Impairment &nbsp; &nbsp;</strong></p><ul><li><strong>Etoposide: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>CrCl &gt;50 mL/min: No dose reduction &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;CrCl &nbsp;15-50 mL/min: 75% of normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;CrCl &lt;15 mL/min: 50% of normal dose&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li><li><strong>Cisplatin : &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> CrCl &gt;60 mL/min: No dose reduction &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; CrCl &nbsp;45-59 mL/min: 75% of normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; CrCl &lt;45 mL/min: Consider switching to Carboplatin &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li><li><strong>Doxorubicin:</strong><br>CrCl &lt; 20 mL/min: Avoid doxorubicin. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li><li><strong>Mitotane:</strong><br>CrCl &lt;60 mL/min: Consider dose reduction.</li></ul><p><strong>Hepatic Impairment</strong></p><ul><li><strong>Etoposide: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>AST/ALT: 60-180 unit/L: 50% of normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;AST/ALT: &gt;180 unit/L: dosing based on clinical decision &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li><li><strong>Doxorubicin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>AST/ALT: 60-180 unit/L: 50% of normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;AST/ALT: &gt;180 unit/L: 25% of normal dose</li></ul>', 'Medium', null, null, null, '<p><strong>Etoposide:&nbsp;100</strong> mg/m2&nbsp;IV on days 5-7</p><p><strong>Doxorubicin:&nbsp;20</strong> mg/m2&nbsp;IV on day 1 and 8</p><p><strong>Cisplatin:&nbsp;40</strong> mg/m2&nbsp;IV on days 1 and 9</p><p><strong>Mitotane: 4</strong> g PO daily</p><p>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (789, null, 'Streptozocin + Mitotane', '<p><strong>Renal Impairment &nbsp; &nbsp;</strong></p><ul><li><strong>Mitotane:</strong><br>CrCl &lt;60 mL/min: Consider dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</li><li><strong>Streptozosin:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; CrCl &gt;50 mL/min: No dose reduction &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; CrCl 10-50 mL/min: 75% of normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; CrCl &lt;10 mL/min: Avoid Streptozosin.</li></ul>', 'Medium', null, null, null, '<p><strong>Streptozocin:&nbsp; 1,000</strong> mg IV on days 1-5 for cycle 1 and &nbsp;2,000 mg IV on days 1-5 on subsequent cycles</p><p><strong>Mitotane:&nbsp; 4 </strong>g PO daily</p><p>Repeat cycle every 28 days.</p>', null);
