insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (273, 'Ofatumumab : Arzerra', 'Ofatumumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ofatumumab &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ofatumumab &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li></ul>', 'Low', '<b>Ofatumumab</b>', 'Arzerra [package insert]. <br> Wierda WG, Kipps TJ, Mayer J, et al. Ofatumumab as single agent CD20 immunotherapy in fluda-rabine-refractory chronic lymphocytic leukemia. $J Clin Oncol. 2010;28:1749-1755.$', null, '<p><strong>Previously untreated CLL:</strong><br><strong>Ofatumumab: </strong>300 mg IV on Day 1 followed by 1,000 mg IV on Day 8 (Cycle 1)<br>Then: 1,000 mg IV on Day 1 of subsequent 28-day cycles for a minimum of 3 cycles until best response or a maximum of 12 cycles.</p><p><br><strong>Refractory CLL:</strong><br><strong>Ofatumumab:</strong> 300 mg IV, initial dose,<br>Followed 1 week later by<br><strong>Ofatumumab:</strong> 2,000 mg IV, &nbsp;every week for 7 weeks</p><p>Followed 4 weeks later by<br><strong>Ofatumumab:</strong> 2,000 mg IV, &nbsp;every 4 weeks for 4 doses.<br>Repeat every 28 days for 4 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (682, '', 'FP + Trastuzumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Trastuzumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Trastuzumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> No dose reduction.</li></ul><p>&nbsp;<strong>Cardiac Impairment</strong></p><ul><li><strong>Trastuzumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> If<strong>&nbsp;</strong>EF decreases ≥16% &nbsp;from baseline &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong> &nbsp; OR &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;EF below normal limits and ≥10% decrease from baseline &nbsp; &nbsp; &nbsp;Hold treatment for 4 weeks and repeat LVEF every 4 weeks. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Resume treatment if EF returns to normal limits within 4 to 8 weeks and remains at ≤15% decrease from the baseline value. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Stop treatment permanently if EF declines persistently for &gt; 8 weeks or for &gt;3 incidents of interruptions in treatment for cardiomyopathy.</li></ul>', 'High', '', '', 'METASTATIC', '<p><strong>5-Fluorouracil:&nbsp;</strong>800 mg/m<sup>2</sup>&nbsp;IV continuous infusion on days 1-5</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (3, 'Temozolomide : Temodar', 'Temozolomide+RT', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate for IV<br> <=75 mg/m<sup>2</sup> PO is minimal-low;<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', '<b>Temozolomide+RT</b>', 'Stupp R, et al.  Radiotherapy plus Concomitant and Adjuvant Temozolomide for Glioblastoma.$N Engl J Med 2005; 352:987-996$', null, '<b>Temozolomide</b> 75 mg/m<sup>2</sup> daily along with radiation<br> <b>FOLLOWED 28 DAYS LATER BY</b><br><b>Temozolomide<sup>e</sup></b> 150 mg/m<sup>2</sup> daily for 5 days<br>Repeat every 28 days.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (6, 'Temozolomide : Temodar', 'Temozolomide', '<b>Hepatic Impairment</b><ul><li>Temozolomide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate for IV<br> >75 mg/m<sup>2</sup> PO is moderate-high   ', '<b>Temozolomide</b>', 'Gállego Pérez-Larraya J, et al.Temozolomide in elderly patients with newly diagnosed glioblastoma and poor performance status: an ANOCEF phase II trial.$J Clin Oncol. 2011 Aug 1;29(22):3050-5. doi: 10.1200/JCO.2011.34.8086. Epub 2011 Jun 27$', null, '<b>Temozolomide</b> 150 mg/m<sup>2</sup> every day for 5 days per 28-day cycle<br> ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (817, null, 'Hydroxyurea', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Hydroxyurea: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction &nbsp; &nbsp;&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Hydroxyurea: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> &nbsp;If<strong> </strong>CrCl ≥60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;60 mL/min: Reduce initial dose by 50%.</li></ul>', 'Low', null, null, null, '<p><strong>Hydroxyurea:</strong> 1-5 g/day PO</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (809, null, 'Linker Regimen', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Daunorubicin:</strong><br>If bilirubin 1.2-3 mg/dL: 75% of usual dosage.<br>If bilirubin 3.1-5 mg/dL: 50% of usual dosage.<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL: 50% of usual dosage.<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL: Do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>L-Asparaginase: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Do not use in patients with severe hepatic impairment.</li><li><strong>Teniposide:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 3 mg/dL: 50% of usual dosage.</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 5 mg/dL: Do not use</li><li><strong>Leucovorin:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Daunorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>L-Asparaginase:</strong><br>No dose reduction</li><li><strong>Teniposide:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min: 50% of normal dosage.<br>If CrCl &lt; 30 mL/min: Do not use</li><li><strong>Leucovorin:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Remission induction:</strong></p><p><strong>Daunorubicin:</strong> 50 mg/m2 IV every 24 hours on days 1-3 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Vincristine:</strong> &nbsp;2 mg IV on days 1, 8, 15 and 22 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Prednisone:</strong> 60 mg/m2 PO divided into 3 doses on days 1-28 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>L-Asparaginase:</strong> 6,000 units/m2 IM on days 17-28.</p><p>If bone marrow on day 14 is positive for residual leukemia,</p><p><strong>Daunorubicin:</strong> 50 mg/m2 IV on day 15</p><p>&nbsp;If bone marrow on day 28 is positive for residual leukemia,</p><p><strong>Daunorubicin:</strong> 50 mg/m2 IV on days 29 and 30 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Vincristine:</strong> &nbsp;2 mg IV on days 29 and 36 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Prednisone:</strong> 60 mg/m2 PO on days 29 to 42 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>L-Asparaginase:</strong> 6,000 units/m2 IM on days 29 to 35.</p><p><strong>Consolidation therapy:</strong></p><p><strong>Treatment A</strong> (cycles 1, 3, 5, and 7)</p><p><strong>Daunorubicin:</strong> 50 mg/m2 IV on days 1 and 2 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>Vincristine:</strong> &nbsp;2 mg IV on days 1 and 8 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>Prednisone:</strong> 60 mg/m2 PO on days 1 to 14 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>L-Asparaginase:</strong> 12,000 units/m2 on days 2, 4, 7, 9, 11, and 14</p><p><strong>Treatment B</strong> (cycles 2, 4, 6, and 8)</p><p><strong>Teniposide: </strong>165 mg/m2 IV on days 1, 4, 8, and 11</p><p><strong>Cytarabine:</strong> 300 mg/m2 IV on days 1, 4, 8, and 11</p><p><strong>Treatment C</strong> (cycle 9)</p><p><strong>Methotrexate:</strong> 690 mg/m2 IV over 42 hours&nbsp;</p><p><strong>Leucovorin:</strong> 15 mg/m2 IV every 6 hours for 12 doses beginning at 42 hours.</p><p>&nbsp;</p><p><strong>Maintenance Therapy</strong></p><p><strong>Methotrexate:</strong> 20 mg/m2 PO weekly.</p><p><strong>6 Mercaptopurine:</strong> 75 mg/m2 PO daily</p><p>Continue for a total of 30 months of complete response.</p><p><strong>CNS Prophylaxis</strong><br><strong>Cranial irradiation:</strong> 1,800 Gy in 10 fractions over 12-14 days<br><strong>Methotrexate:</strong> 12 mg IT weekly for 6 weeks<br>Begin within 1 week of complete response.<br>In patients with documented CNS involvement at the time of diagnosis, intrathecal<br>chemotherapy should begin during induction chemotherapy.<br><strong>Methotrexate: </strong>12 mg IT weekly for 10 doses<br><strong>Cranial irradiation:</strong> 2,800 cGy</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (39, 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various', 'Gemcitabine + Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Low', '<b>Gemcitabine + Cisplatin</b>', '$Thongprasert S, et al. Ann Oncol 2005;16:279-281$', null, '<b>Gemcitabine</b> 1,250 mg/m<sup>2</sup> IV on Day 1, 8<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV on Day 1<br> Repeat cycle every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1043, null, 'Venetoclax + Rituximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Venetoclax:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If mild to moderate impairment (Child-Pugh classes A and B): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child-Pugh class C): Reduce dose by 50%.</li><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Venetoclax: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If<strong>&nbsp;</strong>CrCl ≥15 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If<strong>&nbsp;</strong>CrCl &lt;15 mL/min: Dosage adjustment not provided by the manufacturer.</li><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>Venetoclax:</strong> 20 mg/day PO on days 1-7<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 50 mg/day PO on days 8-14<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 100 mg/day PO on days 15-21<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 200 mg/day PO on days 22-28 and then 400 mg/day PO daily<br><strong>Venetoclax:</strong> 400 mg PO daily<br><strong>Rituximab:</strong> 375 mg/m2 IV on day 1 of cycle 1, then 500 mg/m IV on day 1 of cycles 2-6<br>Repeat cycle every 28 days for up to 6 cycles. After cycle 6, venetoclax is continued at 400 mg PO daily for up to 2 years.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (14, 'Cisplatin : CDDP<br>Fluorouracil : 5-FU', '5-Fluorouracil + Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High', '<b>5-Fluorouracil + Cisplatin</b>', '$Kies MS, et al. Cancer 1987;60:2156-2160.$', null, '<b>5-Fluorouracil</b>1,000 mg/m<sup>2</sup>/day IV continuous infusion on days 1-5<br><b>Cisplatin</b>100 mg/m<sup>2</sup> IV on day 1<br>Repeat cycle on weeks 1, 5, 8, and 11.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (15, 'Cisplatin : CDDP', 'Irinotecan + Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Irinotecan:<br>No dose reduction </li></ul>', 'High', '<b>Irinotecan + Cisplatin</b>', '$Ilson DH, et al. J Clin Oncol 1999;17:3270-3275.$', null, '<b>Irinotecan</b>65 mg/m<sup>2</sup> IV weekly for 4 weeks<br><b>Cisplatin</b>30 mg/m<sup>2</sup> IV weekly for 4 weeks<br>Repeat cycle every 6 weeks.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (16, 'Cisplatin : CDDP<br>Paclitaxel : Taxol', 'Paclitaxel + Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 