insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (17, 'Capecitabine : Xeloda<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'Capecitabine + Oxaliplatin', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Oxaliplatin:<br>Unknown</li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Oxaliplatin:<br>No dose reduction necessary in mild to moderate impairment <br> Reduce the dose to 65 mg/m<sup>2</sup> if there is severe renal impairment</li></ul>', 'Moderate', '<b>Capecitabine + Oxaliplatin</b>', '$Van Meerten E, et al. Br J Cancer 2007;96:1348-1352.$', null, '<b>Capecitabine</b>1,000 mg/m<sup>2</sup> PO bid on days 1-14<br><b>Oxaliplatin</b>130 mg/m<sup>2</sup> IV on day 1<br>Repeat cycle every 21 days.', null);
