insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (26, 'Etoposide : VP-16<br>Fluorouracil : 5-FU', 'ELF', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Low', '<b>ELF</b>', 'Vanhoefer U, Rougier P, Wilke H, et al. Final results of a randomized phase III trial of sequential high-dose methotrexate, fluorouracil, and doxorubicin versus etoposide, leucovorin, and fluorouracil versus infusional flurorouracil and cisplatin in advanced gastric cancer: a trial of the European Organization for Research and Treatment of Cancer Gasttrointestinal Tract Cancer Cooperative Group. $J Clin Oncol. 2000;18(41):2648-2657$', null, '<b>Leucovorin calcium</b> 300 mg/m<sup>2</sup>/d IV over 10 minutes, <b>FOLLOWED BY</b><br> <b>Etoposide</b> 120 mg/m<sup>2</sup>/d IV over 50 minutes,<br> <b>THEN</b><br> <b>Fluorouracil</b> 500 mg/m<sup>2</sup>/d IV  bolus<br> Give all agents on days 1-3; repeat every 22 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (27, 'Fluorouracil : 5-FU', '5-FU/LV and Radiation', '<b>Hepatic Impairment</b><ul><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Low', '<b>5-FU/LV and Radiation</b>', 'Macdonald JS, Smalley SR, Benedetti J, et al. Chemoradiotherapy afer surgery compared with surgery alone for adenocarcinoma of the stomach or gastroesophageal junction. $N Engl J Med. 2001;345(10):725-730.$', null, '<b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days,<br> <b>FOLLOWED BY</b><br> <b>RT</b> 4,500 cGy (180cGy/d), 5 days a week for 5 weeks<br> <b>Fluorouracil</b> 400 mg/m<sup>2</sup>/d IV, on the first 4 and last 3 days of RT<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d, on the first 4 and last 3 days of RT,<br> <b>FOLLOWED BY</b><br> <b>Fluorouracil</b> 425 mg/m<sup>2</sup>/d for 5 days<br> <b>Leucovorin calcium</b> 20 mg/m<sup>2</sup>/d for 5 days<br>repeat 28 days X 2 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (28, 'Capecitabine : Xeloda<br>Cisplatin : CDDP<br>Fluorouracil : 5-FU<br>Trastuzumab : Herceptin', 'Trastuzumab + Cisplatin + Capecitabine or Fluorouracil', '<b>Hepatic Impairment</b><ul><li>Capecitabine:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Capecitabine:<br>If CrCl 30-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 30 mL/min, do not use </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'High day 1, low for the remaining treatment days', '<b>Trastuzumab + Cisplatin + Capecitabine or Fluorouracil</b>', 'Bang YJ, Van Cutsem E, Feyereislava A, et al. Trastuzumab in combination with chemotherapy versus chemotherapy alone for treatment of HER2-positive advanced gastric or gastro-oesophageal junction cancer (ToGA): a phase 3 open-label, randomised controlled trial. $Lancet. 2010;376(99742):687-697.$', null, '<b>Trastuzumab</b> 8 mg/kg IV, day 1 of the first cycle <b>THEN</b>  6 mg/kg IV, day 1<br><br> <b>Cisplatin</b> 80 mg/m<sup>2</sup> IV, day 1. <b>Capecitabine</b> 1,000 mg/m<sup>2</sup> PO bid, days 1-14 <br> Repeat  every 21 days<br> <b>OR</b><br> <b>Fluorouracil</b> 800 mg/m<sup>2</sup>/d CIVI for 5 days of each 3-week cycle<br> Repeat for 6 cycles', null);
