insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (98, 'Cisplatin : CDDP', 'Cisplatin + CPT-11', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Irinotecan:<br>If bilirubin 1-2 mg/dL, may need to reduce the dose<br>                               If bilurubin > 2 mg/dL, may not use  </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Irinotecan:<br>No dose reduction </li></ul>', 'High', '<b>Cisplatin + CPT-11</b>', 'Noda K, Nishiwaki Y, Kawahara M, et al.                     Irinotecan plus cisplatin compared with etoposide plus cisplatin for extensive small-cell lung cancer. $N Engl J Med. 2002;346(2):85-91.$', null, '<b>Cisplatin</b> 60mg/m<sup>2</sup>, day 1<br> <b>Irinotecan</b> 60mg/m<sup>2</sup>/d, days 1, 8, 15<br> Repeat every 28days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (190, 'Flutamide : various', 'FL', '<b>Hepatic Impairment</b><ul><li>Leuprolide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul></ul>', 'N/A', '<b>FL</b>', 'Sarosdy MF, Schellhammer PF, Johnson R, Carroll K, Kolvenbag GJCM. Does prolonged combined androgen blockade have survival benefits over short-term combined androgen blockade therapy? $Urology. 2000;55(3):391-396.$', null, '<b>Flutamide</b> 250 mg PO tid <b>WITH</b><br> <b>Leuprolide acetate depot</b> 7.5 mg IM<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (99, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'CAV', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vincristine:<br>No dose reduction </li></ul>', 'High', '<b>CAV</b>', '$Roth  BJ, et al. J Clin Oncol 1992;10:282-291.$', null, '<b>Cyclophosphamide</b>  1,000 mg/m<sup>2</sup>IV on day 1.  <br>                                              <b>Doxorubicin</b> 40 mg/m<sup>2</sup>IV on day 1.<br>                                                                <b>Vincristine</b> 1 mg/m<sup>2</sup>IV on day 1. (maximum, 2 mg) <br>Repeat cycle every 21 days.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (357, 'Zelboraf', 'Vemurafinib', '', 'Minimal', '<b>Vemurafinib</b>', 'Sosman JA, Kim KB, Schuchter L, Survival in BRAF V600-Mutant advanced Melanoma treated with Vemurafenib. $N Engl J Med. 2012 Feb 23; 366(8): 707–714 .$', null, '<b>Vemurafenib</b> 960 mg po BID', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (100, 'Carboplatin : Paraplatin<br>Paclitaxel : Taxol', 'Carboplatin + Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Carboplatin + Paclitaxel</b>', '$Neubauer M, et al. J Clin Oncol2004;22:1872-1877$', null, '<b>Carboplatin</b>AUC of 2, IV on days1,8, and 15.<br><b>Paclitaxel</b> 80 mg/m<sup>2</sup> IV on days 1,8, and 15. Repeat cycle every 28 days.', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (101, 'Carboplatin : Paraplatin<br>Etoposide : VP-16', 'EC', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Moderate', '<b>EC</b>', 'Viren M, Liippo K, Ojala a, et al. Carboplatin and etoposide in extensive small-cell lung cancer. $Acta Oncal. 1994;33(8):921-924.$', null, '<b>Etoposide</b> 100mg/m<sup>2</sup>/d IV, days 1-3<br><b>Carboplatin</b> 450mg/m<sup>2</sup> IV, day 1<br> Repeat every 28 days for 6 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (102, 'Cisplatin : CDDP<br>Etoposide : VP-16', 'EP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>EP</b>', 'Ihde DC, Mulshine JL, Kramer BS, et al.  Prospective randomized comparision of high-dose and standard-dose etoposide and cisplatin chemotherapy in patients with extensive-stage small-cell lung cancer. $J Clin Oncol.1994; 12(10):2022-2034.$', null, '<b>Etoposide</b> 80mg/m<sup>2</sup>/d IV, days 1-3<br> <b>Cisplatin</b> 80mg/m<sup>2</sup> IV, day 1 (Standard dose)<br> Repeat every 21 days <b>OR</b><br> <b>Etoposide</b> 80mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 27mg/m<sup>2</sup> IV, days 1-5(high dose)<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (103, 'Etoposide : VP-16', 'Etoposide', '<b>Hepatic Impairment</b><ul><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'Low', '<b>Etoposide</b>', 'Johnson DH. Recent developments in chemotherapy treatment of small-cell lung cancer. $Semin Oncol. 1993;20(4):315-325.$', null, '<b>Etoposide</b> 160 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 28 days<br> <b>OR</b><br> <b>Etoposide</b> 50 mg PO bid for 14 days<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (104, 'Paclitaxel : Taxol', 'Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Low', '<b>Paclitaxel</b>', 'Ettinger DS, Finkelstein DM, Sarma RP, Johnson DH. Phase II study of paclitaxel in patients with extensive-disease small-cell lung cancer: an Eastern Cooperative Oncology Group study. $J Clin Oncol. 1995;13(6):1430-1435.$<br> Kirschling RJ, Grill JP, Marks RS, et al. Paclitaxel and G-CSF in previously untreated patients with extensive-stage small-cell lung cancer: a phase II study of the North Central Cancer Treatment Group. $Am J Clin Oncol. 1999;22(5):517-522$<br>', null, '<b>Paclitaxel</b> 250 mg/m<sup>2</sup> IV over 24 hours<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (105, 'Topotecan : Hycamtin', 'Topotecan IV', '<b>Hepatic Impairment</b><ul><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Low', '<b>Topotecan IV</b>', 'Ardizzoni A, Hansen H, Dombernowsky P, et al. Topotecan, a new active drug in the second-line treatment of small-cell lung cancer: a phase II study in patients with refractory and sensitive disease. $J Clin Oncol.1997;15(5):2090-2096.$<br> von Pawel J, Schiller JH, Shepherd FA, et al. Topotecan versus cyclophosphamide, doxorubicin, and vincristine for the treatment of recurrent small-cell lung cancer. $J Clin Oncol. 1999;17(2):658-667.$', null, '<b>Topotecan</b> 1.5 mg/m<sup>2</sup>/d IV over 30 minutes, days 1-5<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (106, 'Topotecan : Hycamtin', 'Topotecan PO', '<b>Hepatic Impairment</b><ul><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'Low', '<b>Topotecan PO</b>', 'Eckardt JR, von Pawel J, Pujol JL, et al. Phase III study of oral compared with intravenous topotecan as second-line therapy in small-cell lung cancer. $J Clin Oncol. 2007;25(15):2086-2092.$ $Erratum in: J Clin Oncol. 2007;25(22):3387.$', null, '<b>Topotecan</b> 2.3 mg/m<sup>2</sup>/d PO, days 1-5<br> Repeat every 21 days', null);
