insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1044, null, 'ldelalisib + Rituximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>ldelalisib:</strong><br>If SGOT or SGPT and bilirubin&nbsp;&lt; 1.5 times ULN: 100% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If SGOT or SGPT &gt;2.5 times ULN or bilirubin &gt;1.5 times ULN. Monitor closely for toxicity.</li><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>ldelalisib:</strong><br>If CrCl ≥15 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt; 15 mL/min: Dose modification not provided by the manufacturer</li><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>ldelalisib:</strong> 150 mg PO bid</p><p><strong>Rituximab: </strong>375 mg/m2 IV on day 1, then 500 mg/m2 IV every 2 weeks for 4 doses and then every 4 weeks for 3 doses for a total of 8 doses.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (892, null, 'Paclitaxel + Estramustine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li><strong>Estramustine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Paclitaxel</strong>:<br>No dose reduction</li><li><strong>Estramustine:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Paclitaxel:</strong> 120 mg/m2 IV continuous infusion on days 1-4<br><strong>Estramustine</strong>: 600 mg/m2 PO daily, starting 24 hours before paclitaxel<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (893, null, 'Mitoxantrone + Prednisone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Mitoxantrone:</strong><br>If bilirubin &gt; 3 mg/dL, reduce dose by 25%</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Mitoxantrone:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Mitoxantrone: </strong>12 mg/m2 IV on day 1<br><strong>Prednisone:</strong> 5 mg PO bid daily</p><p>Repeat cycle every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (722, '', 'Gemcitabine + Nab-Paclitaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Nab-Paclitaxel:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin 1.2 to ≤1.8 mg/dL:100% dosage<br>If bilirubin &gt;1.8 to ≤3.6 mg/dL: Reduce dose by 75%<br>If bilirubin &gt; 3.6 mg/dL, do not use</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Nab-Paclitaxel:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥30 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: There are no dosage adjustments provided because of insufficient data.</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Gemcitabine:&nbsp;</strong>1,000 mg/m<sup>2</sup>&nbsp;IV on days 1, 8, and 15</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (112, 'Carboplatin : Paraplatin<br>Paclitaxel : Taxol', 'Carboplatin + Paclitaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin:</strong><br>Dosing based on AUC</li><li><strong>Paclitaxel:</strong><br>No dose reduction</li></ul>', 'Moderate', '<b>Carboplatin + Paclitaxel</b>', 'Lemma GL, et al.$ J Clin Oncol 2011;29:2060-2065.$', null, '<p><strong>Carboplatin:</strong> AUC of 6, IV on day 1&nbsp;<br><strong>Paclitaxel: </strong>225 mg/m2 IV on day 1<br>Repeat cycle every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (111, 'Gemcitabine : Gemzar, various<br>Topotecan : Hycamtin', 'Gemcitabine + Topotecan', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Topotecan:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Topotecan:</strong><br>If CrCl &gt; 40 mL/min, no dose reduction&nbsp;<br>If CrCl 20-39 mL/min, reduce to 0.75 mg/m2</li></ul>', 'Moderate', '<b>Gemcitabine + Topotecan</b>', 'William WN, et al.$Am J Clin Oncol 2009;32:15-19.$', null, '<p><strong>Gemcitabine: </strong>1000 mg/m2 IV on days 1,8, and 15.<br><strong>Topotecan: </strong>0.75-1.5 mg/m2 IV on days 1,8, and 15.<br>Repeat cycle every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (110, 'Cisplatin : CDDP<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'VIP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Ifosfamide:</strong><br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Ifosfamide</strong>: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥10 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose by 25%</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul>', 'High', '<b>VIP</b>', 'Loehrer PJ, et al.$Cancer 2001;91:2010-2015.$', null, '<p><strong>Etoposide(VP-I6):</strong> 75 mg/m2 IV on days 1-4<br><strong>Ifosfamide:</strong> 1200 mg/m2 IV on days 1-4<br><strong>Cisplatin:</strong> 20 mg/m2 IV on days 1-4<br><strong>Mesna:</strong> 240 mg/m2 IV before first ifosfamide dose, then 4 and 8 hours later on days 1-4.<br>Repeat cycle every 21 days for a total of 4 cycles. G-CSF support at 5 micrograms/kg/day on days 5-15.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (113, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various', 'AC', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li></ul>', 'High', '<b>AC</b>', 'Fisher B,Brown AM,Dimitor NV,et al.Two months of doxorubicin-cyclophosphamide with and without interval reinduction therapy compared with 6 months of cyclophosphamide,methotrexade,and fluorouracil in positive-node breast cancer patients with tamoxifen-nonresponsive tumors:results from the National Surgical Adjuvant Breast and Bowel Project B-15. $J Clin Oncol. 1990;8(9):1483-1496$', null, '<b>Doxorubicin</b> 60mg/m<sup>2</sup> IV,day 1<br> <b>Cyclophosphamide</b> 600mg/m<sup>2</sup>  IV,day 1<br> Repeat every 21 days for 4 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (689, '', 'Irinotecan', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Irinotecan: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin &gt;1.2 to ≤2 mg/dL: &nbsp;Reduce initial dose by one dose level &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;2 mg/dL: Do not use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Irinotecan:&nbsp;</strong><br>No dose reduction.</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>Irinotecan:&nbsp;</strong>250 mg/m2&nbsp;IV on day 1</p><p>Repeat every 3 weeks. The dose of irinotecan can be increased to 350 mg/m2 based on toxicity.</p>', 92);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1045, null, 'Obinutuzumab + Chlorambucil', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Obinutuzumab:</strong><br>Dosage adjustment not provided by the manufacturer.</li><li><strong>Chlorambucil:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Obinutuzumab:</strong><br>No dose reduction</li><li><strong>Chlorambucil:</strong><br>If CrCl &gt;50 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 10 to 50 mL/min: Reduce dose by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;10 mL/min: Reduce dose by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Obinutuzumab:</strong> 100 mg IV on day 1, cycle 1; 900 mg IV on day 2, cycle 1; 1,000 mg IV on days 8 and 15 of cycle 1; 1,000 mg IV on day 1 of cycles 2-6<br><strong>Chlorambucil:</strong> 0.5 mg/kg PO on days 1 and 15<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (123, 'Cyclophosphamide : Cytoxan<br>Epirubicin : Ellence, various<br>Fluorouracil : 5-FU', 'FEC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Fluorouracil:</strong><br>If bilirubin &lt; 5 mg/dL, no dose reduction&nbsp;<br>If bilirubin &gt; 5 mg/dL, do not use&nbsp;<br>No dose reduction for infusional administration</li><li><strong>Epirubicin: &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin 1.2 to 3 mg/dL: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;3 to 5 mg/dL: Reduce dose by 75% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;5 mg/dL: Do not use it.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Fluorouracil:</strong><br>No dose reduction</li><li><strong>Epirubicin:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If creatinine &gt;5 mg/dL: Reduce dose by 50%.</li></ul>', 'High', '<b>FEC</b>', 'Levine MN, Bramwell VH, Pritchard KI, et al. Randomized tiral of intensive cyclophosphamide, epirubicin, and fluorouracil chemotherapy compared with cyclophosphamide, methotrexate, and fluorouracil in premenopausal women with node-positive breast cancer. $J Clin Oncol. 1998;16(8):2651:2658.$<br> French Epirubicin Study Group. Epirubicin-based chemotherapy in metastatic breast cancer patients: role of dose intensity and duration of treatment. $J Clin Oncol. 2000;18(7):3115-3124.$', null, '<p><strong>Cyclophosphamide:</strong> 75 mg/m2/d PO, days 1-14<br><strong>Epirubicin:</strong> 60 mg/m2/d IV, days 1,8<br><strong>Fluorouracil:</strong> 500 mg/m2 IV, days 1,8<br>Repeat every 28 days for 6 cycles<br><strong>OR</strong><br><strong>Cyclophosphamide:</strong> 500 mg/m2 IV, day 1<br><strong>Epirubicin:</strong> 100 mg/m2 IV, day 1<br><strong>Fluorouracil:</strong> 500 mg/m2 IV, day 1<br>Repeat every 21 days for 6 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (115, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Paclitaxel : Taxol<br>Trastuzumab : Herceptin', 'AC  Paclitaxel/Trastuzumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li><strong>Trastuzumab: &nbsp; &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dosage modification.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Paclitaxel:</strong><br>No dose reduction</li><li><strong>Trastuzumab: &nbsp; &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dosage modification. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Cardiac Impairment</strong></li><li><strong>Trastuzumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> If<strong> </strong>EF decreases ≥16% &nbsp;from baseline &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong> &nbsp; OR &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;EF below normal limits and ≥10% decrease from baseline &nbsp; &nbsp; &nbsp;Hold treatment for 4 weeks and repeat LVEF every 4 weeks. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Resume treatment if EF returns to normal limits within 4 to 8 weeks and remains at ≤15% decrease from the baseline value. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Stop treatment permanently if EF declines persistently for &gt; 8 weeks or for &gt;3 incidents of interruptions in treatment for cardiomyopathy.</li></ul>', 'High ', '<b>AC  Paclitaxel/Trastuzumab</b>', 'Romond EH,perez EA,Bryant J.et al. Trastuzumab plus adjuvant chemotherapy for operable HER2-positive breast cancer. $N Engl J Med. 2005;353(16):1673-1684.$ ', null, '<p><strong>Doxorubicin:</strong> 60 mg/m2 IV, day 1<br><strong>Cyclophosphamide: </strong>600 mg/m2 IV, day 1<br>Repeat every 21 days for 4 cycles, followed by<br><strong>Paclitaxel:</strong> 175 mg/m2 IV over 3 hours, day 1<br>Repeat every 21 days for 4 cycles.<br><strong>OR</strong><br><strong>Paclitaxel:</strong> 80 mg/m2 IV over 1 hour, weekly X 12&nbsp;<br><strong>Trastuzumab:</strong> (concurrently with paclitaxel) 4 mg/kg IV over 90 minutes, day 1<br><strong>FOLLOWED BY</strong><br>2 mg/kg IV weekly for 51 additional weeks</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (819, null, 'Interferon + Cytarabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Interferon alfa</strong>:<br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Interferon alfa</strong>:<br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li></ul>', 'Low', null, null, null, '<p><strong>Interferon-alpha-2b:</strong> 5 million U/m2 SC daily</p><p><strong>Cytarabine: </strong>20 mg/m2 SC daily for 10 days.</p><p>Repeat cytarabine on a monthly basis. The dose of interferon should be<br>decreased by 50% when the neutrophil count drops below 1500/mm3, platelet<br>count drops below 100,000/m3, or both. When the neutrophil count drops below 1,000/mm, platelet count drops below 50,000/mm, or both then interferon and cytarabine should be discontinued.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (691, '', 'Pembrolizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pembrolizumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Pembrolizumab:&nbsp;</strong>200 mg IV on day 1</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1046, null, 'Venetoclax + Obinituzumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Obinutuzumab:</strong><br>Dosage adjustment not provided by the manufacturer.</li><li><strong>Venetoclax:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If mild to moderate impairment (Child-Pugh classes A and B): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child-Pugh class C): Reduce dose by 50%.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Obinutuzumab:</strong><br>No dose reduction.</li><li><strong>Venetoclax: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If<strong>&nbsp;</strong>CrCl ≥15 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If<strong>&nbsp;</strong>CrCl &lt;15 mL/min: Dosage adjustment not provided by the manufacturer.</li></ul>', 'Low', null, null, null, '<p><strong>Obinituzumab:</strong> 100 mg IV on day 1 and 900 mg lV on day 2 (or 1,000 mg on day 1), 1,000 mg on day 8 and 1,000 mg on day 15 of cycle 1, and subsequently 1,000 mg on day 1 of cycles 2 through 6.<br><strong>Venetoclax:</strong> Start on day 22 of cycle 1 with a 5-week dose ramp-up (1 week each of 20, 50, 100, and 200 mg P0, then 400 mg P0 daily for 1 week), thereafter at 400 mg PO daily until completion of cycle 12.<br>Repeat cycle every 28 days for up to 6 cycles with obinituzumab and 12 cycles with<br>venetoclax.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (690, '', 'Ramucirumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ramucirumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ramucirumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Ramucirumab:&nbsp;</strong>8 mg/kg IV on day 1</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1047, null, 'Cladribine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cladribine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dosage adjustment not provided because of insufficient data. &nbsp;&nbsp;<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong></li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cladribine:</strong><br>If CrCl 10-50 mL/min: Dose reduction by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;10 mL/min: Dose reduction by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Cladribine:</strong> 0.09 mg/kg/day IV continuous infusion on days 1-7<br>Repeat cycle every 28-35 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1048, null, 'Prednisone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Prednisone: </strong>20-30 mg/m2/day PO for 1-3 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (121, 'Bevacizumab : Avastin<br>Paclitaxel : Taxol', 'Bevacizumab/Paclitaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Paclitaxel:</strong><br>For 3-hour infusion:&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use&nbsp;<br>For 24-hour infusion:&nbsp;<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)&nbsp;<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2&nbsp;<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2&nbsp;<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li><strong>Bevacizumab: &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dosage adjustment provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Paclitaxel:</strong><br>No dose reduction</li><li><strong>Bevacizumab: &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dosage adjustment provided by the manufacturer</li></ul>', 'Low', '<b>Bevacizumab/Paclitaxel</b>', 'Miller KD. E2100: a phase III trial of paclitaxel versus paclitaxel/bevacizumab for metastatic breast cancer. $J Clin Oncol. 2003;3(6):421-422$', null, '<p><strong>Bevacizumab:</strong> 10 mg/kg IV on days 1, and 15<br><strong>Paclitaxel:</strong> 90 mg/m2 IV over 1 hour on days 1, 8, and 15<br>Repeat cycle every 28 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1049, null, 'Rituximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>Rituximab:</strong> 375 mg/m2 IV weekly for 4 weeks<br>Repeat cycle every 6 months for a total of 4 courses.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (124, 'Cyclophosphamide : Cytoxan<br>Fluorouracil : 5-FU', 'CMF', '<b>Hepatic Impairment</b><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT > 180 mg/dL, reduce dose by 25%  <br>    If bilirubin >5 mg/dL, do not use </li><li>Fluorouracil:<br>If bilirubin < 5 mg/dL, no dose reduction  <br>                                                   If bilirubin > 5 mg/dL, do not use  <br>                       No dose reduction for infusional administration </li></ul><b>Renal Impairment</b><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>                                                                      If CrCl < 10 mL/min, reduce dose by 50% </li><li>Fluorouracil:<br>No dose reduction </li></ul>', 'Moderate for IV, Moderate-High for PO', '<b>CMF</b>', 'Amadori D, Nanni O, Marangolo M, et al. Disease-free survival advantage of adjuvant cyclophosphamide, methotrexate, and fluorouracil in patients with node-negative, rapdly proliferating breast cancer: a randomized multicenter study. $J Clin Oncol. 2000:18(17):3125-3134.$<br> Bonadonna G, Zambetti M, Valagussa P. Sequential or alternating doxorubicin and CMF regimens in breast cancer with more than three positive nodes: ten-year results.$ JAMA. 1995:273(7):542-547.$', null, '<b>Cyclophosphamide </b>100 mg/m<sup>2</sup>/d PO, days 1-14<br> <b>Methotrexate</b> 40 mg/m<sup>2</sup>/d IV, days 1,8<br> <b>Fluorouracil</b> 600 mg/m<sup>2</sup>/d IV, days 1, 8<br> Repeat every 28 days for up to 6 cycles<br> <b>OR</b><br> <b>Cyclophosphamide</b> 600 mg/m<sup>2</sup> IV, day 1<br> <b>Methotrexate</b> 40 mg/m<sup>2</sup> IV, day 1<br> <b>Fluorouracil</b> 600 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for up to 12 courses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (692, '', 'TAS-102', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>TAS-102:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &lt;1.2 to 1.8 mg/dL: 100 % dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;1.8 to 3.6 mg/dL: Do not initiate therapy.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>TAS-102:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥30 mL/min: 100% dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 15 to 29 mL/min: Decrease dosage from 35 mg/m2 to 20 mg/m2</li></ul><p>If unable to tolerate the 20 mg/m2 dose: Decrease the dose to 15 mg/m2. If unable to tolerate the 15 mg/m2 dose: Permanently stop the treatment with trifluridine/tipiracil.</p>', 'Moderate', '', '', 'METASTATIC', '<p><strong>TAS-102:&nbsp;</strong>35 mg/m<sup>2</sup>&nbsp;PO twice daily on days 1-5 and 8-12</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1050, null, 'Pentostatin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Pentostatin:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Pentostatin:</strong><br>If CrCl 46-60 mL/min: Reduce dose by 30%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31-45 mL/min: Reduce dose by 40%&nbsp;<br>If CrCl &lt;30 mL/min: Use alternative drug.</li></ul>', 'Low', null, null, null, '<p><strong>Pentostatin:</strong> 4 mg/m2 IV on day 1<br>Repeat cycle every 14 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (723, '', 'Capecitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Capecitabine:</strong>&nbsp;1,000 mg/m<sup>2</sup>&nbsp;PO twice daily on days 1-14</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (128, 'Capecitabine : Xeloda<br>Lapatinib : Tykerb', 'Lapatinib/Capecitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>No dose reduction</li><li><strong>Lapatinib:</strong><br>If Child-Pugh C, reduce dose to 750 mg/d</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>If CrCl 30-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Lapatinib:</strong><br>Not studied. Only 2% eliminated by kidneys</li></ul>', 'Low', '<b>Lapatinib/Capecitabine</b>', 'Geyer CE, Forster J, Lindquist D, et al. Lapatinib plus capecitabine for HER2-positive advanced breast cancer. $N Engl J Med. 2006;355(26):2733-2743.$', null, '<p><strong>Lapatinib:</strong> 1,250 mg PO 1 hour before breakfast, daily<br><strong>Capecitabine:</strong> 1,000 mg/m2 PO bid, days 1-14<br>Repeat every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (129, 'Lapatinib : Tykerb<br>Letrozole : Femara', 'Lapatinib/Letrozole', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lapatinib:</strong><br>If Child-Pugh C, reduce dose to 750 mg/day</li><li><strong>Letrozole:</strong><br>If Child-Pugh A or B, no dose reduction&nbsp;<br>If Child-Pugh C, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lapatinib:</strong><br>Not studied. Only 2% eliminated by kidneys</li><li><strong>Letrozole:</strong><br>If CrCl &gt; 10 mL/min, no dose reduction</li></ul>', 'Low', '<b>Lapatinib/Letrozole</b>', 'Johnston S, Pippen J Jr, Pivot X, et al. Lapatinib combined with letrozole versus letrozole and placebo as first-line therapy for postmenopausal hormone receptor-positive metastatic breast cancer. $J Clin Oncol. 200927(33):5538-5546.$', null, '<p><strong>Lapatinib</strong> 1,500 mg PO, daily (days 1 - 28)<br><strong>Letrozole</strong> 2.5 mg PO, daily (days 1 - 28)</p><p>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (693, '', 'Imatinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Imatinib</strong>:<br>If bilirubin &lt; 3 upper normal limit, no dose reduction<br>If bilirubin &gt; 3 upper normal limit, use with caution</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Imatinib</strong>:<br>If CrCl 40 to 59 mL/min: Maximum dose recommended-600 mg. &nbsp; If CrCl 20 to 39 mL/min: Reduce dose by 50%; dose may be increased as tolerated; maximum recommended dose: 400 mg. &nbsp; If CrCl &lt;20 mL/minute: Use caution; 100 mg daily dose has been tolerated in a limited number of patients with severe impairment.</li></ul>', 'Moderate', '', '', 'ADJUVANT', '<p><strong>Imatinib:&nbsp;</strong>400 mg/day&nbsp;PO</p><p>Continue treatment for a total of 3 years.</p>', 8);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (694, '', 'Imatinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Imatinib</strong>:<br>If bilirubin &lt; 3 upper normal limit, no dose reduction<br>If bilirubin &gt; 3 upper normal limit, use with caution</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Imatinib</strong>:<br>If CrCl 40 to 59 mL/min: Maximum dose recommended-600 mg. &nbsp; If CrCl 20 to 39 mL/min: Reduce dose by 50%; dose may be increased as tolerated; maximum recommended dose: 400 mg. &nbsp; If CrCl &lt;20 mL/minute: Use caution; 100 mg daily dose has been tolerated in a limited number of patients with severe impairment.</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>Imatinib:&nbsp;</strong>400 mg/day PO</p><p>Treatment to be continued until disease progression. May increase dose to 600 mg/day if no response is seen. In patients with KIT exon 9 (or exon 11) mutation, use a dose of 800 mg/day (400 mg twice daily).</p>', 8);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1051, null, 'Bendamustine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bendamustine:</strong><br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin&nbsp;1.5-3 upper normal limit, or bilirubin &gt; 3 upper normal limit: Do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bendamustine:</strong><br>If CrCl &lt; 30 mL/min: Do not use</li></ul>', 'Moderate', null, null, null, '<p><strong>Bendamustine:</strong> 100 mg/m2 IV on days 1 and 2<br>Repeat cycle every 28 days for up to 6 cycles.</p><p><strong>OR</strong></p><p><br><strong>Bendamustine:</strong> 60 mg/m2 IV on days 1-5<br>Repeat cycle every 28 days. In patients &gt;70 years, use dose of 50 mg/m2.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (720, '', 'Capecitabine + Cisplatin', '<p><strong>Hepatic Impairment:</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Renal Impairment:&nbsp;</strong></li><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li></ul>', 'High', '', '', 'METASTATIC', '<p><strong>Capecitabine:&nbsp;</strong>1,250 mg/m<sup>2</sup>&nbsp;PO twice daily on days 1-14</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (765, null, 'Cladribine/Rituximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cladribine:</strong><br>No dose reduction</li><li><strong>Rituximab:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cladribine:</strong><br>If CrCl 10-50 mL/min: Dose reduction by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;10 mL/min: Dose reduction by 50%</li><li><strong>Rituximab:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Cladribine:&nbsp;</strong>0.15 mg/kg/day Day1-5</p><p><strong>Rituximab: &nbsp;</strong>375 mg/m2 weekly x 8</p><p>&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1052, null, 'Lenalidomide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>If CrCl &gt;60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 30 to 60 mL/min: 5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min not requiring dialysis: 2.5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min requiring dialysis: 2.5 mg once daily (administer after dialysis on dialysis days).</li></ul>', 'Low', null, null, null, '<p><strong>Lenalidomide:</strong> 10 mg PO daily on days 1-28<br>Increase dose by 5 mg every 28 days up to a maximum of 25 mg daily until disease<br>progression or toxicity.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (358, 'Yervoy', 'Ipilumimab', '', 'Minimal', '<b>Ipilumimab</b>', 'Hodi FS, O''Day SJ, McDermott DF, et al. Improved survival with Ipilumimab in patients with Metastatic Melanoma. $N Engl J Med 2010; 363:711-723. $', null, '<b>Ipilumimab</b>  IV 3 mg/kg  every 3 weks upto 4 doses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (142, 'Exemestane : Aromasin, various', 'Exemestane', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Exemestane:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Exemestane:</strong><br>No dose reduction</li></ul>', 'NA', '<b>Exemestane</b>', 'Lonning PE, Bajetta E, Murray R, et al. Activity of exemestane in metastatic breast cancer after failure of nonsteroidal aromatase in hibitors: a phase II trial. $J Clin Oncol. 2000;18(11):2234-2244.$<br> Kaufmann M, Bajetta E, Dirix LY, et al. Exemestane is superior to megestrol acetate after tamoxifen failure in postmenopausal women with advanced breast cancer: results of a phase III randomized double-blind trial. The Exemestane Study Group. $J Clin Oncol. 2000;18(7):1399-1411.$<br> Coombes RC, Hall E, Gibson LJ, et al. A randomized trial of exemestane after two to three years of tamoxifen therapy in postmenopausal women with primary breast cancer. $N Engl J Med. 2004;350(11):1081-1092.$', null, '<p><strong>Exemestane:</strong> 25 mg PO once daily</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (134, 'Carboplatin : Paraplatin<br>Docetaxel : Taxotere<br>Trastuzumab : Herceptin', 'TCH', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Docetaxel:<br>No dose reduction </li></ul>', 'Moderate for combination therapy, low for trastuzumab monotherapy', '<b>TCH</b>', 'Pegram M, Forbes J, Pienkowski T, et al. BCIRG 007: first overall survival analysis of randomized phase III trial of trastuzumab plus docetaxel with or without carboplatin as first line therapy in HER2 amplified metastatic breast cancer (MBC). $J Clin Oncol. 2007;25:LBA 1008 (abstract).$', null, '<p><strong>Docetaxel:</strong> 75 mg/m2 IV, day 1<br><strong>Carboplatin:</strong> AUC of 6 day 1&nbsp;<br>Repeat every 21 days for 8 cycles<br><strong>Trastuzumab:</strong> 4 mg/kg IV,<br><strong>FOLLOWED BY</strong><br><strong>Trastuzumab:</strong> 2 mg/kg IV weekly during chemotherapy, <strong>THEN</strong> 6 mg/kg IV<br>Repeat every 21 days for 1 year</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1134, null, 'Daratumumab + Bortezomib + Dexamethasone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Daratumumab:</strong>&nbsp;<br>There are no dosage adjustments provided in the manufacturer''s labeling</li><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin&nbsp;1-1.5 times upper normal limit: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin&nbsp;&gt;1.5 times upper normal limit: Reduce the initial dose to 0.7 mg/m2.</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Daratumumab:</strong>&nbsp;<br>There are no dosage adjustments provided in the manufacturer''s labeling</li><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Daratumumab:</strong> 16 mg/kg IV weekly on cycles 1-3<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 16 mg/kg IV on day 1 of cycles 4-8<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 16 mg/kg IV on day 1 of cycles 9+<br><strong>Bortezomib:</strong> 1.3 mg/m2 IV on days 1, 4, 8, and 11 on cycles 1-8<br><strong>Dexamethasone:</strong> 20 mg P0 on days 1, 2, 4, 5, 8, 9, 11, and 12 on cycles 1-8<br>Continue treatment until disease progression or toxicity.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (698, '', 'Regorafenib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Regorafenib:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Regorafenib:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Regorafenib:&nbsp;</strong>160 mg/day PO for 21 days</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (945, null, 'R-GemOx', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>No dose reduction.</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li><li><strong>Gemcitabine:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Rituximab:</strong><br>No dose reduction.</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: 75% dosage</li><li><strong>Gemcitabine:</strong>&nbsp;<strong>&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Moderate', null, null, null, '<p><strong>Rituximab: </strong>375 mg/m2 IV on day 1<br><strong>Gemcitabine:</strong> 1,000 mg/m2 IV on day 2<br><strong>Oxaliplatin: </strong>50 mg/m2 IV on day 2<br>Repeat cycle every 2 weeks for a total of 8 cycles.<br><strong>OR</strong><br><strong>Rituximab: </strong>375 mg/m2 IV on day 0<br><strong>Gemcitabine:</strong> 1,000 mg/m2 IV on day 1<br><strong>Oxaliplatin:</strong> 100 mg/m2 IV on day 1<br>Repeat cycle every 2 weeks for a total of 6 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (146, '', 'Megestrol', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Megestrol:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Megestrol:</strong><br>No dose reduction</li></ul>', 'N/A', '<b>Megestrol</b>', 'Buzdar A, Douma J, et al. Phase III, multicenter, double-blind, randomized study of letrozole, and aromatiase inhibitor, for advanced breast cancer versus megestrol acetate. $J Clin Oncol. 2001;19(14):3357-3366.$', null, '<p><strong>Megestrol:</strong> 40 mg PO, every 6 hours</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (153, 'Carboplatin : Paraplatin<br>Docetaxel : Taxotere', 'Carbo-Docetaxel', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Docetaxel:<br>If bilirubin > upper normal limit, do not use<br>                                              If  SGOT and/or SGPT > 1.5 upper normal limit plus alkaline                  Phosphatase >2.5 upper normal limit, do not use                                                  </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Docetaxel:<br>No dose reduction </li></ul>', 'Moderate', '<b>Carbo-Docetaxel</b>', 'Vasey PA, Jayson GC, Gordon A, et al. Phase III randomized trial of docetaxel-carboplatin versus paclitaxel-carboplatin as first-line chemotherapy for ovarian carcinoma. $J Natl Cancer Inst. 2004;96(22):1682-1691.$', null, '<b>Docetaxel</b> 75 mg/m<sup>2</sup> IV over 1 hour, day 1,<br> <b>FOLLOWED BY</b><br> <b>Carboplatin</b> dose AUC of 5 IV over 1 hour day 1<br> Repeat every 21 days for 6 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (154, 'Carboplatin : Paraplatin<br>Paclitaxel : Taxol', 'Carbo-Tax', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>Carbo-Tax</b>', 'Neijt JP, Engelholm SA, Tuxen MK, et al. Exploratory phase III study of paclitaxel and cisplatin versus paclitaxel and carboplatin in advanced ovarian cancer. $J Clin Oncol. 2000;18(17):3084-3092.$', null, '<b>Paclitaxel</b> 175 mg/m<sup>2</sup> IV over 3 hours, day 1<br> <b>Carboplatin</b> dose AUC of 5 IV  day 1<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (155, 'Carboplatin : Paraplatin', 'CD (Doxil/Carboplatin)', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li></ul>', 'Moderate', '<b>CD (Doxil/Carboplatin)</b>', 'Pujade-Lauraine E, Wagner U, Aavall-Lundqvist E, et al. Pegylated liposomal doxorubicin and carboplatin compared with paclitaxel and carboplatin for patients with platinum-sensitive ovarian cancer in late relppase. $J Clin Oncol. 2010;28:3323-3329.$', null, '<b>Liposomal doxorubicin</b> 30 mg/m<sup>2</sup> IV, day 1 <b>Carboplatin</b>AUC of 5 IV day 1<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (148, 'Tamoxifen : Nolvadex', 'Tamoxifen', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Tamoxifen:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Tamoxifen:</strong><br>No dose reduction</li></ul>', 'N/A', '<b>Tamoxifen</b>', 'Hackshaw A, Roughton M, et al. Long-term benefits of 5 years of tamoxifen: 10-year follow-up of a large randomized trial in women at least 50 years of age with early breast cancer. $J Clin Oncol. 2011;29(13):1657-1663.$', null, '<p><strong>Tamoxifen:</strong> 20 mg PO once daily</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (143, 'Fulvestrant : Faslodex', 'Fulvestrant', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Fulvestrant:</strong><br>If SGOT, SGPT, bilirubin and alkaline phosphatase &lt; 2 upper normal limit, no dose reduction<br>If SGOT, SGPT, bilirubin and alkaline phosphatase &gt; 2 upper normal limit, unknown</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Fulvestrant:</strong><br>No dose reduction</li></ul>', 'N/A', '<b>Fulvestrant</b>', 'Di Leo A, et al.Results of the CONFIRM phase III trial comparing fulvestrant 250 mg with fulvestrant 500 mg in postmenopausal women with estrogen receptor-positive advanced breast cancer.$J Clin Oncol. 2010 Oct 20;28(30):4594-600$', null, '<p><strong>Fulvestrant:</strong> 500 mg IM loading dose on day 1, then 500 mg IM on days 15 and 29.</p><p>&nbsp;Continue treatment on a monthly basis until disease progression.</p>', null);
