insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (156, 'Cisplatin : CDDP<br>Paclitaxel : Taxol', 'CT', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'High', '<b>CT</b>', 'McGuire WP, Hoskins WJ, Brady MF, et al. Cyclo-phosphamide and cisplatin compared with paclitaxel and cisplatin in patients with stage III and stage IV ovarian cancer. $N Engl J Med. 1996;334(1):1-6.$<br> Neijt JP, Engelholm SA, Tuxen MK, et al. Exploratory phase III study of paclitaxel and cisplatin versus paclitaxel and carboplatin in advanced ovarian cancer. $J Clin Oncol. 2000;18(17):3084-3092.$', null, '<b>Paclitaxel</b> 135 mg/m<sup>2</sup> CIVI over 24 hours, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for 6 cycles<br> <b>OR</b><br> <b>Paclitaxel</b> 175 mg/m<sup>2</sup> IV over 3 hours, day 1<br> <b>Cisplatin</b> 75 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days', null);
