insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (947, null, 'Tisagenlecleucel (Kymriah)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: Reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Fludarabine:</strong><br>No dose reduction</li><li><strong>Tisagenlecleucel:</strong><br>Dosage adjustments have not been studied yet.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-29 mL/min: Reduce dose by 25%<br>If CrCl &lt; 10 mL/min: Reduce dose by 50%</li><li><strong>Fludarabine:</strong><br>If CrCl 10-50 mL/min: Reduce dose by 25%&nbsp;<br>If CrCl &lt;10 mL/min: Reduce dose by 50%</li><li><strong>Tisagenlecleucel:</strong><br>Dosage adjustments have not been studied yet.</li></ul>', 'Moderate', null, null, null, '<p><strong>Cyclophosphamide:</strong> 250 mg/m2 IV on days -5, -4, and -3<br><strong>Fludarabine:</strong> 25 mg/m2 IV on day -5, -4, and -3<br><strong>Tisagenlecleucel:</strong> 0.6-6.0 x 10^8 CAR-positive T cells/kg body weight IV on day 1</p>', null);
