insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1154, null, 'Doxorubicin + Olaratumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Olaratumab:</strong><br><strong>&nbsp; </strong>There are no dosage adjustments provided in the manufacturer’s labeling</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Olaratumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>There are no dosage adjustments provided in the manufacturer’s labeling</li><li><strong>Doxorubicin</strong>:<br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Doxorubicin: </strong>75 mg/m2 IV on day1<br><strong>Olaratumab: </strong>15 mg/kg IV on days 1 and 8<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (360, 'Dabrafenib:Tafilnar<br> Trametinib:Mekinist', 'Dabrafenib/Trametinib', '', 'Minimal', '<b>Dabrafenib/Trametinib</b>', 'Robert c, Karaszewska B, Schachter J, et al. Improved overall Survival in Melanoma with combines Dabrafenib and Trametinib. $ N Engl J Med. 2015 Jan 1;372(1):30-9.$', null, '<bDabrafenibt</b> 150 mg PO  BID <br> Trametinib</b>2 mg once daily ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (171, 'Cisplatin : CDDP<br>Topotecan : Hycamtin', 'Cisplatin/Topotecan', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Topotecan:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Topotecan:<br>If CrCl > 40 mL/min, no dose reduction   <br>                                                                          If CrCl 20-39 mL/min, reduce to 0.75 mg/m2 </li></ul>', 'High', '<b>Cisplatin/Topotecan</b>', 'Long HJ, Bundy BN, Grendys EC, et al. Randomized Phase III trial of cisplatin with or without topotecan in carcinoma of the uterine cervix: a Gynecologic Oncology Group study. $J Clin Oncol. 2005;23(21):4626-4633.$', null, '<b>Cisplatin</b> 50 mg/m<sup>2</sup> IV, day 1<br> <b>Topotecan</b> 0.75 mg/m<sup>2</sup>/d IV, days 1-3<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (178, 'Sunitinib : Sutent', 'Sunitinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Sunitinib:</strong><br>If Child-Pugh A or B, no dose reduction&nbsp;<br>If Child-Pugh C, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Sunitinib:</strong><br>If CrCl &gt; 42 mL/min, no dose reduction</li></ul>', 'Low', '<b>Sunitinib</b>', 'Motzer RJ, Hutson TE, Tomczak P, et al. Sunitinib versus interferon alfa in metastatic renal-cell carcinoma. $N Engl J Med. 2007;356(2):115-124$', null, '<p><strong>Sunitinib malate:</strong> 50 mg PO daily for 28 days&nbsp;<br>Repeat cycle every 6 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (894, null, 'Docetaxel + Estramustine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Docetaxel:</strong><br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline Phosphatase &gt;2.5 upper normal limit, do not use</li><li><strong>Estramustine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Docetaxel:</strong><br>No dose reduction</li><li><strong>Estramustine:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Docetaxel:</strong> 35 mg/m2 IV on day 2 of weeks 1 and 2<br><strong>Estramustine:</strong> 420 mg PO for the first 4 doses and 280 mg PO for the next 5 doses on days 1-3 of weeks 1 and 2.</p><p>Repeat cycle every 21 days. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Dexamethasone is administered at 4 mg PO bid on days 1-3 of weeks 1 and 2.<br><strong>OR</strong><br><strong>Docetaxel:</strong> 60 mg/m2 IV on day 2<br><strong>Estramustine:</strong> 280 mg PO tid on days 1-5</p><p>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (180, 'Carboplatin : Paraplatin<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Carboplatin', '<b>Hepatic Impairment</b><ul><li>Carboplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Carboplatin:<br>Dosing based on AUC </li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'Moderate', '<b>Gemcitabine/Carboplatin</b>', 'Bamias A, Moulopoulos LA, Koutras A, et al. The combination of gemcitabine and carboplatin as first-line treatment in patients with advanced urothelial carcinoma. $Cancer. 2006;106(2):297-303.$', null, '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup> IV over 30 minutes, days 1 and 8<br> <b>Carboplatin</b> AUC of 5 IV over 1 hour, day 1<br> Repeat cycle every 21 days upto 6 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (181, 'Cisplatin : CDDP<br>Gemcitabine : Gemzar, various', 'Gemcitabine/Cisplatin', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Gemcitabine:<br>No dose reduction </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Gemcitabine:<br>No dose reduction </li></ul>', 'High', '<b>Gemcitabine/Cisplatin</b>', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', null, '<b>Gemcitabine</b> 1,000 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1,8,15<br> <b>Cisplatin</b> 70mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (182, 'Cisplatin : CDDP<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'MVAC', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>MVAC</b>', 'von der Maase H, Hansen SW, Roberts JT, et al. Gemcitabine and cisplatin versus methotrexate, vinblastine, doxorubicin, and ciplatin in advanced or metastatic bladder cancer: results of a large, randomized, mulinational, multicenter, phase III study. $J Clin Oncol. 2000;18(17):3068-3077.$', null, '<b>Methotrexate</b> 30 mg/m<sup>2</sup>/d IV, days 1, 15, 22<br> <b>Vinblastine</b> 3 mg/m<sup>2</sup>/d IV, days 2, 15,22<br> <b>Doxorubicin</b> 30 mg/m<sup>2</sup> IV, day 2<br> <b>Cisplatin</b> 70 mg/m<sup>2</sup> IV, day 2<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (347, 'Beleodaq', 'Belinostat', '', 'Minimal', '<b>Belinostat</b>', 'Foss F, Advani R, Duvic M, et al. A Phase II trial of Belinostat (PXD101) in patients with relapsed or refractory peripheral or cutaneous T-cell lymphoma.$Br J Haematol. 2015 Mar;168(6):811-9.$', null, '<b>Belinostat</b> 1000 mg/m<sup>2</sup>  days 1-5, <br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (193, 'Bicalutamide : Casodex<br>Goserelin : Zoladex', 'Bicalutamide/Leuprolide.  ', '<b>Hepatic Impairment</b><ul><li>Bicalutamide:<br>No dose reduction</li><li>Goserelin:<br>No dose reduction </li><li>Leuprolide:<br>No dose reduction</li></ul><b>Renal Impairment</b><ul><li>Bicalutamide:<br>No dose reduction </li><li>Goserelin:<br>No dose reduction </li></ul>', 'N/A', 'Bicalutamide/Leuprolide.  ', 'Schellhammer P, Sharifi R, Block N, et al. A controlled trial of bicalutamide versus flutamide, each in combination with luteinizing hormone-releasing hormone analogue therapy, in patients with advanced prostate cancer. $Urology. 1995;45(5):745:752.$', null, '<b>Bicalutamide</b> 50 mg/d PO <b>WITH</b><br> <b>Leuprolide acetate depot</b> 7.5 mg IM<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (179, '', 'Temsirolimus', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Temsirolimus:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Temsirolimus:</strong><br>No dose reduction</li></ul>', 'Low', '<b>Temsirolimus</b>', 'Hudes G, Carducci M, Tomczak P, et al. Temsirolimus, interferon alpha, or both for advanced renal-cell carcinoma. $N Engl J Med. 2007;356(22):2271-2281.$', null, '<p><strong>Temsirolimus:</strong> 25 mg IV over 30 minutes weekly.</p><p>Continue treatment until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (183, 'Bleomycin : Blenoxane<br>Cisplatin : CDDP<br>Etoposide : VP-16', 'BEP', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>BEP</b>', 'Nichols CR, Catalano PJ, Crawford ED, et al. Randomized comparison of cisplatin and etoposide and either bleomycin or ifosfamide in treatment of advanced disseminated germ cell tumors: an Eastern Cooperative Oncology Group, jSouthwest Oncology Group, and Cancer and Leukemia Group B Study. $J Clin Oncol. 1998;16(4):1287-1293.$', null, '<b>Bleomycin</b> 30 units IV bolus, weekly for 12 weeks<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV over 30-60 minutes,days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV over 30-60 minutes, days 1-5<br> Repeat every 21 days for 4 courses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (184, 'Cisplatin : CDDP<br>Etoposide : VP-16', 'EP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li></ul>', 'High', '<b>EP</b>', 'Motzer RJ, Sheinfeld J, Mazumdar M, et al. Etoposide and cisplatin adjuvant therapy for patients with pathologic stage II germ cell tumors. $J Clin Oncol. 1995;13(11):2700-2704.$', null, '<b>Etoposide</b> 100 mg/m<sup>2</sup>/d CIVI, days 1-5<br> <b>Cisplatin</b> 20 mg/m<sup>2</sup>/d IV, days 1-5<br> Repeat after 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (185, 'Cisplatin : CDDP<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Paclitaxel : Taxol', 'TIP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Ifosfamide:<br>No dose reduction</li><li>Mesna:<br>No dose reduction</li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'High', '<b>TIP</b>', 'Kondagunta GV, Bacik J, Donadio A, et al. Combination of paclitaxel, ifosfamide, and cisplatin is an effective second-line therapy for patients with relapsed testicular germ cell tumors. $J Oncol. 2005;23(27):6549-6555.$', null, '<b>Paclitaxel</b> 250 mg/m<sup>2</sup>/d CIVI over 24 hours, day 1<br> <b>Ifosfamide</b> 1,500 mg/m<sup>2</sup>/d IV over 1 hour, days 2-5<br> <b>Cisplatin</b> 25 mg/m<sup>2</sup>/d IV over 30 minutes, days 2-5<br> <b>Mesna</b> 500 mg/m<sup>2</sup> IV before ifosfamide, and at 4 and 8 hours after ifosfamide daily, days 2-5<br> <b>Peg-Filgrastim</b> 6 mcg SQ, days 6<br> Repeat every 21 days for 4 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (186, 'Cisplatin : CDDP<br>Etoposide : VP-16<br>Mesna : Mesnex, various<br>Vinblastine : Velban', 'VIP', '<b>Hepatic Impairment</b><ul><li>Cisplatin:<br>No dose reduction</li><li>Etoposide:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li><li>Mesna:<br>No dose reduction</li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Cisplatin:<br>If CrCl 30-60 mL/min, reduce dose by 50%  <br>                                                                    If CrCl < 30 mL/min, do not use</li><li>Etoposide:<br>If CrCl 10-50 mL/min, reduce dose by 25%   <br>                                                                   If CrCl < 10 mL/min, reduce dose by 50% </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>VIP</b>', 'Loehrer PJ, Lauer R, Roth BJ, et al. Salvage therapy in recurrent germ-cell cancer: ifosfamide and cisplatin plus either vinblastine or etoposide. $Ann Intern Med. 1988;109(7):540-546.$', null, '<b>Vinblastine</b> 0.11 mg/kg/d IV, days 1, 2<br> <b>OR</b><br> <b>Etoposide</b> 75 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>AND</b><br> <b>Infosfamide</b> 1,2000 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 20mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Mesna</b> 400 mg IV 15 minutes prior to ifosfamide, <b>THEN</b> 1,200 mg/d CIVI, days 1-5<br> Repeat every 21 days for 4 cycles', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (187, 'Cabazitaxel : Jevtana<br>Prednisone : various', 'Cabazitaxel', '<b>Hepatic Impairment</b><ul><li>Cabazitaxel:<br></li></ul><b>Renal Impairment</b><ul><li>Cabazitaxel:<br>Caution should be used in patients with severe renal impairment (creatinine clearence of <30 ml/hr)</li></ul>', 'Low', '<b>Cabazitaxel</b>', 'de Bono JS, Oudard S, Ozguroglu M, et al. Prednisone plus cabazitaxel or mitoxantrone for metastatic castration-resistant prostate cancer progressing after docetaxel treatment: a randomised open-label trial. $Lancet. 2010;376(9747):1147-1154.$', null, '<b>Cabazitaxel</b> 25 mg/m<sup>2</sup> IV over 1 hour, day 1<br> <b>Prednisone</b> 10 mg PO daily<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1155, null, 'Gemcitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Gemcitabine:</strong> 1,200 mg/m2 IV on days 1 and 8<br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (192, 'Mitoxantrone : Novantrone<br>Prednisone : various', 'Mitoxantrone/Prednisone', '<b>Hepatic Impairment</b><ul><li>Mitoxantrone:<br>If bilirubin > 3 mg/dL, reduce dose by 25%</li></ul><b>Renal Impairment</b><ul><li>Mitoxantrone:<br>No dose reduction</li></ul>', 'Low', '<b>Mitoxantrone/Prednisone</b>', 'Tannock IF, Osoba D, Stockler MR, et al. Chemotherapy with mitoxantrone plus prednisone or prednisonealone for symptptomatic hormone-resistant prostate cancer: a Canadian randomized trial with palliative endpoints. $J Clin Oncol. 1996;14(6)1756-1764.$', null, '<b>Mitoxantrone</b> 12 mg/m<sup>2</sup> IV, day 1<br> <b>Prednisone</b> 5 mg PO bid<br> Repeat every 21 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (262, 'Cytarabine : Ara-C', 'HiDAC', '<b>Hepatic Impairment</b><ul><li>Cytarabine:<br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>                                           If bilirubin > 3 mg/dL, reduce dose by 50%</li></ul><b>Renal Impairment</b><ul></ul>', 'Moderate', '<b>HiDAC</b>', 'Mayer RJ, Davis RB, Schiffer CA, et al. Intensive postremission chemotherapy in adults with acute myeloid deukemia. Cancer and Leukemia Group B. $N Engl J Med. 1994;331(14):896-903.$', null, 'For patients <60 years of age:<br> <b>Cytarabine</b> 3,000 mg/m<sup>2</sup> CIVI over 3 hours, every 12 hours, days 1, 3, 5<br> Repeat every 28 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (194, 'Degarelix : Firmagon', 'Degarelix', '<b>Hepatic Impairment</b><ul></ul><b>Renal Impairment</b><ul></ul>', 'N/A', '<b>Degarelix</b>', 'Klotz L, Boccon-Gibod L, Shore ND, et al. The efficacy and safety of degarelix: a 12-month, comparative, randomized, open-label, parallel-group phase III study in patients with prostate cancer. $BJU Int. 2008;102(11):1531-1538.$', null, '<b>Degarelix</b> 240 mg SQ <br> <b>FOLLOWED BY</b><br> 80 mg SQ<br> Repeat every 28 days for 13 courses', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (195, 'Abaraterone : Zytiga', 'Abaraterone', '<b>Hepatic Impairment</b><ul><li>Abaraterone:<br>No dosage adjustment is necessary for patients with baseline mild hepatic impairment. <br>In patients with baseline moderate hepatic impairment (Child-Pugh Class B), reduce the recommended dose of ZYTIGA to 250 mg once daily. If elevations in ALT or AST >5X ULN or total bilirubin >3X ULN occur in patients with baseline moderate hepatic impairment, discontinue ZYTIGA treatment. <br> The safety of ZYTIGA in patients with baseline severe hepatic impairment has not been studied. These patients should not receive ZYTIGA. For patients who develop hepatotoxicity during treatment, interruption of treatment</li></ul><b>Renal Impairment</b><ul><li>Abaraterone:<br>No dose adjustment needed</li></ul>', 'Low', '<b>Abaraterone</b>', 'Ryan CJ, et al. "Abiraterone in Metastatic Prostate Cancer without Previous Chemotherapy". $N. Engl. J. Med. 368 (2): 138–48.$', null, '<b>Abaraterone</b>1000 mg PO once daily on empty stomach<br> <b>Prednisone</b> 5 mg PO twice daily', null);
