insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (895, null, 'Docetaxel + Prednisone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Docetaxel:</strong><br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline Phosphatase &gt;2.5 upper normal limit, do not use</li><li><strong>Prednisone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Docetaxel:</strong><br>No dose reduction</li><li><strong>Prednisone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Docetaxel:</strong> 75 mg/m2 IV on day 1<br><strong>Prednisone:</strong> 5 mg PO bid<br>Repeat the cycle every 21 days for up to a total of 10 cycles.</p>', null);
