insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (359, 'Ipilumimab:Yeroy<br> Nivolumab:Opdivo', 'Ipilumimab/Nivolumab', '', 'Minimal', '<b>Ipilumimab/Nivolumab</b>', 'Postow, MA, Chesney J, Pavlick AC, et al. Nivolumab and Ipilumimab versus Ipilumimab in untreated melanoma. $N Engl J Med. 2015 May 21;372(21):2006-17. $', null, '<b>Ipilumimab</b> 3mg/kg  IV <br> Nivolumab </b> 1 mg/kg IV<br> every 3 weeks  x 4 cycles <br> followed by Nivolumab 3 mg/kg  every 3 weeks until disease progression on unaccceptable toxicity', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (329, '', 'MTX/6-MP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 5 mg/dL: Do not use</li><li><strong>6-Mercaptopurine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min: 50% of normal dosage.<br>If CrCl &lt; 30 mL/min: Do not use</li><li><strong>6-Mercaptopurine:</strong><br>No dose reduction</li></ul>', 'Low', '<b>MTX/6-MP</b>', 'Camitta B, Leventhal B, Lauer S, et al. Intermediate-dose intravenous methotrexate and mercaptopurine therapy for non-T, non-B acute lymphocytic leukemia of chldhood: a Pediatric Oncology Group study. $J Clin Oncol. 1989;7(10):1539-1544.$', null, '<p><strong>Methotrexate:</strong> 20 mg/m2 IM weekly, weeks 25-130<br><strong>Mercaptopurine:</strong> 50 mg/m2/day PO, weeks 25-130</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (311, 'Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various', 'VP/high-dose Ifos', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Ifosfamide:</strong><br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Ifosfamide</strong>: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥10 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose by 25%</li><li><strong>Mesna:</strong><br>No dose reduction</li></ul>', 'High', '<b>VP/high-dose Ifos</b>', 'Goorin AM, Harris MB, Bernstein M, et al. Phase II/III trial of etoposide and high-dose ifosfamide in newly diagnosed metastatic osteosarcoma: a pediatric oncology group trial. $J Clin Oncol. 2002;20(2):426-433.', null, '<p><strong>Etoposide:</strong> 100 mg/m2 days 1-5<br><strong>Ifosfamide:</strong> 3,500 mg/m2 days 1-5<br><strong>Mesna:</strong> 700 mg/m2 over 4 hours, then mesna 700 mg/m2 at hour 4, 7, 10, 13, days 1-5<br><strong>GCF:</strong> 5 mcg/kg/day begin day 6<br>Repeat cycle every 3 weeks for a total of 2 courses.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1054, null, 'Venetoclax', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Venetoclax:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If mild to moderate impairment (Child-Pugh classes A and B): 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child-Pugh class C): Reduce dose by 50%.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Venetoclax: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If<strong>&nbsp;</strong>CrCl ≥15 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If<strong>&nbsp;</strong>CrCl &lt;15 mL/min: Dosage adjustment not provided by the manufacturer.</li></ul>', 'Low', null, null, null, '<p><strong>Venetoclax:</strong> 20 mg/day PO on days 1-7<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 50 mg/day PO on days 8-14<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 100 mg/day PO on days 15-21<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 200 mg/day PO on days 22-28 and<br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; then 400 mg/day PO daily<br>Continue treatment until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1055, null, 'Duvelisib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Duvelisib:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Duvelisib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If<strong> </strong>CrCl 23 to 80 mL/minute: There are no dosage adjustments provided in the manufacturer’s labeling; however, this level of renal impairment had no clinically significant effect on duvelisib exposure.</li></ul><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;23 mL/minute: &nbsp;Dosage adjustment not provided by the manufacturer.</p>', 'Low', null, null, null, '<p><strong>Duvelisib: </strong>25 mg P0 bid<br>Repeat cycle every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (321, 'Dexamethasone : various<br>Prednisone : various<br>Vincristine : Oncovin, VCR', 'VCR/Asparaginase/Dex/MTX', '<b>Hepatic Impairment</b><ul><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate for MTX agent IT, minimal for others', '<b>VCR/Asparaginase/Dex/MTX</b>', 'Bostrom BC, Sensel MR, Sather HN, et al. Dexamethasone versus prednisone and daily oral versus weekly intravenous mercaptopurine for patients with standard-risk acute lymphoblastic leukemia: a report from the Children''s Cancer Group. $Blood 2003;101(10):3809-3817.$', null, '<b>Vincristine</b> 1.5 mg/m<sup>2</sup> IV, days 0,7, 14, 21<br> <b>Asparaginase</b> 6,000 units/m<sup>2</sup> IM, 3 times weekly for 9 doses, starting on days 2-4<br> <b>Dexamethasone</b> 6 mg/m<sup>2</sup>/d PO in 3 equal doses (was substituted for prednisone)<br> <b>Methotrexate</b> 8, 10, or 12 mg IT, age-adjusted, days 0, 14 (also days 7, 21 for patients with CNS disease at diagnosis)<br> <b>Prednisone</b> 40 mg/m<sup>2</sup>/d PO', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1163, null, 'Dexamethasone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Dexamethasone:</strong> 40 mg IV or PO on days 1-4, 9-12, and 17-20.<br>Repeat cycle every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (316, 'Cyclophosphamide : Cytoxan<br>Dactinomycin : Actinomycin<br>Vincristine : Oncovin, VCR', 'VAC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Dactinomycin:</strong><br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Dactinomycin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', '<b>VAC</b>', 'Crist WM, Anderson JR, Meza JL, et al. Intergroup rhabdomyosarcoma study-IV: results for patients with nonmetastatic disease. $J Clin Oncol. 2001;19(12):3091-3102.$', null, '<p><strong>Vincristine:</strong> 1.5 mg/m2 ( max 2 mg) IV weekly for 3 weeks<br><strong>Dactinomycin:</strong> 0.015 mg/kg/d (maximum, 0.5 mg/kg/day) IV for 5 days<br><strong>Cyclophosphamide:</strong> 2,200 mg/m2 IV, day 1</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (313, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Vincristine : Oncovin, VCR', 'VCR/Cyclophosphamide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', '<b>VCR/Cyclophosphamide</b>', 'Grier HE, Krailo MD, Tarbell NJ, et al. Addition of ifosfamide and etoposide to standard chemotherapy for Ewing''s sarcoma and primitive neuroectodermal tumor of bone. $N Engl J Med. 2003;348(8):694-701.$', null, '<p><strong>Vincristine:</strong> 2 mg/m2 IV, day 1<br><strong>Doxorubicin:</strong> 75 mg/m2 IV bolus, day 1<br><strong>Cyclophosphamide:</strong> 1,200 mg/m2 IV, day 1<br>Repeat every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (317, 'Bleomycin : Blenoxane<br>Dacarbazine : DTIC<br>Doxorubicin : Adriamycin, various<br>Vinblastine : Velban', 'ABVD', '<b>Hepatic Impairment</b><ul><li>Bleomycin:<br>No dose reduction</li><li>Dacarbazine:<br>No dose reduction</li><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Vinblastine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>  If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Bleomycin:<br>If CrCl 10-50 mL/min, reduce dose by 25% <br>                                                                     If CrCl < 10 mL/min, reduce dose by 50% </li><li>Dacarbazine:<br>If CrCl 10-50 mL/min, reduce dose by 25%    <br>                                                                  If CrCl < 10 mL/min, reduce dose by 50% </li><li>Doxorubicin:<br>No dose reduction </li><li>Vinblastine:<br>No dose reduction </li></ul>', 'High', '<b>ABVD</b>', 'Fryer CJ, Hutchinson RJ, Krailo M, et al. Efficacy and toxicity of 12 courses of ABVD therapy followed by low-dose regional radiation in advanced Hodgkin''s disease in children: a report from the Children''s Cancer Study Group. $J Clin Oncol. 1990;8(12):1971-1980.$', null, '<b>Doxorubicin</b> 25 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Bleomycin</b> 10 units/m<sup>2</sup>/d IV, days 1, 15<br> <b>Vinblastine</b> 6 mg/m<sup>2</sup>/d IV, days 1, 15<br> <b>Dacarbazine</b> 375 mg/m<sup>2</sup>/d IV, days 1, 15<br> Repeat every 14 days for 12 courses', null);
