insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (952, null, 'Romidepsin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Romidepsin:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;1 to 1.5 times ULN: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;1.5 to 3 times ULN: Reduce initial dose to 7 mg/m2. &nbsp; If bilirubin &gt;3 times ULN: Reduce initial dose to 5 mg/m2</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Romidepsin:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Romidepsin:</strong> 14mg/m2 IV on days 1, 8, and 15.<br>Repeat every 28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (355, 'Opdivo', 'Nivolumab', '', 'Minimal', '<b>Nivolumab</b>', 'Motzer RJ, Escudier B, McDermott DF, et al.  Nivolumab versus Everolimus in Advanced Renal Cell Carcinoma. $N Engl J Med. 2015 Nov 5;373(19):1803-13. $', null, '<b>Nivolumab</b> 3 mg/kg IV<br> Repeat Every 14 Days<br> Until disease progression or Unacceptable toxicity', null);
