insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (341, 'Necitumumab:Portrazza<br> Gemcitabine:Gemzar<br> Cisplatin<br>Various', 'Necitumumab/Gemcitabine/Cisplatin', '', 'Severe', '<b>Necitumumab<br>Gemcitabine<br>Cisplatin</b>', 'Thatcher N, Hirsch FR, Luft AV, et al. Necitumumab plus gemcitabine and cisplatin versus gemcitabine and cisplatin alone as first line therapy in patients with stage IV sqaumous non-small cell lung cancer (SQUIRE): an open label, randomised, controlled phase 3 trial. $Lancet Oncol. 2015 Jul;16(7):763-74. doi: 10.1016/S1470-2045(15)00021-2. Epub 2015 Jun 1.$', null, '<b>Necitumumab</b> 800 mg IV Day 1 and 8<br><b>Gemcitabine</b>1250 mg/m <sup>2</sup> IV<br><b>Cisplatin</b> 75 mg/m<sup>2</sup>IV<br>Repeat every 21 days<br>Continue Necitumumab until disease progression or intolerable toxicity', null);
