insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (351, 'Ofatumumab:Arzerra<br> Chlorambucil:Various', 'Ofatumumab/Chlorambucil', '', 'Minimal', '<b>Ofatumumab<br>Chlorambucil</b>', 'Hillmen P, Robak T, Janssens A, et al. Chlorambucil plus ofatumumab versus chlorambucil alone in previously untreated patients with chronic lymphocytic leukemia  (Complimet1 (: A rondomised, multicentre, open-label phase 3 trial. $Lancet. 2015 May 9;385(9980):1873-83. $', null, '<b>Ofatumumab</b> 300mg IV on Day 1 and 1000 mg on day 8, Cycle 1. Subsequent cycles 1000 mg IV on Day 1 only <br><b>Chlorambucil</b> PO 10 mg/m<sup>2</sup>, day 1 to 7. Repeat every 28 days', null);
