insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (353, 'Ixazomib:Ninlaro<br> Lenalidamide:Revlimid<br> Dexamethasone:Decadron', 'Ixazomib/Lenolidamide/Dexamethasone', '', 'Moderate', '<b>Ixazomib/Lenolidamide/Dexamethasone</b>', 'Kumar SK, Berdeja JG, Niesvizky, R. Safety and tolerability of ixazomib, an oral proteasome inhibitor, in combination with lenalidomide and dexamethasone in patients with previously unreated multiple myeloma . $Lancet Oncol. 2014 Dec;15(13):1503-12. $', null, '<b>Ixazomib</b> 4 mg PO  on days 1,8,15<br> <br> <b>Lenalidomide</b> 25mg PO on day 1 thru 21<br> <b>Dexamethasone</b> 40 mg PO, days 1,8,15,22<br> Repeat every 28 days', null);
