insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (377, 'Obinutuzumab : Gazyva', 'Obinutuzumab/Chlorambucil', '', 'Minimal', '<b>Obinutuzumab/Chlorambucil</b>', 'Goede V, et. al. Obinutuzumab plus chlorambulcil in patients with CLL and coexisting conditions. $N Engl J Med. 2014 Mar 20;370(12):1101-10.$', null, '<b>Obinutuzumab/Chlorambucil</b><br><b>Cycle 1:</b><br>100 mg IV on day 1 and 900 mg IV on day 2, 1,000 mg on day 8 and 15<br><b>Cycle 2-6:</b><br>1,000 mg IV on day 1<br><b>Chlorambucil</b><br>0.5 mg/kg on Day 1 and 15 of each cycle', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (302, 'Cisplatin : CDDP<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16', 'A<sub>1</sub>', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>If CrCl 30-60 mL/min, reduce dose by 50%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', '<b>A<sub>1</sub></b>', 'Kaneko M, Tsuchida Y, Mugishima H, et al. Intensified chemotherapy increases the survival rates in patients with stage 4 neuroblastoma with MYCN amplification. $J Pediatr Hematol Oncol. 2002;24(8):613-621.$', null, '<b>Cyclophosphamide</b> 1,200 mg/m<sup>2</sup> IV, day 1<br> <b>Doxorubicin</b> 40 mg/m<sup>2</sup> IV, day 3<br> <b>Etoposide</b> 100 mg/m<sup>2</sup>/d IV, days 1-5<br> <b>Cisplatin</b> 90 mg/m<sup>2</sup> IV, day 5', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (385, 'Nivolumab (Opdivo)', 'Nivolumab monotherapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Nivolumab:</strong>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Nivolumab:</strong>&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul>', 'Low', '', 'CheckMate 275: Sharma P, Retz M, Siefker-Radtke A, Baron A, Necchi A, Bedke J, Plimack ER, Vaena D, Grimm MO, Bracarda S, Arranz JÁ, Pal S, Ohyama C, Saci A, Qu X, Lambert A, Krishnan S, Azrilevich A, Galsky MD. Nivolumab in metastatic urothelial carcinoma after platinum therapy (CheckMate 275): a multicentre, single-arm, phase 2 trial. Lancet Oncol. 2017 Mar;18(3):312-322. Epub 2017 Jan 25.', 'METASTATIC', '<h4>&nbsp;</h4><p><strong>Nivolumab (Opdivo):</strong> 240 mg IV on day 1</p><p>Repeat cycle every 14 days. May also consider administering at 480 mg IV on day 1 with cycles repeated every 28 days.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1059, null, 'CAV', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Cyclophosphamide:</strong> 1,000 mg/m2 IV on day 1<br><strong>Doxorubicin:</strong> 40 mg/m2 IV on day 1<br><strong>Vincristine:</strong> 1mg/m2 IV on day 1 (maximum, 2 mg)<br>Repeat cycle every 21 days&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1061, null, 'Etoposide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Etoposide</strong>:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Etoposide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Etoposide:</strong> 160 mg/m2 PO on days 1-5<br>Repeat cycle every 28 days.</p><p>OR</p><p><br><strong>Etoposide:</strong> 50 mg/m2/day PO on days 1-21<br>Repeat cycle as tolerated.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (380, 'Gemcitabine (Gemzar), Cisplatin (Platinum) ', 'Gemcitabine and Cisplatin (single-dose cisplatin)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul>', 'High', '', 'Dash A, Pettus JA 4th, Herr HW, Bochner BH, Dalbagni G, Donat SM, Russo P, Boyle MG, Milowsky MI, Bajorin DF. A role for neoadjuvant gemcitabine plus cisplatin in muscle-invasive urothelial carcinoma of the bladder: a retrospective experience. Cancer. 2008 Nov 1;113(9):2471-7', 'NEOADJUVANT', '<h4>&nbsp;</h4><p><strong>Cisplatin (Platinol): </strong>75 mg/m2&nbsp;IV on day 1.</p><p><strong>Gemcitabine (Gemzar):</strong>&nbsp;1,000 mg/m2&nbsp;IV once per day on days 1, 8, 15.</p><p>Repeat cycle every 28 days.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (602, '', 'Oxaliplatin + 5-Fluorouracil + Leucovorin (FOLFOX6)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>No dose reduction.</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: 75% dosage</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>Oxaliplatin:&nbsp;</strong>100 mg/m<sup>2</sup>&nbsp;IV on day 1</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (334, 'Azacitidine : Vidaza<br>Cytarabine : Ara-C<br>Etoposide : VP-16', 'HI-C DAZE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Daunorubicin:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Azacitidine:</strong><br>Dosage adjustment not provided by the manufacturer. Use is contraindicated in patients with advanced malignant hepatic tumors.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Daunorubicin:</strong><br>If Cr &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Azacitidine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> No dose reduction.</li></ul>', 'High', '<b>HI-C DAZE</b>', 'Hurwitz CA, Mounce KG, Grier HE. Treatment of patients with acute myelogenous leukemia: review of clinical trials of the past decade. $J Pediatr Hematol Oncol. 1995;17(3):185-197.$', null, '<p><strong>Daunorubicin:</strong> 30 mg/m2/d IV, days 1-3<br><strong>Cytarabine:</strong> 3,000 mg/m2/d IV every 12 hours, days 1-4<br><strong>Etoposide:</strong> 200 mg/m2/d IV, days 1-3, 6-8<br><strong>Azacitidine:</strong> 150 mg/m2/d IV, days 3-5, 8-10</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (335, '', 'MACE (Consolidation)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Amsacrine:</strong><br>If bilirubin &gt;2 mg/dL: Reduce dose by at least 50%.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: Reduce dose by 40%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 45 mL/minute: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Amsacrine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If serum creatinine 1.2 to 1.8 mg/dL: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If serum creatinine &gt;2 to 3 mg/dL or oliguric patients: Reduce initial dose by 30% to 40%.</li></ul>', 'Low', '<b>MACE/consolidation</b>', 'Stevens RF, Hann IM, Wheatley K, Gray RG. Marked improvements in outcome with chemotherapy alone in paediatric acute myeloid leukaemia: results of the United Kingdom Medical Research Council''s 10th AML trial. $Br J Haematol. 1998;101(1):130-140.$', null, '<p><strong>Amsacrine:</strong> 100 mg/m2 IV (1-hour infusion), days 1-5<br><strong>Cytarabine:</strong> 200 mg/m2 CIVI, days 1-5<br><strong>Etoposide:</strong> 100 mg/m2 IV (1-hour infusion), days 1-5</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1164, null, 'EP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>If CrCl 30-60 mL/min, reduce dose by 50%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', null, null, null, '<p><strong>Etoposide:</strong> 100 mg/m2/day IV on days 1-5<br><strong>Cisplatin: </strong>20 mg/m2/day IV on days 1-5<br>Repeat cycle every 21 days.</p>', null);
