insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (390, 'Erdafitinib (Balversa)', 'Erdafitinib monotherapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Erdafitinib (Balversa):</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Erdafitinib (Balversa):</strong><br>No dose reduction</li></ul>', 'Low', '', 'BLC2001: Loriot Y, Necchi A, Park SH, Garcia-Donas J, Huddart R, Burgess E, Fleming M, Rezazadeh A, Mellado B, Varlamov S, Joshi M, Duran I, Tagawa ST, Zakharia Y, Zhong B, Stuyckens K, Santiago-Walker A, De Porre P, O''Hagan A, Avadhani A, Siefker-Radtke AO; BLC2001 Study Group. Erdafitinib in locally advanced or metastatic urothelial carcinoma. N Engl J Med. 2019 Jul 25;381(4):338-348.', 'METASTATIC', '<h4>Biomarker eligibility criteria</h4><h4>Alterations: FGFR3 mutation, FGFR2 fusion, or FGFR3 fusion</h4><p><strong>Erdafitinib (Balversa):</strong>&nbsp;8 mg PO once per day</p><ul><li>If serum phosphorus level and tolerability are acceptable at days 14 to 21, increase to 9 mg PO once per day</li><li>Additional dose adjustments per package insert</li></ul><p>Continue until disease progression or unacceptable toxicity occurs.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (391, 'Carboplatin (Paraplatin), Gemcitabine (Gemzar)', 'Carboplatin + Gemcitabine (AUC4.5/1000)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Carboplatin:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Carboplatin</strong>:<br>Dosing based on&nbsp;<strong>mg/m2</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: &nbsp;Reduce dose to 50% of the usual dose. &nbsp; If GFR &lt;10 mL/min: Reduce dose to 75% of the usual dose.</li></ul>', 'High', '', 'De Santis M, Bellmunt J, Mead G, Kerst JM, Leahy M, Maroto P, Skoneczna I, Marreaud S, de Wit R, Sylvester R. Randomized phase II/III trial assessing gemcitabine/ carboplatin and methotrexate/carboplatin/vinblastine in patients with advanced urothelial cancer "unfit" for cisplatin-based chemotherapy: phase II--results of EORTC study 30986. J Clin Oncol. 2009 Nov 20;27(33):5634-9', 'METASTATIC', '<h4><strong>Gemcitabine (Gemzar):&nbsp;</strong>1000 mg/m2&nbsp;IV over 30 minutes once per day on days 1 &amp; 8,&nbsp;given first.</h4><h4><strong>Carboplatin (Paraplatin):</strong>&nbsp;AUC 4.5, IV over 60 minutes once on day 1,&nbsp;given second.</h4><h4>Repeat cycle every 21-days for up to 6 cycles.</h4><p>Patients who achieved complete response were given two additional cycles of treatment.</p>', 100);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1060, null, 'CAE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', null, null, null, '<p><strong>Cyclophosphamide:</strong> 1,000 mg/m2 IV on day 1<br><strong>Doxorubicin:</strong> 45 mg/m2 IV on day 1<br><strong>Etoposide:</strong> 50 mg/m2 IV on days 1-5<br><br>Repeat cycle every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (850, null, 'VAD', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Dactinomycin:</strong><br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Dactinomycin:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Vincristine:</strong> 1.5 mg/m2 IV weekly for 10 weeks, then every 3 weeks for 15 weeks<br><strong>Dactinomycin:</strong> 1.5 mg/m2 IV every 6 weeks for 26 weeks<br><strong>Doxorubicin:</strong> 40 mg/m2 IV every 6 weeks for 1 year (stage III)<br><strong>OR</strong><br><strong>Vincristine:</strong> 1.5 mg/m2 IV, day 1<br><strong>Dactinomycin:</strong> 15 mcg/kg/d IV, days 1-5 or 60 mcg/kg IV, day 1<br>Repeat every 6 weeks.</p><p>&nbsp;If stage III or IV add: <strong>Doxorubicin:</strong> 20 mg/m2 IV, day 1, and RT</p>', null);
