insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (821, null, ' Ara-C+Daunorubicin+Gemtuzumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Daunorubicin:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Gemtuzumab:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Daunorubicin:</strong><br>If Cr &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Gemtuzumab:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Cytarabine:</strong> 200 mg/m2 IV on days 1-7<br><strong>Daunorubicin:</strong> 60 mg/m2 IV on days 1-3<br><strong>Gemtuzumab:</strong> 3 mg/m2 IV on days 1, 4, and 7.</p>', null);
