insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (480, 'Cisplatin: Platinol Gemcitabine: Gemzar', 'Cisplatin + Gemcitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li></ul>', 'High', '', 'SQUIRE: Thatcher N, Hirsch FR, Luft AV, Szczesna A, Ciuleanu TE, Dediu M, Ramlau R, Galiulin RK, Bálint B, Losonczy G, Kazarnowicz A, Park K, Schumann C, Reck M, Depenbrock H, Nanda S, Kruljac-Letunic A, Kurek R, Paz-Ares L, Socinski MA; SQUIRE Investigators. Necitumumab plus gemcitabine and cisplatin versus gemcitabine and cisplatin alone as first-line therapy in patients with stage IV squamous non-small-cell lung cancer (SQUIRE): an open-label, randomised, controlled phase 3 trial. Lancet Oncol. 2015 Jul;16(7):763-74. Epub 2015 Jun 1.', 'METASTATIC', '<p><strong>Cisplatin:</strong>&nbsp