insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (219, 'Capecitabine : Xeloda<br>Oxaliplatin : Eloxatin, Oxaliplatin, various', 'Oxaliplatin + Capecitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>No dose reduction</li><li><strong>Oxaliplatin:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>If CrCl 30-50 mL/min: Reduce dose by 25%&nbsp;<br>If CrCl &lt; 30 mL/min: Do not use</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl ≥30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Reduce dose by 25%&nbsp;</li></ul>', 'Moderate', '<b>Oxaliplatin Capecitabine</b>', 'Hainsworth JD, Spigel DR, Burris HA, et al. Oxaliplatin and capecitabine inn the treatment of patients with recurrent or refractory carcinoma of unknown primary site: A phase 2 trial of the Sarah Cannon Oncology Research Consortium. $Cancer. 2010; 116(10):2448-2454.$', null, '<p><strong>Oxaliplatin:</strong> 130 mg/m2 IV, day 1<br><strong>Capecitabine:</strong> 1,000 mg/m2 PO, days 1-14<br>Repeat cycle every 21 days up to 6 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1068, null, 'Entrectinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Entrectinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (total bilirubin &lt;1 to 1.5 times ULN): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If moderate to severe impairment (total bilirubin &gt;1.5 times ULN): Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Entrectinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 30 to 90 mL/min: No dosage adjustment necessary. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: There are no dosage adjustments provided in the manufacturer''s labeling.</li></ul>', 'Low', null, null, null, '<p><strong>Entrectinib:</strong> 600 mg PO daily.</p><p>Continue treatment until disease progression.</p>', null);
