insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (609, '', 'Capecitabine + Oxaliplatin (XELOX)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Oxaliplatin:&nbsp;</strong><br>&nbsp;If CrCl ≥30 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Reduce initial dose from 85 mg/m2 to 65 mg/m2.</li></ul>', 'Medium', '', '', 'METASTATIC', '<p><strong>Capecitabine:&nbsp;</strong>1,000 mg/m2&nbsp;PO bid on days 1-14</p><p><strong>Oxaliplatin:&nbsp;</strong>130 mg/m2&nbsp;IV on day 1</p><p>Repeat every 21 days. May decrease the dose of capecitabine to 850 mg/m2&nbsp;PO twice daily and the dose of oxaliplatin to 100 mg/m2&nbsp;IV to reduce risk of toxicity without compromising clinical efficacy.</p><p><strong>OR</strong></p><p><strong>Capecitabine:&nbsp;</strong>1,750 mg/m2&nbsp;PO twice daily on days 1-7</p><p><strong>Oxaliplatin:&nbsp;</strong>85 mg/m2&nbsp;IV on day 1</p><p>Repeat every 14 days. May decrease dose of capecitabine to 1,000-1,250 mg/m2&nbsp;PO twice daily to reduce the risk of toxicity without compromising clinical efficacy.</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (879, null, 'Octreotide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Octreotide:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Octreotide:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'N/A', null, null, null, '<p><strong>Octreotide:</strong> 150-250 μg SC tid.<br>Continue until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (878, null, 'Capecitabine + Temozolomide (Pancreatic Endocrine Carcinoma)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>No dose reduction</li><li><strong>Temozolomide:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Temozolomide:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Capecitabine: </strong>750 mg/day PO on days 1-14<br><strong>Temozolomide:</strong> 200 mg/m2/day PO on days 10-14</p><p>Repeat cycle every 28 days.</p>', null);
