insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (962, null, 'Lenalidomide (Mantle cell lymphoma)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>If CrCl &gt;60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 30 to 60 mL/min: 5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min not requiring dialysis: 2.5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min requiring dialysis: 2.5 mg once daily (administer after dialysis on dialysis days).</li></ul>', 'Low', null, null, null, '<p><strong>Lenalidomide:</strong> 25 mg/day PO on days 1-21<br>Repeat cycle every 28 days for up to 52 weeks.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (227, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'CODOX-M/IVAC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Ifosfamide:</strong><br>No dose reduction</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin</strong>:<br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% normal dose.</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'Moderate', '<b>CODOX-M/IVAC</b>', 'Mead GM, Sydes MR, Walewski J, et al. An international evaluation of CODOX-M and CODOX-M alternating with IVAC in adult Burkitt''s lymphoma: results of United Kingdom Lymphoma Group LY06 Study. $Ann Oncol. 2002, 13(8): 1254-1274.$', null, '<p>Cycles 1 AND 3 (CODOX-M)<br><strong>Cyclophosphamide:</strong> 800 mg/m2 IV, day 1<br><strong>Cyclophosphamide:</strong> 600 mg/m2/d IV, days 2-5<br><strong>Doxorubicin:</strong> 40 mg/m2 IV, day 1<br><strong>Vincristine:</strong> 1.5 mg/m2 IV up to a maximum dose of 2 mg, days 1,8 for cycle 1, days 1,8, 15 for cycle 2<br><strong>Methotrexate:</strong> 1,200 mg/m2 IV over 1 hour, day 10, <strong>THEN</strong> 240 mg/m2/h CIVI for the next 23 hours<br><strong>Leucovorin calcium: </strong>192 mg/m2 IV, starting at hour 36 <strong>THEN</strong> 12 mg/m2 every 6 hours until Methotrexate level is &lt;5*10-8 M<br><strong>Filgrastim: </strong>5 mcg/kg/d SQ until ANC &gt;1,000/mm3<br><strong>Methotrexate:</strong> 12mg IT, day 15<br><strong>Leucovorin calcium:</strong> 15 mg PO 24 hours after Methotrexate, day 16<br>CNS PROPHYLAXIS<br><strong>Cytarabine:</strong> 70 mg IT, days 1,3,5<br><strong>Methotrexate:</strong> 12 mg IT, day 12 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cycles 2 AND 4 (IVAC)<br><strong>Ifosfamide:</strong> 1,500mg/m2 IV over 1 hour, days 1-5<br><strong>Mesna:</strong> 360 mg/m2 IV over 1 hour, <strong>Then</strong> 360 mg/m2 IV every 3 hours for a maximum of 7 doses per 24 hour period, days 1-5<br><strong>Cytarabine:</strong> 2,000 mg/m2 IV over 3 hours every 12 hours, days 1 and 2<br><strong>Methotrexate:</strong> 12 mg IT, day 5<br><strong>Leucovorin calcium:</strong> 15 mg PO 24 hours after Methotrexate, day 6<br><strong>Filgrastim:</strong> 5 mcg/kg/d SQ until ANC &gt; 1,000/mm3</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (226, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Rituximab : Rituxan<br>Vincristine : Oncovin, VCR', 'R - CHOP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Rituximab:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Rituximab:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul>', 'Moderate', '<b>R - CHOP</b>', 'Coiffier B, Leapage E, Briere J, et al. CHOP chemotherapy plus rituximab compared with CHOP alone in elderly patients with diffuse large B-cell lymphoma. $N Engl J Med. 2002;346(4):235-242.$<br> Czuczman MS, Grillo-Lopez AJ, White CA, et al. Treatment of patients with low-grade B-cell lymphoma with the combination of chimeric anti-CD20 monoclonal antibody and CHOP chemotherapy. $J Clin Oncol. 1999; 17(1):268-276$.', null, '<p><strong>Cyclophosphamide:</strong> 750 mg/m2 IV, day 1<br><strong>Doxorubicin:</strong> 50 mg/m2 IV, day 1<br><strong>Vincristine:</strong> 1.4 mg/m2 IV, day 1 up to a maximum dose of 2mg<br><strong>Prednisone:</strong> 40 mg/m2/d PO, days 1-5<br><strong>Rituximab:</strong> 375 mg/m2 IV, day 1<br>Repeat every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (242, 'Rituximab : Rituxan', 'Rituximab Maintenance', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Rituximab:</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Rituximab:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li></ul>', 'Low', '<b>Rituximab Maintenance</b>', 'van Oers NH, Van Glabbeke M, Glurgea L, et al. Rituximab maintenance treatment of relapsed/residual follicular Non Hodgikin''s lymphoma: Long-term outcome or EROTC 20891 phase III randomized intergroup study. $ JClin Oncol. 2010;28(17):2853-2858.$', null, '<p><strong>Rituximab:</strong> 375 mg/m2/day IV, day 1<br>Repeat every 90 days until relapse or maximum of 2 years.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (261, '', 'Arsenic trioxide (acute promyelocytic leukemia only)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Arsenic trioxide:</strong>&nbsp;<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Arsenic trioxide:</strong>&nbsp;<br>If CrCl ≥30 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: Use with caution, may require dosage reduction (monitor closely for toxicity)</li></ul>', 'Low', '<b>Arsenic</b>', 'Trisenox [package insert]. $Frazer, PA: Cephalon; 2010.$', null, '<p><strong>Arsenic trioxide:</strong> 0.15 mg/kg IV daily.</p><p>Continue treatment until bone marrow remission up to a maximum of 60 doses.<br>Patients who meet the criteria for clinical CR can receive an additional course<br>of arsenic trioxide as consolidation therapy beginning 3-4 weeks after completion of<br>induction therapy up to a cumulative total of 25 doses.</p>', null);
