insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (251, 'Cyclophosphamide : Cytoxan<br>Cytarabine : Ara-C<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various<br>Prednisone : various<br>Thioguanine : 6-TG<br>Vincristine : Oncovin, VCR', 'CALGB 8811', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Daunorubicin:</strong><br>If bilirubin 1.2-3 mg/dL: 75% of usual dosage.<br>If bilirubin 3.1-5 mg/dL: 50% of usual dosage.<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL: 50% of usual dosage.<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL: Do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>L-Asparaginase: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Do not use in patients with severe hepatic impairment.</li><li><strong>Methotrexate</strong>:<br>If bilirubin 3.1-5 mg/dL or SGOT &gt; 180 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 5 mg/dL: Do not use</li><li><strong>6-Mercaptopurine:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL: 75% of usual dosage.<br>If bilirubin &gt; 3 mg/dL: 50% of usual dosage.</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL: 50% of usual dosage.<br>If bilirubin 3.1-5 mg/dL: 25% of usual dosage.<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>6-Thioguanine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min: 75% of usual dosage.<br>If CrCl &lt; 10 mL/min: 50% of usual dosage.</li><li><strong>Daunorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>L-Asparaginase:</strong><br>No dose reduction</li><li><strong>Methotrexate</strong>:<br>If CrCl 30-60 mL/min: 50% of normal dosage.<br>If CrCl &lt; 30 mL/min: Do not use</li><li><strong>6-Mercaptopurine:</strong><br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>6-Thioguanine:</strong><br>No dose reduction</li></ul>', 'High', '<b>CALGB 8811</b>', 'Larson RA, Dodge RK, Burns CP, et al. A five-drug remission induction regimen with intensive consolidation for adults with acute lymphoblastic leukemia: Cancer and Leukemia Group B study 8811. $Blood. 1995;85(8):2025-2037.$', null, '<p><strong>INDUCTION</strong> (COURSE 1 to 4 WEEKS)<br><strong>Cyclophosphamide:</strong> 1,200 mg/m2 IV, day1 (800 mg/m2 for patients &gt;=60 years)<br><strong>Daunorubicin:</strong> 45 mg/m2 IV, days 1-3 (30 mg/m2 for patients&gt;=60 years)<br><strong>Vincristine:</strong> 2 mg IV, days 1, 8, 15, 22<br><strong>Prednisone:</strong> 60 mg/m2/d PO, days 1-21 (days 1-7 for patients &gt;=60 years)<br><strong>Asparaginase:</strong> 6,000 units/m2 SQ, days 5, 8, 11, 15, 18, 22<br>EARLY INTENSIFICATION (COURSE 2 to 4 WEEKS, REPEAT ONCE)<br><strong>Methotrexate:</strong> 15 mg IT, day 1<br><strong>Cyclophosphamide:</strong> 1,000 mg/m2 IV, day 1<br><strong>Mercaptopurine:</strong> 60 mg/m2/d PO, days 1-14<br><strong>Cytarabine:</strong> 75 mg/m2/d SQ, days 1-4, 8-11<br><strong>Vincristine:</strong> 2 mg IV, days 15, 22<br><strong>Asparaginase:</strong> 6,000 units/m2 SQ, days 15, 18, 22, 25<br>CNS PROPHYLAXIS AND INTERIM MAINTENANCE (COURSE 3 to 12 WEEKS)<br><strong>Cranial RT:</strong> 2,400 cGy, days 1-12<br><strong>Methotrexate:</strong> 15 mg IT, days 1, 8, 15, 22, 29<br><strong>Mercaptopurine:</strong> 60 mg/m2/d PO, days 1-70<br><strong>Methotrexate:</strong> 20 mg/m2 PO, days 36, 43, 50, 57, 64<br>LATE INTENSIFICATION (COURSE 4 to 8 WEEKS)<br><strong>Doxorubicin:</strong> 30 mg/m2 IV, days 1, 8, 15<br><strong>Vincristine:</strong> 2 mg IV, days 1, 8, 15<br><strong>Dexamethasone:</strong> 10 mg/m2/d PO, days 1-14<br><strong>Cyclophosphamide:</strong> 1,000 mg/m2 IV, day 29<br><strong>Thioguanine:</strong> 60 mg/m2/d PO, days 29-42<br><strong>Cytarabine:</strong> 75 mg/m2/d SQ, days 29-32, 36-39<br>MAINTENANCE &nbsp;(FOR 2 YEARS FROM DIAGNOSIS)<br><strong>Vincristine:</strong> 2 mg IV, day 1 of every 4 weeks<br><strong>Prednisone:</strong> 60 mg/m2/d PO, days 1-5 of every 4 weeks<br><strong>Methotrexate:</strong> 20 mg/m2 PO, days 1, 8, 15, 22<br><strong>Mercaptopurine:</strong> 60 mg/m2/d PO, days 1-28</p>', null);
