insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (299, 'Carboplatin : Paraplatin<br>Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'Carbo/VP-16/Vincristine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin:</strong><br>No dose reduction</li><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin:</strong><br>Dosing based on AUC</li><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', '<b>Carbo/VP-16/Vincristine</b>', 'Taylor RE, Bailey CC, Robinson K, et al. Results of a randomized study of preradiation chemotherapy versus radiotherapy alone for nonmetastatic medulloblastoma: the International Society of Paediatric Oncology/United Kingdom Children''s Cancer Study Group PNET-3 study. $J Clin Oncol. 2003;21(8):1581-1591.$', null, '<p>CYCLES 1 AND 3 (3-WEEK CYCLES)<br><strong>Vincristine:</strong> 1.5 mg/m2 IV, days 1, 7, 14<br><strong>Etoposide:</strong> 100 mg/m2 IV on days 1-3<br><strong>Carboplatin:</strong> 500 mg/m2 IV days 1, 2<br>CYCLES 2 AND 4 (3-WEEK CYCLES)<br><strong>Vincristine:</strong> 1.5 mg/m2 IV days 1, 7, 14<br><strong>Etoposide: </strong>100 mg/m2 IV days 1-3<br><strong>Cyclophosphamide:</strong> 1,500 mg/m2 IV, day 1<br><strong>Mesna: </strong>750 mg/m2 IV 15 minutes before and 4 and 8 hours after cyclophosphamide</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (260, '', 'ATRA', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>All-trans-retinoic acid: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Dose reduction not provided by the manufacturer because of insufficient data</li></ul>', 'Low', '<b>ATRA</b>', 'Degos L, Dombret H, Chomienne C, et al. All-trans-retinoic acid as a differentiating agent in the treatment of acute promyelocytic leukemia. $Blood. 1995;85(10):2643-2653.$', null, '<p><strong>All-trans-retinoic acid</strong> (ATRA, tretinoin): 45 mg/m2 PO daily in 1-2 divided doses.</p><p>Continue treatment until documentation of complete remission (CR). Discontinue treatment 30 days after CR or after 90 days, whichever occurs first</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (389, 'Avelumab (Bavencio) ', 'Avelumab Monotherapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Avelumab (Bavencio):</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Avelumab (Bavencio):</strong><br>No dose reduction</li></ul>', 'Low', '', 'Phase 1: Apolo AB, Infante JR, Balmanoukian A, Patel MR, Wang D, Kelly K, Mega AE, Britten CD, Ravaud A, Mita AC, Safran H, Stinchcombe TE, Srdanov M, Gelb AB, Schlichting M, Chin K, Gulley JL. Avelumab, an anti-programmed death-ligand 1 antibody, in patients with refractory metastatic urothelial carcinoma: Results from a multicenter, phase Ib study. J Clin Oncol. 2017 Jul 1;35(19):2117-2124. Epub 2017 Apr 4.', 'METASTATIC', '<h4><strong>Avelumab (Bavencio):</strong>&nbsp;10 mg/kg IV over 60 minutes once on day 1, every 2 weeks.</h4><p>Continue treatment until disease progression or unacceptable toxicity.</p>', 100);
