insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (529, 'Bleomycin: Blenoxane<br>Ifosfamide: Ifex<br>Mesna: Mesnex<br>Cisplatin: CDDP', 'BIP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin</strong>:<br>No dose reduction</li><li><strong>Ifosfamide</strong>:<br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin</strong><br>If CrCl &gt;50 mL/min: No dose reduction<br>If CrCl 40-50 mL/min: 70% of normal dose<br>If CrCl 30-40 mL/min: 60% of normal dose<br>If CrCl 20-30 mL/min: 55% normal dose<br>If CrCl 10-20 mL/min: 45% normal dose<br>If CrCl 5-10 mL/min: 40% normal dose</li><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% normal dose.</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li></ul>', 'High', '', 'Buxton EJ, Meanwell CA, et al. Combination bleomycin, ifosfamide, and cisplatin chemotherapy in cervical cancer. J Natl Cancer Inst. 1989;81(5):359-61.', '', '<p><strong>Bleomycin:</strong>&nbsp;30 U IV over 24 hours on day 1<br><strong>Ifosfamide:</strong>&nbsp;5,000 mg/m2&nbsp;IV over 24 hours on day 2<br><strong>Mesna:</strong>&nbsp;6,000 mg/m2&nbsp;IV over 36 hours on day 2<br><strong>Cisplatin:</strong>&nbsp;50 mg/m2&nbsp;IV on day 2</p><p>Repeat cycle every 21 days.</p>', 18);
