insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (250, 'Cyclophosphamide : Cytoxan<br>Etoposide : VP-16<br>Prednisone : various<br>Vincristine : Oncovin, VCR', 'EPOCH Dose-adjusted ', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin</strong>:<br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul>', 'Moderate', '<b>EPOCH Dose-adjusted </b>', 'Little RF, Pittaluga S, Grant N, et al. Highly effective treatment of acquired immunodeficiency syndrome-related lymphoma with dose-adjusted EPOCH: impact of antiretroviral therapy suspension and tumor biology. $BLOOD.2003;101(12):4653-4659.$', null, '<p><strong>Etoposide:</strong> 50 mg/m2/d CIVI, days 1-4 (96 hours)<br><strong>Doxorubicin:</strong> 10 mg/m2/d CIVI, days 1-4 (96 hours)<br><strong>Vincristine:</strong> 0.4 mg/m2/d /CIVI, days 1-4 (96 hours)<br><strong>Cyclophosphamide</strong>&nbsp;<br><strong>Cycle1:</strong>&nbsp;<br>375 mg/m2 IV, day 5, if CD4+ cells &gt;= 100/mm3; 187 mg/m2 IV, day 5, if CD4+ cells&lt;100/mm3<br>After cycle 1: increase dose by 187 mg if nadir ANC &gt; 500/mvL or platelets &lt;25,000/mcL<br><strong>Prednisone:</strong> 60 mg/m2/d PO, days 1-5<br><strong>Filgrastim:</strong> 5 mcg/kg/d SQ, day 6, and until ANC &gt;5,000/mcl<br>Repeat every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (234, 'Bendamustine : Treanda', 'Bendamustine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bendamustine:</strong><br>If SGOT or SGPT 2.5-10 upper normal limit and bilirubin&nbsp;1.5-3 upper normal limit, or bilirubin &gt; 3 upper normal limit, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bendamustine:</strong><br>If CrCl &lt; 40 mL/min, do not use</li></ul>', 'Moderate', '<b>Bendamustine</b>', 'Kahl BS, Bartlett NL, Leonard JP, et al. Bendamustine is effective therapy in patients with rituximab-refractory, indolent B-cell non-Hodgikin lymphoma: results from a multicenter study. $Cancer. 2010,116(1):106-114 $.', null, '<p><strong>Bendamustine:</strong> 120 mg/m2 IV days 1 and 2<br>Repeat every 21 days</p>', null);
