insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (561, 'Cisplatin: CDDP<br>Paclitaxel: Taxol', 'CP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Paclitaxel</strong>:<br>No dose reduction</li></ul>', 'High', '', 'McGuire WP, Hoskins WJ, et al. Cyclophosphamide and cisplatin compared with paclitaxel and cisplatin in patients with stage III and stage IV ovarian cancer. N Engl J Med. 1996;334(1):1-6.', '', '<p><strong>Cisplatin:</strong>&nbsp;75 mg/m2&nbsp;IV on day 2<br><strong>Paclitaxel:</strong>&nbsp;135 mg/m2&nbsp;IV over 24 hours on day 1</p><p>Repeat cycle every 21 days.</p>', 99);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (551, 'Paclitaxel: Taxol<br>Ifosfamide: Ifex', 'Paclitaxel/Ifosfamide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ifosfamide</strong>:<br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Paclitaxel</strong>:<br>No dose reduction</li><li><strong>Ifosfamide</strong>: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥10 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: &nbsp;75% dosage.</li></ul>', 'Moderate', '', 'Homesley HD, Filiaci V, et al. Phase III trial of ifosfamide with or without paclitaxel in advanced uterine carcinosarcoma: a Gynecologic Oncology Group Study. J Clin Oncol. 2007;25(5):526-31.', '', '<p><strong>Paclitaxel:</strong>&nbsp;135 mg/m2&nbsp;IV over 3 hours on day 1<br><strong>Ifosfamide:</strong>&nbsp;1600 mg/m2/day IV on days 1-3</p><p>Repeat cycle every 21 days for up to 8 cycles.&nbsp;</p><p>Mesna to be given as either IV or PO. G-CSF support at 5 μg/kg/day SC to be started on day 4.&nbsp;</p>', 98);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (222, 'Bleomycin : Blenoxane<br>Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Procarbazine : Matulane<br>Vincristine : Oncovin, VCR', 'BEACOPP (Escalated)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>No dose reduction</li><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Procarbazine:</strong><br>If transaminases 1.6 to 6 times ULN: Administer 75% of dose &nbsp; &nbsp; &nbsp; &nbsp; If transaminases &gt;6 times ULN: Use clinical judgment &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If serum bilirubin &gt;5 mg/dL or transaminases &gt;3 times ULN: Avoid use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Procarbazine:</strong><br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li></ul>', 'High', '<b>BEACOPP (Escalated)</b>', 'Diehl V, Franklin J, Pfreundschuh M, et al. Standard and increased-dose BEACOPP chemotherapy compared with COPP-ABVD for advanced Hodgikin''s disease. $N Engl J Med. 2003;348(24):2386-2395$.', null, '<p><strong>Bleomycin:</strong> 10 mg/m2 IV, day8<br><strong>Etoposide:</strong> 200 mg/m2 IV, days 1-3<br><strong>Doxorubicin:</strong> 35 mg/m2 IV, day 1<br><strong>Cyclophosphamide:</strong> 1,200 mg/m2 IV, day 1<br><strong>Vincristine:</strong> 1.4 mg/m2 IV, day 8<br><strong>Procarbazine:</strong> 100 mg/m2 PO, days 1-7<br><strong>Prednisone:</strong> 40 mg/m2 PO, days 1-14<br>Repeat cycle every 21 days. G-CSF support, at dose of 5 µg/kg/day SC, starting on day 8 and continue until neutrophil recovery.</p>', null);
