insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (223, 'Bleomycin : Blenoxane<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Prednisone : various<br>Vinblastine : Velban<br>Vincristine : Oncovin, VCR', 'Stanford V', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Mechlorethamine:</strong><br>No dose reduction</li><li><strong>Vinblastine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Mechlorethamine:</strong><br>No dose reduction</li><li><strong>Vinblastine:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Prednisone: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>Dosage adjustment not provided by the manufacturer.</li></ul>', 'High', '<b>Stanford V</b>', 'Bartlett NL, Rosenberg SA, Hoppe RT, Hancock SL, Horning SJ.Brief chemotherapy, Stanford V, and adjuvant radiotherapy for bulky or advanced-stage Hodgikin''s disease: a Preliminary report. $J Clin Oncol. 1995; 13(5):1080-1088$.', null, '<p><strong>Doxorubicin:</strong> 25 mg/m2 IV, days 1 and 15<br><strong>Vinblastine:</strong> 6 mg/m2 IV, days 1 and 15<br><strong>Mechlorethamine:</strong> 6 mg/m2 IV, day1<br><strong>Vincristine:</strong> 1.4 mg/m2 IV, days 8 and 22<br><strong>Bleomycin:</strong> 5 U/m2 IV, days 8 and 22<br><strong>Etoposide:</strong> 60mg/m2 IV, days 15 and 16<br><strong>Prednisone:</strong> 40 mg PO every other day</p><p>Repeat cycle every 28 days. In patients &gt;50 years of age, the vinblastine dose<br>&nbsp;must be reduced to 4 mg/m2 and the vincristine dose must be reduced to 1 mg/m2 on weeks 9 and 12. The dose of prednisone should be tapered starting from week 10. The patient should be placed on prophylactic trimethoprim/sulfamethoxazole (Bactrim DS) PO bid and acyclovir 200 mg PO tid.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (235, 'Bexarotene : Targretin', 'Bexarotene', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bexarotene: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bexarotene: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul>', 'Low', '<b>Bexarotene</b>', 'Targretin [package insert]. $Woodcliff Lake, NJ: Eisai Inc.; 2011.$', null, '<p><strong>Bexarotene:</strong> 300 mg/m2/day PO with a meal</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (563, 'Carboplatin: Paraplatin<br>Paclitaxel: Taxol<br>Bevacizumab: Avastin', 'Carboplatin/Paclitaxel/Bevacizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bevacizumab</strong><br>No dose reduction</li><li><strong>Carboplatin</strong><br>No dose reduction</li><li><strong>Paclitaxel</strong><br>Transaminases &lt;10 x ULN and bilirubin ≤1.25 x ULN: 175 mg/m2<br>Transaminases &lt;10 x ULN and bilirubin 1.26 to 2 x ULN: 135 mg/m2<br>Transaminases &lt;10 x ULN and bilirubin 2.01 to 5 x ULN: 90 mg/m2<br>Transaminases ≥10 x ULN or bilirubin &gt;5 x ULN: Use is not recommended</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bevacizumab</strong><br>No dose reduction</li><li><strong>Carboplatin</strong>:<br>Dosing based on <strong>mg/m2</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% of the usual dose. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% of the usual dose.</li><li><strong>Paclitaxel</strong><br>No dose reduction</li></ul>', 'High', '', 'Burger RA, et al. J Clin Oncol 2010:28:946S (LBA1).', '', '<p><strong>Carboplatin:</strong>&nbsp;AUC of 6, IV on day 1<br><strong>Paclitaxel:</strong>&nbsp;175 mg/m2&nbsp;IV over 3 hours on day 1<br><strong>Bevacizumab:</strong>&nbsp;15 mg/kg IV on day 1</p><p>Repeat cycle every 21 days for 6 cycles, then maintenance bevacizumab for cycles 7-22.</p>', 99);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (883, null, 'Sunitinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Sunitinib:</strong><br>If Child-Pugh A or B, no dose reduction&nbsp;<br>If Child-Pugh C, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Sunitinib:</strong><br>If CrCl &gt; 42 mL/min, no dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Sunitinib:</strong> 50 mg PO daily for 28 days&nbsp;<br>Repeat cycle every 6 weeks.</p>', null);
