insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (585, 'Bleomycin: Blenoxane<br>Etoposide: VP-16<br>Cisplatin: CDDP', 'BEP (germ cell)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin</strong>:<br>No dose reduction</li><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Etoposide</strong>:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin</strong><br>If CrCl &gt;50 mL/min: No dose reduction<br>If CrCl 40-50 mL/min: 70% of normal dose<br>If CrCl 30-40 mL/min: 60% of normal dose<br>If CrCl 20-30 mL/min: 55% normal dose<br>If CrCl 10-20 mL/min: 45% normal dose<br>If CrCl 5-10 mL/min: 40% normal dose</li><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Etoposide</strong>:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'High', '', 'Dimopoulos MA, Papadimitriou C, et al. Treatment of ovarian germ cell tumors with a 3-day bleomycin, etoposide, and cisplatin regimen: a prospective multicenter study. Gynecol Oncol. 2004;95(3):695-700.', '', '<p><strong>Bleomycin:</strong>&nbsp;30 U IV on days 2, 9, and 16<br><strong>Etoposide:</strong>&nbsp;100 mg/m2&nbsp;IV on days 1-5<br><strong>Cisplatin:</strong>&nbsp;20 mg/m2&nbsp;IV on days 1-5</p><p>Repeat cycle every 21 days.</p>', 99);
