insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (583, 'Rucaparib: Rubraca', 'Rucaparib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Rucaparib:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Rucaparib:</strong><br>No dose reduction</li></ul>', 'High', '', 'Swisher EM, Lin KK, et al. Rucaparib in relapsed, platinum-sensitive high-grade ovarian carcinoma (ARIEL2 Part 1): an international, multicentre, open-label, phase 2 trial. Lancet Oncol. 2017;18(1):75-87.', '', '<p><strong>Rucaparib:</strong>&nbsp;600 mg PO daily</p><p>Repeat cycle every 28 days.</p>', 99);
