insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (971, null, 'ITP', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Ifosfamide</strong>:<br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use<br>&nbsp;</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug.</li><li><strong>Ifosfamide</strong>: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥10 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: &nbsp;75% dosage.</li><li><strong>Paclitaxel</strong>:<br>No dose reduction</li></ul>', 'High', null, null, null, '<p><strong>Ifosfamide: </strong>1,500 mg/m2 IV on days 1-3<br><strong>Paclitaxel:</strong> 200 mg/m2 IV over 3 hours on day 1<br><strong>Cisplatin:</strong> 70 mg/m2 IV on day 1<br>Repeat cycle every 21 days. G-CSF support is recommended. Regimen can<br>also, be administered every 28 days.</p>', null);
