insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (675, '', 'ECF', '<p><strong>Hepatic Impairment:</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use<br>No dose reduction for infusional administration</li><li><strong>Epirubicin</strong>:<br>If bilirubin 1.2-3 mg/dL or SGOT 2 to 4 times upper limit normal: reduce dose by 50%<br>If bilirubin &gt; 3mg/dL or SGOT &gt; 4 times upper limit normal: reduce dose by 75%</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>No dose reduction</li><li><strong>Epirubicin</strong><br>If serum Cr &gt;&nbsp;5 mg/dL, reduce dose by 50%</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nb