insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (683, '', 'FLO', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: 75% dosage</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>5-Fluorouracil</strong>:<br>No dose reduction.</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>5-Fluorouracil:&nbsp;</strong>2,600 mg/m<sup>2</sup>&nbsp;IV on day 1 as a continuous infusion over 24 hours</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (764, null, 'Cladribine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cladribine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cladribine:</strong><br>If CrCl 10-50 mL/min: Dose reduction by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;10 mL/min: Dose reduction by 50%</li></ul>', 'Low', null, null, null, '<p><strong>Cladribine:&nbsp;</strong>0.09 mg/kg/day IV as continuous infusion on days 1-7&nbsp;</p><p>Administer one cycle.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (247, 'Cyclophosphamide : Cytoxan<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16', 'CDE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li></ul>', 'Moderate', '<b>CDE</b>', 'Sparano JA,Lee S, Chen MG, et al. Phase II trial of   infusional, cyclophosphamide, doxorubicin, and etoposide in patients with HIV-associated non-Hodgkins lymphoma: an Eastern Cooperative Oncology Group Trial (E1491). $J Clin Oncol. 2004; 22(8): 1491-1500.$', null, '<p><strong>Cyclophosphamide:</strong> 200mg/m2/day CIVI for 4 days<br><strong>Doxorubicin:</strong> 12.5 mg/m2/day CIVI for 4 days<br><strong>Etoposide:</strong> 60 mg/m2/day CIVI for 4 days<br>Repeat every 28 days for a maximum of 8 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (746, null, 'Afatinib', '<p><strong>RENAL DOSE ADJUSTMENT</strong></p><p>eGFR &gt;30 mL/minute/1.73 m2: No dosage adjustment is necessary.</p><p>eGFR 15 to-29 mL/minute/1.73 m2: Starting dose reduced to 30 mg once daily.</p><p>eGFR &lt;15 mL/minute/1.73 m2 and hemodialysis: has not been studied</p><p><strong>HEPATIC</strong> <strong>DOSE ADJUSTMENT</strong></p><p><strong>Preexisting mild to moderate impairment (Child-Pugh class A/B): </strong>No dosage adjustment is necessary.</p><p><strong>Preexisting severe impairment (Child-Pugh class C): </strong>has not been studied; closely monitor and adjust dose if necessary.</p><p><strong>Hepatotoxicity during treatment:</strong>&nbsp;</p><ul><li>Withhold therapy for ≥ grade 3 hepatic dysfunction.&nbsp;<ul><li>If improvement to baseline or ≤ grade 1, resume therapy at 10 mg per day less than previous dose.&nbsp;</li><li>Permanently discontinue for severe hepatic impairment.</li></ul></li></ul>', 'Low', null, null, null, '<p>40 mg once daily&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (747, null, 'Afatinib', '<p><strong>Renal</strong>:&nbsp;</p><ul><li>Severe renal impairment (eGFR 15 to-29 mL/min /1.73 m2: Reduce starting dose to 30 mg once daily. (i.e., 10 mg per day less than the dose at which the adverse reaction occurred.)</li><li>eGFR &lt;15 mL/minute/1.73 m2 and hemodialysis: Has not been studied</li></ul><p><strong>Hepatic</strong>:</p><ul><li><strong>Preexisting mild to moderate impairment (Child-Pugh class A/B): </strong>No dosage adjustment is necessary.</li><li><strong>Preexisting severe impairment (Child-Pugh class C): </strong>Has not been studied. Close monitoring</li><li><strong>Hepatotoxicity during treatment:</strong> Withhold treatment</li></ul>', 'Low', null, null, null, '<p>30 mg daily, indefinite duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (757, null, 'Ceritinib', '<p><strong>Renal Impairment</strong></p><p>Mild-moderate renal impairment: no dosage adjustment necessary (renal excretion is low)</p><p>Severe renal impairment: has not been studied</p><p><strong>Hepatic Impairment</strong></p><p>Mild hepatic impairment (total bilirubin &lt;ULN and AST &gt; ULN, or total bilirubin &gt; 1.0-1.5x ULN and any AST):</p><p>Severe hepatic impairment: not yet been determined&nbsp;</p>', 'Moderate-High', null, null, null, '<p>450mg once per day by mouth (with food) for indefinite duration</p><p>OR</p><p>750mg once per day by mouth (without food)&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (758, null, 'Crizotinib', '<p>&nbsp;Renal Impairment</p><p>Hepatic Impairment</p>', 'Moderate-High', null, null, null, '<p>250 mg daily by mouth for indefinite duration</p><p><i>(for severe hepatic or renal impairment, see below)</i></p><p>OR</p><p>200 mg twice per day by mouth for indefinite duration</p><p><i>(for moderate hepatic impairment, see below)</i></p><p><i>OR</i></p><p>250 mg twice per day by mouth for indefinite duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (750, null, 'Gefitinib', '<p><strong>Renal</strong>:</p><ul><li>CrCl&gt;20mL/min: No dosage adjustments provided in the manufacturer’s labeling ( &lt;4% renal excretion)&nbsp;</li><li>CrCl &nbsp;≤20 mL/minute: Has not been studied</li></ul><p><strong>Hepatic</strong></p><ul><li>Grade 2 or higher hepatic impairment: Withhold treatment for up to 14 days; may resume treatment when fully resolved or improved to grade 1 hepatic impairment.</li><li>Severe hepatic impairment: Permanently discontinue.</li></ul>', 'Low', null, null, null, '<p>250mg PO daily, &nbsp;24 month duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (751, null, 'Osimertinib', '<p><strong>Renal</strong></p><ul><li>CrCl 15 to 89 mL/minute: No dosage adjustment necessary.</li><li>CrCl ≤15 mL/minute and ESRD: Has not been studied</li></ul><p><strong>Hepatic</strong></p><ul><li>Mild to moderate impairment : No dosage adjustment necessary<ul><li>Child-Pugh class A /B, OR</li><li>Total bilirubin ≤ ULN and AST &gt; ULN, OR&nbsp;</li><li>Total bilirubin 1 - 3 x ULN and any AST</li></ul></li><li>Severe impairment: Has not been studied<ul><li>Total bilirubin 3 - 10 x ULN and any AST</li></ul></li></ul>', 'Low', null, null, null, '<p><strong>Osimertinib: 80 mg PO daily, indefinite duration</strong></p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (749, null, 'Dacomitinib', '<p>Renal:</p><ul><li>Mild-moderate renal impairment (CrCl 30 to 89 mL/minute): No dosage adjustment is necessary.</li><li>Severe renal impairment (CrCl &lt;30 mL/minute): Has not been studied.</li></ul><p>Hepatic:</p><ul><li>Mild: No dosage adjustment is necessary.<ul><li>AST &gt; ULN and total bilirubin within normal limits, OR</li><li>Total bilirubin 1 - 1.5 x ULN</li></ul></li><li>Moderate: No dosage adjustment is necessary.<ul><li>Total bilirubin 1.5 -3x ULN</li></ul></li><li>Severe: has not been studied<ul><li>Total bilirubin 3 -10 x ULN</li></ul></li></ul>', 'Low', null, null, null, '<p>40 mg daily, indefinite duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (745, '', '5-Fluorouracil + Radiation Therapy (German AIO regimen)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>No dose reduction.</li></ul>', 'Low', '', '', 'NEOADJUVANT', '<p><strong>5-Fluorouracil:&nbsp;</strong>1,000 mg/m2/day IV continuous infusion on days 1-5</p><p>Repeat infusional 5-FU on weeks 1 and 5.</p><p><strong>Radiation Therapy:&nbsp;</strong>180 cGy/day for 5 days per week (total dose, 5,040 cGy)</p><p>Followed by surgical resection and adjuvant chemotherapy with 5-FU at 500 mg/m2 IV for 5 days every 28 days for a total of 4 cycles.</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (748, null, 'Erlotinib', '<p><strong>Renal</strong></p><p><i>Renal impairment at treatment initiation:</i> There are no dosage adjustments provided in the manufacturer’s labeling (has not been studied), although &lt;9% of a single dose is excreted in the urine.</p><p><i>Renal toxicity during treatment:</i></p><ul><li>Renal toxicity, hepatorenal syndrome, dehydration: Withhold treatment</li></ul><p><strong>Hepatic</strong></p><p><i>Hepatic impairment at treatment initiation:</i></p><ul><li>Total bilirubin &gt; ULN or Child-Pugh classes A, B, and C: &nbsp;No dosage adjustments provided; use with caution and monitor closely during treatment.</li><li>Total bilirubin &gt;3 times ULN: Use with extra caution.</li></ul><p><br><i>Hepatic impairment during treatment:</i></p><ul><li>Withhold treatment if severe changes in bilirubin or transaminases</li></ul>', 'Low', null, null, null, '<p>Erlotinib 150 mg daily, indefinite duration</p><p>Erlotinib 25 mg daily, indefinite duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (752, null, 'Erlotinib & Ramucirumab', '<p><strong>Renal</strong></p><p><i>Renal impairment at treatment initiation:</i> There are no dosage adjustments provided in the manufacturer’s labeling (has not been studied), although &lt;9% of a single dose is excreted in the urine.</p><p><i>Renal toxicity during treatment:</i></p><ul><li>Renal toxicity, hepatorenal syndrome, dehydration: Withhold treatment</li></ul><p><strong>Hepatic</strong></p><p><i>Hepatic impairment at treatment initiation:</i></p><ul><li>Total bilirubin &gt; ULN or Child-Pugh classes A, B, and C: &nbsp;No dosage adjustments provided; use with caution and monitor closely during treatment.</li><li>Total bilirubin &gt;3 times ULN: Use with extra caution.</li></ul><p><br><i>Hepatic impairment during treatment:</i></p><ul><li>Withhold treatment if severe changes in bilirubin or transaminases</li></ul><p><i><strong>Ramucirumab</strong></i></p><p><strong>Renal</strong></p><ul><li>No dose adjustments provided by manufacturer per studies</li></ul><p><strong>Hepatic</strong></p><ul><li>Use with caution in Child-Pugh class B or C cirrhosis. Monitor for &nbsp;encephalopathy, ascites, or hepatorenal syndrome.<br>&nbsp;</li></ul>', 'Low', null, null, null, '<ul><li>Erlotinib: 150 mg PO daily</li><li>Ramucirumab: 10 mg/kg IV on day 1<ul><li>14 day cycles</li></ul></li></ul>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (755, null, 'Alecitinib', '<p><strong>Renal Impairment</strong></p><ul><li>Prior to initiation:<ul><li>Mild-moderate renal impairment: No dose adjustment provided</li><li>Severe renal impairment (CrCl&lt;30) or ESRD: Not yet studied</li></ul></li><li>During treatment:<ul><li>Grade 3 renal impairment: temporarily withhold until sCr &lt; 1.5, then resume at reduced dose</li><li>Grade 4 renal impairment: permanently discontinue</li></ul></li></ul><p><strong>Hepatic Impairment</strong></p><ul><li>Prior to initiation:<ul><li>Mild Hepatic Impairment(Total bilirubin &lt; ULN and AST &gt; ULN, or total bilirubin &gt; 1.0-1.5x ULN): No dose adjustment recommended&nbsp;</li><li>Moderate to severe hepatic impairment: Not yet studied</li></ul></li></ul>', 'Low', null, null, null, '<p>300mg twice a day by mouth, indefinite duration<br>OR</p><p>600mg twice a day by mouth, indefinite duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (279, '', 'Interferon alfa-2a', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Interferon alfa-2a:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Interferon alfa-2a:</strong><br>No dose reduction</li></ul>', 'Low', '<b>Interferon alfa-2a</b>', 'Ratain MJ, et al. $Blood 1985;65:644-648.$', null, '<p><strong>Interferon alfa-2a:</strong> 3 million IU SC or IM, 3 times per week<br>Continue treatment for up to 1 to 1.5 years.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (754, null, 'R - CVP', '<b>Hepatic Impairment</b><ul><li>Rituximab:<br>No dose reduction</li><li>Vincristine:<br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br> If bilirubin > 3 mg/dL or SGOT > 180 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Vincristine:<br>No dose reduction </li></ul>', 'Moderate', null, null, null, '<b>Cyclophosphamide</b> 750 mg/m<sup>2</sup> IV, day 1<br> <b>Vincristine</b> 1.4 mg/m<sup>2</sup> up to a maximum dose of 2 mg IV, day 1<br> <b>Prednisone</b> 40 mg PO, days 1-5<br> <b>Rituximab</b> 375 mg/m<sup>2</sup> IV, day 1<br> Repeat every 21 days for a maximum off 8 cycles ', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (756, null, 'Brigatinib', '<p><strong>Renal Impairment</strong></p><p>Mild-moderate renal impairment (CrCl 30-89 mL/min): No dose adjustment is recommended&nbsp;</p><p>Severe renal impairment (CrCl 15-29 mL/min): Reduce daily dose by approximately 50%&nbsp;</p><p><strong>Hepatic impairment</strong></p><p>Mild-moderate hepatic impairment (Child Pugh A/B): No dose adjustment is recommended</p><p>Severe hepatic impairment (Child-Pugh C): Reduce daily dose by approximately 40%&nbsp;</p>', 'Low', null, null, null, '<p>90mg once per day by mouth for 1 week duration</p><p>Followed by</p><p>180mg once per day for indefinite duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (760, null, 'Lorlatinib', '<p><strong>Renal Impairment</strong></p><p>Mild/moderate renal impairment (CrCl 30-89 mL/min): No dose adjustment is recommended.&nbsp;</p><p>Severe renal impairment: The recommended dose has not been established&nbsp;</p><p><strong>Hepatic Impairment</strong></p><p>Mild hepatic impairment (Total bilirubin &lt; ULN with AST &gt; ULN or total bilirubin 1-1.5 x ULN with any AST) No dose adjustment is recommended&nbsp;</p><p>Moderate/severe hepatic impairment: The recommended dose has not been established&nbsp;</p>', 'Low', null, null, null, '<p>100mg once daily, indefinite duration</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (761, null, 'Bosutinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bosutinib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bosutinib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>For first-line treatment: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &gt;50 ml/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 30 to 50 ml/min: Reduce dose to 300 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 ml/min<i>:</i> Reduce dose to 200 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; For resistant treatment: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &gt;50 ml/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 30 to 50 ml/min: Reduce dose to 400 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 ml/min<i>:</i> Reduce dose to 300 mg once daily.&nbsp;</li></ul>', 'Moderate', null, null, null, '<p><strong>First-line treatment:</strong></p><p><strong>Bosutinib:&nbsp;</strong>400 mg PO daily</p><p>Continue until disease progression or unacceptable toxicity</p><p><strong>Resistant:</strong></p><p><strong>Bosutinib:&nbsp;</strong>500 mg PO daily</p><p>Continue until disease progression or unacceptable toxicity</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (762, null, 'Ponatinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ponatinib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>Mild-to-severe impairment (Child-Pugh class A, B, or C): Reduce initial dose to 30 mg once daily.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ponatinib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Ponatinib:&nbsp;</strong>45 mg PO bid</p><p>Continue until loss of response or unacceptable toxicity</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (921, null, 'FCR', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: Reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Fludarabine:</strong><br>No dose reduction</li><li><strong>Rituximab:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-29 mL/min: Reduce dose by 25%<br>If CrCl &lt; 10 mL/min: Reduce dose by 50%</li><li><strong>Fludarabine:</strong><br>If CrCl 10-50 mL/min: Reduce dose by 25%&nbsp;<br>If CrCl &lt;10 mL/min: Reduce dose by 50%</li><li><strong>Rituximab:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Fludarabine:</strong> 25 mg/m2 IV on days 1-3<br><strong>Cyclophosphamide:</strong> 300 mg/m2 IV on days 1-3<br><strong>Rituximab:</strong> 375 mg/m2 IV on day 1<br>Repeat every 21 days for a total of 4 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (923, null, 'R-FCM', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: Reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Fludarabine:</strong><br>No dose reduction</li><li><strong>Rituximab:</strong><br>No dose reduction</li><li><strong>Mitoxantrone:</strong><br>If bilirubin &gt; 3 mg/dL, reduce dose by 25%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-29 mL/min: Reduce dose by 25%<br>If CrCl &lt; 10 mL/min: Reduce dose by 50%</li><li><strong>Fludarabine:</strong><br>If CrCl 10-50 mL/min: Reduce dose by 25%&nbsp;<br>If CrCl &lt;10 mL/min: Reduce dose by 50%</li><li><strong>Rituximab:</strong><br>No dose reduction</li><li><strong>Mitoxantrone:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Rituximab:</strong> 375 mg/m2 IV on day 0<br><strong>Fludarabine: </strong>25 mg/m2 IV on days 1-3<br><strong>Cyclophosphamide:</strong> 200 mg/m2 IV on days 1-3<br><strong>Mitoxantrone:</strong> 8 mg/m2 IV on day 1</p><p><br>Repeat every 4 weeks for a total of 4 cycles. Patients who experience a response can then receive maintenance therapy at 3 and 9 months after completion of therapy with<br><strong>Rituximab: </strong>375 mg/m2 IV weekly for 4 weeks</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1041, null, 'ABV', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Bleomycin</strong>:<br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Doxorubicin</strong>:<br>No dose reduction</li><li><strong>Bleomycin</strong><br>If CrCl &gt;50 mL/min: No dose reduction<br>If CrCl 40-50 mL/min: 70% of normal dose<br>If CrCl 30-40 mL/min: 60% of normal dose<br>If CrCl 20-30 mL/min: 55% normal dose<br>If CrCl 10-20 mL/min: 45% normal dose<br>If CrCl 5-10 mL/min: 40% normal dose</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'Moderate', null, null, null, '<p><strong>Doxorubicin: </strong>40 mg/m2 IV on day1<br><strong>Bleomycin:</strong> 15 U/m2 IV on days 1 and 15<br><strong>Vinblastine:</strong> 6 mg/m2 IV on day 1<br>Repeat cycle every 28 days&nbsp;</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (285, 'Dexamethasone : various<br>Lenalidomide : Revlimid', 'Lenalidomide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Lenalidomide:</strong><br>If CrCl &gt;60 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 30 to 60 mL/min: 5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min not requiring dialysis: 2.5 mg once daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min requiring dialysis: 2.5 mg once daily (administer after dialysis on dialysis days).</li></ul>', 'Low', '<b>Lenalidomide/Dexamethasone</b>', 'Dimopoulos M, Spencer A, Attal M, et al. Lenalidomide plus dexamethasone for relapsed or refractory multiple myeloma. $N Engl J Med. 2007;357(21):2123-2132.$', null, '<p><strong>Lenalidomide:</strong> 10 mg PO daily</p><p>Continue until disease progression.</p><p><strong>OR</strong><br><strong>Lenalidomide:</strong> 10 mg PO daily for 21 days.<br>Repeat every 28 days until disease progression</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (13, 'Cetuximab : Erbitux', 'Cetuximab', '<p><strong>Hepatic Impairment</strong></p><p>No dose adjustment necessary</p><p><strong>Renal Impairment</strong></p><p>No dose adjustment necessary</p>', 'Low', '<b>Cetuximab</b>', 'Bonner JA, Harari PM, Giralt J, et al. Radiotherapy plus cetuximab for locoregionally advanced head and neck cancer: 5-year survival datat from a phase 3 randomised trial, and relation between cetuximab-induced rash and surival. $Lancet Oncol.2010;11(1):21-28.$', null, '<b>Cetuximab</b> 400 mg/m<sup>2</sup> loading dose over 120 minutes, <b>THEN</b> 250 mg/m<sup>2</sup> over 60 minutes, every 7 days', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (8, 'Cisplatin : CDDP', 'PF', '<p><strong>Hepatic Impairment:</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use<br>No dose reduction for infusional administration</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>No dose reduction</li><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug.</li></ul>', 'High', '<b>CF</b>', 'Gibson MK, Li Y, Murphy B, et al. Randomized phase III evaluation of cisplatin plus fluorouracil versus cisplatin plus paclitaxel in advanced head and neck cancer (E1395): an intergroup trial of the Eastern Cooperative Oncology Group. $J Clin Oncol. 2005;23(15):3562-3567.$', null, '<p><strong>Cisplatin:</strong> 100 mg/m2 IV, day 1<br><strong>5-Fluorouracil:</strong> 1000 mg/m2/day IV continuous infusion, days 1-5<br>Repeat cycle every 21-28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (206, 'Cyclophosphamide : Cytoxan<br>Dactinomycin : Actinomycin<br>Doxorubicin : Adriamycin, various<br>Etoposide : VP-16<br>Ifosfamide : Ifex<br>Mesna : Mesnex, various<br>Vincristine : Oncovin, VCR', 'CAV-IE', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Dactinomycin:</strong><br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Doxorubicin:</strong><br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%&nbsp;<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Ifosfamide:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Dactinomycin:</strong><br>No dose reduction</li><li><strong>Doxorubicin:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Ifosfamide</strong>: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥10 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: Reduce dose by 25%</li></ul>', 'CAV-High: IE-Moderate', '<b>VAC-IE</b>', 'Grier HE, Krailo MD, Tarbell NJ, et al. Addition of ifosfamide and etoposide to standard chemotherapy for Ewing''s sarcoma and primitive neuroectodermal tumor of bone. $N Engl J Med. 2003;348(8):694-701.$', null, '<p><strong>Cyclophosphamide:</strong> 1,200 mg/m2 IV, day 1 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>Doxorubicin:</strong> 75 mg/m2 IV on day 1<br><strong>Vincristine:</strong> 2 mg IV, day 1<br>Alternating with each cycle<br><strong>Ifosfamide:</strong> 1,800 mg/m2 IV, days 1-5&nbsp;<br><strong>Etoposide:</strong> 100 mg/m2 IV, days 1-5<br>Repeat every 21 days for a total of 17 cycles.</p><p>When the cumulative dose of doxorubicin reaches 375 mg/m2, switch to dactinomycin at 1.25 mg/m2.</p>', null);
