insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1071, null, 'Weekly Carboplatin + Paclitaxel + Radiation Therapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Paclitaxel:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li><strong>Carboplatin</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Paclitaxel:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction</li><li><strong>Carboplatin</strong>:<br>Dosing based on <strong>mg/m2</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min:&nbsp;Reduce dose by 50%&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min:&nbsp;Reduce dose by 75%</li></ul>', 'High', null, null, null, '<p><strong>Paclitaxel: </strong>45 mg/m2 IV weekly for 7 weeks<br><strong>Carboplatin:</strong> AUC of 2, IV weekly for 7 weeks<br><strong>Radiation therapy:</strong> 200 cGy/day for a total dose of 6,300 cGy over 7 weeks<br><br>Chemotherapy is given concurrently with radiation therapy. Three to 4 weeks<br>after the completion of chemoradiotherapy, two additional cycles of the following<br>chemotherapy are given:<br><strong>Paclitaxel:</strong> 200 mg/m2 IV on day 1<br><strong>Carboplatin: </strong>AUC of 6, IV on day 1<br>Repeat cycle every 21 days for 2 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1074, null, 'Cisplatin + Pemetrexed (Adjuvant)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Pemetrexed</strong>:<br>No dose reduction</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Pemetrexed</strong>:<br>If CrCl &gt; 45 mL/min, no dose reduction<br>If CrCl &lt; 45 mL/min, do not use</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin:</strong>&nbsp;80 mg/m2&nbsp;IV once on day 1<br><strong>Pemetrexed:</strong>&nbsp;500 mg/m2&nbsp;IV once on day 1</p><p>Repeat cycle every 21 days for up to 6 cycles.</p><p>Folic acid at 350-1,000 µg PO once daily beginning 1 week prior to therapy and vitamin B12 at 1,000 µg IM beginning 1-2 weeks prior to the first dose of therapy and repeated every 9 weeks. Dexamethasone at 4 mg PO bid on the day before, the day of, and the day after pemetrexed therapy.</p>', null);
