insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1075, null, 'Gemcitabine + Cisplatin + Bevacizumab ', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Bevacizumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li></ul><p>&nbsp;</p><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Gemcitabine:</strong><br>No dose reduction</li><li><strong>Bevacizumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li></ul>', 'High', null, null, null, '<p><strong>Gemcitabine:</strong> 1,250 mg/m2 IV on days 1 and 8<br><strong>Cisplatin:</strong> 80 mg/m2 IV on day 1<br><strong>Bevacizumab:</strong> 7.5 or 15 mg/kg IV on day 1<br>Repeat cycle every 21 days for a total of 6 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (139, 'Capecitabine : Xeloda', 'Capecitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine:</strong><br>If CrCl 30-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Low', '<b>Capecitabine</b>', 'Xeloda [package insert]. $San Francisco, CA: Genentech; 2011.$', null, '<p><strong>Capecitabine:</strong> 1,250 mg/m2 PO bid, days 1-14 with 1 week rest.<br>Repeat every 21 days</p><p>Consider decreasing dose to 850-1,000 mg/m2 PO bid on days 1-14 to decrease the risk of toxicity without compromising clinical efficacy.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (451, 'Carboplatin: Paraplatin<br>Paclitaxel: Taxol', 'Carboplatin/Paclitaxel', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on AUC &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% dosage.</li><li><strong>Paclitaxel</strong>:<br>No dose reduction</li></ul>', 'High', '', 'Perez EA, Hillman DW, et al. A phase II study of paclitaxel plus carboplatin as first-line chemotherapy for women with metastatic breast carcinoma. Cancer. 2000;88(1):124-31.', 'METASTATIC', '<p><strong>Carboplatin:</strong>&nbsp;AUC of 6, IV on day 1<br><strong>Paclitaxel: </strong>200 mg/m2&nbsp;IV over 3 hours on day 1</p><p>Repeat cycle every 21 days.</p>', 15);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (119, 'Doxorubicin : Adriamycin, various<br>Paclitaxel : Taxol', 'AT - Paclitaxel', '<b>Hepatic Impairment</b><ul><li>Doxorubicin:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%  <br>                                         If bilirubin 3.1-5 mg/dL, reduce dose by 75%  <br>                                         If bilirubin >5 mg/dL, do not use </li><li>Paclitaxel:<br>For 3-hour infusion:  <br>                                                                                      If transaminase < 10 upper normal limit and bilirubin < 1.25 upper normal limit, no dose reduction (175mg/m2)    <br>                                       If transaminase < 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2  <br>                                                           If transaminase < 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2  <br>                                                             If transaminase > 10 upper normal limit or bilirubin > 5 upper normal limit, do not use    <br>                                                                             For 24-hour infusion:    <br>                                                                                  If transaminase < 2 upper normal limit or bilirubin < 1.5 mg/mL, no dose reduction (135 mg/m2)   <br>                                                                     If transaminase 2-10 upper normal limit and bilirubin < 1.5 mg/dL, reduce to 100 mg/m2 <br>                                                                                    If transaminase < 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2    <br>                                                                                   If transaminase > 10 upper normal limit or bilirubin > 7.5 mg/dL, do not use </li></ul><b>Renal Impairment</b><ul><li>Doxorubicin:<br>No dose reduction </li><li>Paclitaxel:<br>No dose reduction</li></ul>', 'Moderate', '<b>AT - Paclitaxel</b>', 'Jassem J, Pienkowski T, Pluzanska A.et al. Doxorubicin and paclitaxel versus fluorouracil, doxorubicin, and cyclophosphamide as first-line therapy for women with metastatic breast cancer: final results of a randomized phase III multicenter trial. $J Clin Oncol. 2001;19(6):1707-1715$', null, '<p><strong>Doxorubicin:</strong> 50 mg/m2 IV over, day 1 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br><strong>FOLLOWED 24 HOUR LATER BY &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong><br><strong>Paclitaxel:</strong> 220 mg/m2,IV over 3 hours ,day 2<br>Repeat every 21 days for up to 8 cycles</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1073, null, 'Cisplatin + Vinblastine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Vinblastine:</strong><br>If serum bilirubin 1.5 to 3 mg/dL or transaminases 2 to 3 times ULN: Administer 50% of dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If serum bilirubin &gt;3 times ULN: Avoid use.</li><li><strong>Cisplatin</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Vinblastine:</strong><br>No dose reduction</li><li><strong>Cisplatin</strong>:<strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: Reduce dose by 25% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li></ul>', 'High', null, null, null, '<p><strong>Cisplatin:</strong> 100 mg/m2 IV on day 1<br><strong>Vinblastine: </strong>4 mg/m2 IV on days 1,8, 15, 22, and 29, then every 2 weeks after day 43<br>Repeat cycle every 28 days for 4 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (791, null, 'EC Metastatic regimen', '<p><strong>Hepatic Impairment</strong></p><ul><li>Cyclophosphamide:<br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li>Epirubicin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin 1.2 to 3 mg/dL: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;3 to 5 mg/dL: Reduce dose by 75% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;5 mg/dL: Do not use it.</li></ul><p><strong>Renal Impairment</strong></p><ul><li>Cyclophosphamide:<br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li>Epirubicin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If creatinine &gt;5 mg/dL: Consider lower doses.</li></ul>', 'High', null, null, null, '<p><strong>Epirubicin:&nbsp;</strong>75 mg/m2&nbsp;IV on day 1</p><p><strong>Cyclophosphamide:&nbsp;</strong>600 mg/m2&nbsp;IV on day 1</p><p>Repeat every 21 days for up to 6 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (126, 'Cyclophosphamide : Cytoxan<br>Epirubicin : Ellence, various', 'EC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Epirubicin: &nbsp;&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin 1.2 to 3 mg/dL: Reduce dose by 50% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;3 to 5 mg/dL: Reduce dose by 75% &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;5 mg/dL: Do not use it.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Epirubicin: &nbsp; &nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If creatinine &gt;5 mg/dL: Consider lower doses.</li></ul>', 'High', '<b>EC</b>', 'Piccart MJ, Di Leo A, Beauduin M, et al. Phase III trial comparing two dose levels of epirubicin combined with cyclophosphamide with cyclophosphamide, methotrexate, and fluorouracil in node-positive breast cancer. $J Clin Oncol. 2001;19(12):3103-3110.$', null, '<p><strong>Epirubicin:</strong>100 mg/m2 IV, day 1<br><strong>Cyclophosphamide:</strong> 830 mg/m2 IV, day 1<br>Repeat every 21 days for up to 8 cycles</p>', null);
