insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (497, 'Abemaciclib: Verzenio<br>Fulvestrant: Faslodex', 'Abemaciclib/Fulvestrant', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Abemaciclib</strong><br>Child-Pugh C: Reduce dosing frequency to once daily</li><li><strong>Fulvestrant</strong>:<br>If SGOT, SGPT, bilirubin, and alkaline phosphatase &lt; 2 upper normal limits, no dose reduction<br>If SGOT, SGPT, bilirubin, and alkaline phosphatase &gt; 2 upper normal limits, unknown</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Fulvestrant</strong>:<br>No dose reduction</li><li><strong>Abemaciclib:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≥30 mL/min: 100 % dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: &nbsp;No dosage adjustments because of insufficient data.</li></ul>', 'Low', '', 'Sledge GW Jr, Toi M, et al. MONARCH 2: Abemaciclib in Combination With Fulvestrant in Women With HR+/HER2- Advanced Breast Cancer Who Had Progressed While Receiving Endocrine Therapy. J Clin Oncol. 2017;35(25):2875-2884.', 'METASTATIC', '<p><strong>Abemaciclib:</strong>&nbsp;150 mg PO BID<br><strong>Fulvestrant:</strong>&nbsp;500 mg IM on days 1 and 15 of the first cycle, then 500mg IM on day 1 for all subsequent cycles</p><p>Repeat cycle every 28 days.</p>', 16);
