insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1091, null, 'Sunitinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Sunitinib:</strong><br>If Child-Pugh A or B, no dose reduction&nbsp;<br>If Child-Pugh C, reduce dose by 50%</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Sunitinib:</strong><br>If CrCl &gt; 42 mL/min, no dose reduction</li></ul>', 'Low', null, null, null, '<p><strong>Sunitinib:</strong> 50 mg/day PO for 4 weeks<br>Repeat cycle every 6 weeks</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (799, null, 'Procarbazine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Procarbazine:</strong><br>If AST, ALT are 1.6 to 6 times ULN: 75% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If AST, ALT are &nbsp;&gt;6 times ULN: Use clinical judgment &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If bilirubin &gt;5 mg/dL or AST, ALT &gt;3 times ULN: Avoid use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Procarbazine:</strong><br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'High', null, null, null, '<p><strong>Procarbazine:</strong> 150 mg/m2 PO daily divided into 3 doses.<br>Repeat daily.</p>', null);
