insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (737, '', 'CPT-11 (monthly schedule)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Irinotecan: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin &gt;1.2 to ≤2 mg/dL: &nbsp;Reduce initial dose by one dose level &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;2 mg/dL: Do not use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Irinotecan:&nbsp;</strong><br>No dose reduction.</li></ul>', 'Moderate', '', '', 'HEPATIC ARTERY INFUSION', '<p><strong>CPT-11:&nbsp;</strong>350 mg/m2&nbsp;IV on day 1</p><p>Repeat every 3 weeks.</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (699, '', 'Gemcitabine + Capecitabine (GEM-CAP)- Adjuvant', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'ADJUVANT', '<p><strong>Gemcitabine:&nbsp;</strong>1,000 mg/m<sup>2</sup>&nbsp;IV on days 1, 8, and 15</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (633, '', 'Binimetinib + Encorafenib + Cetuximab (BEC)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Binimetinib:</strong><br>If bilirubin &gt;1 to ≤1.5 times ULN: No dose reduction.<br>If bilirubin &gt;1.5 to ≤3 times ULN: 30 mg PO bid, dosage.</li><li><strong>Encorafenib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction.</li><li><strong>Cetuximab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Binimetinib:</strong><br>No dose reduction.</li><li><strong>Encorafenib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Cetuximab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li></ul>', 'Medium ', '', '', 'METASTATIC', '<p><strong>Binimetinib:&nbsp;</strong>45 mg PO bid</p><p><strong>Encorafenib:&nbsp;</strong>300 mg PO daily</p><p><strong>Cetuximab:&nbsp;</strong>400 mg/m2&nbsp;IV loading dose, then 250 mg/m2&nbsp;IV weekly</p><p>Continue treatment until disease progression. This regimen is only for patients with&nbsp;<i>BRAF</i> V600E-mutant mCRC.</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (610, '', 'Capecitabine + Irinotecan (XELIRI)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Irinotecan: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin &gt;1.2 to ≤2 mg/dL: &nbsp;Reduce initial dose by one dose level &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;2 mg/dL: Do not use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Irinotecan:&nbsp;</strong><br>No dose reduction.</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>Capecitabine:&nbsp;</strong>1,000 mg/m2&nbsp;PO bid on days 1-14</p><p><strong>Irinotecan:&nbsp;</strong>250 mg/m2&nbsp;IV on day 1</p><p>Repeat cycle every 21 days. May decrease the dose of capecitabine to 850 mg/m2&nbsp;PO twice daily and the dose of irinotecan to 200 mg/m2&nbsp;IV to reduce risk of toxicity without compromising clinical efficacy.</p><p><strong>OR</strong></p><p><strong>Capecitabine:&nbsp;</strong>1,500 mg/m2&nbsp;PO bid on days 2-8</p><p><strong>Irinotecan:&nbsp;</strong>150 mg/m2&nbsp;IV on day 1</p><p>Repeat every 14 days.</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (738, '', 'Cetuximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cetuximab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cetuximab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'HEPATIC ARTERY INFUSION', '<p><strong>Cetuximab:&nbsp;</strong>400 mg/m2&nbsp;IV loading dose, then 250 mg/m2&nbsp;IV weekly</p><p>Repeat cycle on a weekly basis.</p><p><strong>OR</strong></p><p><strong>Cetuximab:&nbsp;</strong>500 mg/m2&nbsp;IV every 2 weeks (no loading dose is necessary)</p><p>Repeat cycle every 2 weeks.</p><p>&nbsp;</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (621, '', 'Cetuximab + Bevacizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bevacizumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li><li><strong>Cetuximab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bevacizumab: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> No dose reduction<strong>&nbsp;</strong></li><li><strong>Cetuximab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Cetuximab:&nbsp;</strong>400 mg/m<sup>2</sup>&nbsp;IV loading dose, then 250 mg/m<sup>2</sup>&nbsp;IV weekly</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (726, '', 'Floxuridine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Floxuridine: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> If bilirubin ≤1.4 mg/dL: 80% dosage.<br>If bilirubin &gt;1.4 to &lt;1.8 mg/dL: 50% dosage.<br>If bilirubin &gt; 2.4 mg/dL: No recommendations available for dosage.<br><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Renal Impairment</strong></li><li><strong>Floxuridine:</strong><br>No dose reduction</li></ul>', 'Low', '', '', 'HEPATIC ARTERY INFUSION', '<p><strong>Floxuridine (FUDR):&nbsp;</strong>0.3 mg/kg/day HAI on days 1-14</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (627, '', 'FOLFIRI + Panitumumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Irinotecan: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin &gt;1.2 to ≤2 mg/dL: &nbsp;Reduce initial dose by one dose level &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;2 mg/dL: Do not use.</li><li><strong>Panitumumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>No dose reduction.</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li><li><strong>Irinotecan:&nbsp;</strong><br>No dose reduction.</li><li><strong>Panitumumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>Irinotecan:&nbsp;</strong>180 mg/m2&nbsp;IV on day 1</p><p><strong>5-Fluorouracil:&nbsp;</strong>400 mg/m2&nbsp;IV bolus on day 1, followed by 2,400 mg/m2&nbsp;IV continuous infusion for 46 hours on days 1 and 2</p><p><strong>Leucovorin:&nbsp;</strong>400 mg/m2&nbsp;IV on day 1 as a 2-hour infusion before 5-fluorouracil</p><p><strong>Panitumumab:&nbsp;</strong>6 mg/kg IV on day 1</p><p>Repeat cycle every 2 weeks.</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1142, null, 'Aminoglutethimide', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Aminoglutethimide:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Aminoglutethimide:</strong><br>No dose reduction</li></ul>', 'N/A', null, null, null, '<p><strong>Aminoglutethimide: </strong>250 mg PO qid, if tolerated may increase to 500 mg PO qid.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (744, '', 'Nivolumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Nivolumab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Nivolumab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'HEPATIC ARTERY INFUSION', '<p><strong>Nivolumab:&nbsp;</strong>3 mg/kg IV on day 1</p><p>Repeat cycle every 2 weeks. May also consider administering using a fixed dose of 240 mg IV on day 1 every 2 weeks or 480 mg IV on day 1 every 4 weeks.</p>', 93);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1097, null, 'Brigatinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Brigatinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild to moderate impairment (Child Pugh class A or B): No dose reduction. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If severe impairment (Child Pugh class C): Reduce dose by ~40% (eg, from 180 mg once daily to 120 mg once daily, from 120 mg once daily to 90 mg once daily, from 90 mg once daily to 60 mg once daily).</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Brigatinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &gt;30 to 89 mL/min: No dosage adjustment necessary. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 15 to 29 mL/min: Reduce dose by ~50% (eg, from 180 mg once daily to 90 mg once daily, from 90 mg once daily to 60 mg once daily).</li></ul>', 'Low', null, null, null, '<p><strong>Brigatinib: </strong>90 mg PO daily for 7 days and then 180 mg PO daily<br>Continue treatment until