insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (677, '', 'ECX', '<p><strong>Hepatic Impairment:</strong></p><ul><li><strong>Epirubicin</strong>:<br>If bilirubin 1.2-3 mg/dL or SGOT 2 to 4 times upper limit normal: reduce dose by 50%<br>If bilirubin &gt; 3mg/dL or SGOT &gt; 4 times upper limit normal: reduce dose by 75%</li><li><strong>Cisplatin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Capecitabine</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>5-Fluorouracil</strong>:<br>No dose reduction</li><li><strong>Epirubicin</strong><br>If serum Cr &gt;&nbsp;5 mg/dL, reduce dose by 50%</li><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'High', '', '', 'METASTATIC', '<p><strong>Epirubicin:&nbsp;</strong>50 mg/m<sup>2</sup>&nbsp;IV on day 1</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (649, '', 'CAPOX + Radiation Therapy', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Oxaliplatin:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: 75% dosage</li></ul>', 'Moderate', '', '', 'NEOADJUVANT', '<p><strong>Oxaliplatin:&nbsp;</strong>85 mg/m<sup>2</sup>&nbsp;IV on days 1, 15, and 29</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (705, '', 'Capecitabine-based Chemoradiotherapy (SCALOP Regimen)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'Low', '', '', 'LOCALLY ADVANCED', '<p><strong>Gemcitabine:&nbsp;</strong>1,000 mg/m2&nbsp;IV on days 1, 8, and 15</p><p><strong>Capecitabine:&nbsp;</strong>830 mg/m2&nbsp;PO twice daily on days 1-21</p><p>Repeat every 28 days for 3 cycles. Patients with stable or responding disease can receive one additional cycle, followed by concurrent chemoradiation.</p><p><strong>Capecitabine:&nbsp;</strong>830 mg/m2&nbsp;PO twice daily Monday-Friday for 6 weeks</p><p><strong>Radiation Therapy:&nbsp;</strong>180 cGy/day to a total dose of 5,040 cGy</p><p>Chemotherapy and radiation therapy started on the same day and given concurrently.</p>', 96);
