insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (678, '', 'EOX', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Epirubicin</strong>:<br>If bilirubin 1.2-3 mg/dL or SGOT 2 to 4 times upper limit normal: reduce dose by 50%<br>If bilirubin &gt; 3mg/dL or SGOT &gt; 4 times upper limit normal: reduce dose by 75%</li><li><strong>Oxaliplatin:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction</li><li><strong>Capecitabine</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Epirubicin</strong><br>If serum Cr &gt;&nbsp;5 mg/dL, reduce dose by 50%</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: 75% dosage</li><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Moderate', '', '', 'METASTATIC', '<p><strong>Epirubicin:&nbsp;</strong>50 mg/m<sup>2</sup>&nbsp;IV on day 1</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (666, '', 'FLOT4 Trial', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Docetaxel:</strong><br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline Phosphatase &gt;2.5 upper normal limit, do not use</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>No dose reduction.</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Docetaxel:</strong><br>No dose reduction</li><li><strong>Oxaliplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl &gt;30 mL/min: 100% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl &lt;30 mL/min: 75% dosage</li><li><strong>Leucovorin:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>5-Fluorouracil</strong>:<br>No dose reduction.</li></ul>', 'Moderate', '', '', '', '<p><strong>Preoperative FLOT chemotherapy:</strong></p><p><strong>Docetaxel:&nbsp;</strong>50 mg/m2&nbsp;IV on day 1</p><p><strong>Oxaliplatin:&nbsp;</strong>85 mg/m2&nbsp;IV on day 1</p><p><strong>Leucovorin:&nbsp;</strong>200 mg/m2&nbsp;IV on day 1</p><p><strong>5-Fluorouracil:&nbsp;</strong>2,600 mg/m2&nbsp;IV continuous infusion over 24 hours on day 1</p><p>Repeat every 2 weeks for 4 cycles. Followed by gastrectomy and then adjuvant chemotherapy in patients who had total gross removal of disease with negative margins.</p><p><strong>Adjuvant Chemotherapy:</strong></p><p><strong>Docetaxel:&nbsp;</strong>50 mg/m2&nbsp;IV on day 1</p><p><strong>Oxaliplatin:&nbsp;</strong>85 mg/m2&nbsp;IV on day 1</p><p><strong>Leucovorin:&nbsp;</strong>200 mg/m2&nbsp;IV on day 1</p><p><strong>5-Fluorouracil:&nbsp;</strong>2,600 mg/m2&nbsp;IV continuous infusion over 24 hours on day 1</p><p>Repeat every 2 weeks for 4 cycles. Only patients with resectable gastric and gastroesophageal junction tumors were included in this trial.</p>', 92);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (708, '', 'Gemcitabine + Docetaxel + Capecitabine (GTX)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Docetaxel:</strong><br>If bilirubin &gt; upper normal limit, do not use<br>If&nbsp; SGOT and/or SGPT &gt; 1.5 upper normal limit plus alkaline Phosphatase &gt;2.5 upper normal limit, do not use</li><li><strong>Capecitabine</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>Docetaxel:</strong><br>No dose reduction</li><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Gemcitabine:&nbsp;</strong>750 mg/m<sup>2</sup>&nbsp;IV over 75 minutes on days 4 and 11</p>
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (1099, null, 'Durvalumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Durvalumab:</strong><br>Dose modification not provided by the manufacturer</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Durvalumab:</strong><br>Dose modification not provided by the manufacturer</li></ul>', 'Low', null, null, null, '<p><strong>Durvalumab: </strong>10 mg/kg IV on day 1<br>Repeat cycle every 14 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (714, '', 'Olaparib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Olaparib:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Olaparib:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 51 to 80 mL/min: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If CrCl 31 to 50 mL/min: Reduce dose to 200 mg twice daily. &nbsp; &nbsp; &nbsp; &nbsp; If CrCl ≤30 mL/min: There are no dosage adjustments because &nbsp; &nbsp; of inadequate study.</li></ul>', 'Low', '', '', 'METASTATIC', '<p><strong>Olaparib:&nbsp;</strong>300 mg PO twice daily</p><p>Continue therapy until evidence of disease progression. This regimen is for patients who have germline <i>BRCA1/2&nbsp;</i>mutation and have received at least 16 weeks of platinum-based chemotherapy.</p>', 96);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (703, '', 'RTOG Chemoradiation Regimen', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use<br>No dose reduction for infusional administration</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li><li><strong>5-Fluorouracil</strong>:<br>No dose reduction</li></ul>', 'Low', '', '', 'LOCALLY ADVANCED', '<p><strong>Gemcitabine:&nbsp;</strong>1,000 mg/m2&nbsp;IV on days 1, 8, and 15</p><p><strong>Followed by concurrent chemoradiation:</strong></p><p><strong>5-Fluorouracil:&nbsp;</strong>250 mg/m2/day IV continuous infusion during radiation therapy</p><p><strong>Radiation Therapy:&nbsp;</strong>180 cGy/day to a total dose of 5,040 cGy</p><p>Chemotherapy and radiation therapy started on the same day and given concurrently.</p><p><strong>After chemoradiation:</strong></p><p><strong>Gemcitabine:&nbsp;</strong>1,000 mg/m2&nbsp;IV on days 1, 8, and 15</p><p>Repeat every 4 weeks for a total of 3 cycles. Adjuvant chemotherapy is given to patients with complete gross total resection of pancreatic adenocarcinoma.</p><p>&nbsp;</p>', 96);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (805, null, 'Capecitabine + Erlotinib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>No dose reduction</li><li><strong>Erlotinib:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Capecitabine</strong>:<br>If CrCl 30-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 30 mL/min, do not use</li><li><strong>Erlotinib:&nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; No dose reduction.</li></ul>', 'Low', null, null, null, '<p><strong>Capecitabine:&nbsp;</strong>1,000 mg/m2&nbsp;PO twice daily on days 1-14</p><p><strong>Erlotinib:&nbsp;</strong>150 mg PO daily.</p><p>Repeat cycles every 21 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (530, 'Bleomycin: Blenoxane<br>Ifosfamide: Ifex<br>Mesna: Mesnex<br>Carboplatin: Paraplatin', 'BIC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bleomycin</strong>:<br>No dose reduction</li><li><strong>Ifosfamide</strong>:<br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Carboplatin</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bleomycin</strong><br>If CrCl &gt;50 mL/min: No dose reduction<br>If CrCl 40-50 mL/min: 70% of normal dose<br>If CrCl 30-40 mL/min: 60% of normal dose<br>If CrCl 20-30 mL/min: 55% normal dose<br>If CrCl 10-20 mL/min: 45% normal dose<br>If CrCl 5-10 mL/min: 40% normal dose</li><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% normal dose.</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Carboplatin</strong>:<br>Dosing based on AUC</li></ul>', 'High', '', 'Murad AM, Triginelli SA, et al. Phase II trial of bleomycin, ifosfamide, and carboplatin in metastatic cervical cancer. J Clin Oncol. 1994;12(1):55-9.', '', '<p><strong>Bleomycin:</strong>&nbsp;30 U IV on day 1<br><strong>Ifosfamide:</strong>&nbsp;2000 mg/m2&nbsp;IV on days 1-3<br><strong>Mesna:</strong>&nbsp;400 mg/m2&nbsp;IV, 15min before ifosfamide dose, then 400 mg/m2&nbsp;IV at 4 and 8 hours following ifosfamide<br><strong>Carboplatin:&nbsp;</strong>200 mg/m2&nbsp;IV on day 1</p><p>Repeat cycle every 21 days.</p>', 18);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (291, 'Bortezomib : Velcade', 'Bortezomib', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin&nbsp;1-1.5 times upper normal limit: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin&nbsp;&gt;1.5 times upper normal limit: Reduce the initial dose to 0.7 mg/m2. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li></ul>', 'Low', '<b>Bortezomib</b>', 'Richardson PG, Barlogie B, Berenson J, et al. A phase 2 study of bortezomib in relapsed, refractory myeloma. $N Engl J Med. 2003;348(26):2609-2617.$', null, '<p><strong>Bortezomib:</strong> 1.3 mg/m2 IV or SC on days 1, 4, 8 and 11.<br>Repeat cycle every 21 days</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (172, 'Cisplatin : CDDP', 'Cisplatin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li></ul>', 'High', '<b>Cisplatin</b>', 'Keys HM, Bundy BN, Stehman FB, et al. Cisplatin, radiation, and adjuvant hysterectomy compared with radiation and adjuvant hysterectomy compared with radiation and adjuvant hysterectomy for bulky stage IB cervical carcinoma. $N Engl J Med. 1999;340(15):1154-1161.$', null, '<b>Cisplatin</b> 40 mg/m<sup>2</sup> IV once per week during RT for up to 6', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (7, 'Carboplatin : Paraplatin<br>Cetuximab : Erbitux<br>Fluorouracil : 5-FU', 'Carboplatin/Fluorouracil/Cetuximab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin:</strong><br>No dose reduction</li><li><strong>5-Fluorouracil</strong>:<br>If bilirubin &lt; 5 mg/dL, no dose reduction<br>If bilirubin &gt; 5 mg/dL, do not use<br>No dose reduction for infusional administration</li><li><strong>Cetuximab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on <strong>mg/m2</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% of the usual dose. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% of the usual dose.</li><li><strong>Fluorouracil:</strong><br>No dose reduction</li><li><strong>Cetuximab:&nbsp;</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction.</li></ul>', 'High', '<b>Carboplatin/Fluorouracil/Cetuximab</b>', 'Vermorken JB, Mesia R, Rivera F, et al. Platinum-based chemotherapy plus cetuximab in head and neck cancer. $N Engl J Med. 2008;359(11):1116-1127.$', null, '<p><strong>Carboplatin:</strong> AUC of 5, IV on day 1<br><strong>5-Fluorouracil:</strong> 1,000 mg/m2/day IV continuous infusion, days 1-4<br><strong>Cetuximab:</strong> 400 mg/m2 IV loading dose over 4 hours, day 1, cycle 1 <strong>THEN</strong> 250 mg/m2 IV over 1 hour, every week.</p><p>Repeat every 21 days for a maximum of 6 cycles. If no evidence of disease progression at the end of 6 cycles, can continue with weekly cetuximab.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (523, 'Cisplatin: CDDP', 'Cisplatin/Radiation', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li></ul>', 'High', '', 'Rose PG, Bundy BN, et al. Concurrent cisplatin-based radiotherapy and chemotherapy for locally advanced cervical cancer. N Engl J Med. 1999;340(15):1144-53.', '', '<p><strong>Radiation therapy:&nbsp;</strong>1.8 to 2 Gy per fraction (total dose, 45 Gy)<br><strong>Cisplatin:</strong>&nbsp;40 mg/m2&nbsp;IV weekly (maximal dose, 70mg per week)</p><p>Cisplatin is given 4 hours before radiation therapy on weeks 1-6.</p>', 18);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (927, null, 'Bortezomib (Mantle cell lymphoma)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If<strong> </strong>bilirubin &gt;1 to 1.5 times ULN: 100% of the dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;1.5 ULN: Reduce initial dose to 0.7 mg/m2.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li></ul>', 'low', null, null, null, '<p><strong>Bortezomib:</strong> 1.3 mg/m2 IV or SC on days 1, 4, 8, and 11<br>Repeat cycle every 21 days for up to 17 cycles.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (534, 'Cisplatin: CDDP<br>Gemcitabine: Gemzar', 'Cisplatin + Gemcitabine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Gemcitabine</strong>:<br>No dose reduction</li><li><strong>Cisplatin</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Gemcitabine</strong>:<br>No dose reduction</li><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li></ul>', 'High', '', 'Monk BJ, et al. Proc Am Soc Clin Oncol 2008;26:(LBA5504). --- Brewer CA, Blessing JA, et al. Cisplatin plus gemcitabine in previously treated squamous cell carcinoma of the cervix: a phase II study of the Gynecologic Oncology Group. Gynecol Oncol. 2006;100(2):385-8.', '', '<p><strong>Cisplatin:</strong>&nbsp;50 mg/m2&nbsp;IV on day 1<br><strong>Gemcitabine:</strong>&nbsp;1000 mg/m2&nbsp;IV on days 1 and 8</p><p>Repeat cycle every 21 days for up to 6 cycles.</p><p><strong>OR</strong></p><p><strong>Cisplatin:</strong>&nbsp;30 mg/m2&nbsp;IV on day 1<br><strong>Gemcitabine:</strong>&nbsp;800 mg/m2&nbsp;IV on days 1 and 8</p><p>Repeat cycle every 28 days.</p>', 18);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (533, 'Cisplatin: CDDP<br>Irinotecan: Camptosar', 'Cisplatin + Irinotecan', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong><br>No dose reduction</li><li><strong>Irinotecan: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin &gt;1.2 to ≤2 mg/dL: &nbsp;Reduce initial dose by one dose level &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;2 mg/dL: Do not use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Irinotecan</strong>:<br>No dose reduction</li></ul>', 'High', '', 'Chitapanarux I, Tonusin A, et al. Phase II clinical study of irinotecan and cisplatin as first-line chemotherapy in metastatic or recurrent cervical cancer. Gynecol Oncol. 2003;89(3):402-7.', '', '<p><strong>Cisplatin:</strong>&nbsp;60 mg/m2&nbsp;IV on day 1<br><strong>Irinotecan:</strong>&nbsp;60 mg/m2&nbsp;IV on days 1, 8, and 15</p><p>Repeat cycle every 28 days.</p>', 18);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (169, 'Cisplatin : CDDP<br>Fluorouracil : 5-FU', 'Cisplatin + Fluorouracil', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br>No dose reduction</li><li><strong>Fluorouracil:</strong><br>If bilirubin &lt; 5 mg/dL, no dose reduction&nbsp;<br>If bilirubin &gt; 5 mg/dL, do not use&nbsp;<br>No dose reduction for infusional administration</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br><strong>&nbsp; </strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Fluorouracil:</strong><br>No dose reduction</li></ul>', 'High', '<b>Cisplatin/Fluorouracil</b>', 'Morris M, Eifel PJ, Lu J, et al. Pelvic radiation with concurrent chemoterapy compared with pelvic and para-aortic radiation for high-risk cervical cancer. $N Engl J Med. 1999;340(15):1137-1143.$<br> Whitney CW, Sausse W, Bundy BN, et al. Randomized commparison of fluorouuracil plus cisplatin versus hydroxyurea as an adjunct to radiation therapy in stage IIB-IVA carcinoma of the cervix with negative para-aortic lymph nodes: a Gynecologic Oncology Group and Southwest Oncology Group Study. $J Clin Oncol. 1999;17(5):1339-1348.$', null, '<p><strong>Cisplatin:</strong> 75 mg/m2 IV over 4 hours, day 1, given within 16 hours of the first dose of RT<br><strong>Fluorouracil:</strong> 4,000 mg/m2 IV over 96 hours, days 2-5<br>Repeat every 21 days for 3 cycles<br><strong>OR</strong><br><strong>Cisplatin:</strong> 50 mg/m2/d IV, days 1, 29, given 4 hours before the first dose of RT<br><strong>Fluorouracil:</strong> 1,000 mg/m2/d IV, days 2-5, 30-33</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (527, 'Cisplatin: CDDP<br>Paclitaxel: Taxol<br>Bevacizumab: Avastin', 'Cisplatin + Paclitaxel + Bevacizumab', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Paclitaxel</strong>:<br>For 3-hour infusion:<br>If transaminase &lt; 10 upper normal limit and bilirubin &lt; 1.25 upper normal limit, no dose reduction (175mg/m2)<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.26-2 upper normal limit, reduce to 135 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin2.01-5 upper normal limit, reduce to 90 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 5 upper normal limit, do not use<br>For 24-hour infusion:<br>If transaminase &lt; 2 upper normal limit or bilirubin &lt; 1.5 mg/mL, no dose reduction (135 mg/m2)<br>If transaminase 2-10 upper normal limit and bilirubin &lt; 1.5 mg/dL, reduce to 100 mg/m2<br>If transaminase &lt; 10 upper normal limit and bilirubin 1.6-7.5 mg/dL, reduce to 50 mg/m2<br>If transaminase &gt; 10 upper normal limit or bilirubin &gt; 7.5 mg/dL, do not use</li><li><strong>Bevacizumab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Paclitaxel</strong>:<br>No dose reduction</li><li><strong>Bevacizumab:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;No dose reduction</li></ul>', 'High', '', 'Tewari KS, et al. J Clin Oncol 2013;31:Suppl (abstract 3).', '', '<p><strong>Cisplatin:&nbsp;</strong>50 mg/m2&nbsp;IV on day 1<br><strong>Paclitaxel:</strong>&nbsp;135-175 mg/m2&nbsp;IV on day 1<br><strong>Bevacizumab:</strong>&nbsp;15 mg/kg IV on day 1</p><p>Repeat cycle every 21 days.</p>', 18);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (211, '', 'Liposomal Daunorubicin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Liposomal daunorubicin:</strong><br>If bilirubin 1.2 to 3 mg/dL: 75% of normal dose. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin &gt;3 mg/dL: 50% of normal dose.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Liposomal daunorubicin:</strong><br>&nbsp;If serum creatinine &gt;3 mg/dL: 50% of normal dose.</li></ul>', 'Low', '<b>Liposomal daunorubicin</b>', 'Rosenthal E, Poizot-Martin I, Saint Marc T, et al. Phase Ivstudy of liposomal daunorubicin (DaunoXome) in AIDS-related Kaposi sarcoma. $AM J Clin Oncol.2002;25(1): 57-59$', null, '<p><strong>Liposomal daunorubicin:</strong> 40 mg/m2 IV over 60 min on day 1.<br>Repeat every 14 days. Continue until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (265, 'Cyclophosphamide : Cytoxan<br>Fludarabine : Fludara', 'CF', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL: Reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL: Do not use</li><li><strong>Fludarabine:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-29 mL/min: Reduce dose by 25%<br>If CrCl &lt; 10 mL/min: Reduce dose by 50%</li><li><strong>Fludarabine:</strong><br>If CrCl 10-50 mL/min: Reduce dose by 25%&nbsp;<br>If CrCl &lt;10 mL/min: Reduce dose by 50%</li></ul>', 'Moderate', '<b>FC</b>', 'Catovsky D, Richards S, Oscier D, et al. Assesmentof fludarabine plus cyclo-phosphamide for patients with chronic lymphocytic leukemia (the LRF CLL4 trial): a randomized ocntrolled trial. $Lancet. 2007; 370(9583):230-239.$', null, '<p><strong>Cyclophosphamide:</strong> 1,000 mg/m2 IV on day 1.</p><p><strong>Fludarabine:</strong> 20 mg/m2 IV on days 1-5</p><p>Trimethoprim/sulfamethoxazole (Bactrim DS) 1 tablet PO bid<br>Repeat every 21-28 days.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (525, 'Cisplatin: CDDP<br>Topotecan: Hycamtin ', 'Cisplatin/Topotecan', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Topotecan</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Topotecan</strong>:<br>If CrCl &gt; 40 mL/min, no dose reduction<br>If CrCl 20-39 mL/min, reduce to 0.75 mg/m2/dose</li></ul>', 'High', '', 'Fiorica J, Holloway R, et al. Phase II trial of topotecan and cisplatin in persistent or recurrent squamous and nonsquamous carcinomas of the cervix. Gynecol Oncol. 2002;85(1):89-94.', '', '<p><strong>Cisplatin:</strong>&nbsp;50 mg/m2&nbsp;IV on day 1<br><strong>Topotecan:</strong>&nbsp;0.75 mg/m2/day IV on days 1-3</p><p>Repeat cycle every 21 days.</p>', 18);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (281, 'Bortezomib : Velcade<br>Dexamethasone : various<br>Doxorubicin : Adriamycin, various', 'Bortezomib/Doxorubicin/Dexamethasone', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>If bilirubin&nbsp;1-1.5 times upper normal limit: 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If bilirubin&nbsp;&gt;1.5 times upper normal limit: Reduce the initial dose to 0.7 mg/m2.</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Bortezomib: &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong>No dose reduction</li><li><strong>Doxorubicin</strong>:<br>No dose reduction</li><li><strong>Dexamethasone:</strong><br>No dose reduction</li></ul>', 'Moderate', '<b>Bortezomib/Doxorubicin/Dexamethasone</b>', 'Sonneveld P, van der Holt B, Schmidt-Wolf IGH, et al. First analysis of HOVON-65/GMMG-HD4 randomized phase III trial comparing bortezomib, adriamycin, dexamethasone (PAD) vs VAD as induction treatment prior to high dose melphalan (HDM) in patients with newly diagnosed multiple myeloma (MM). $Blood. 2008;112(11):Abstract 653.$', null, '<p><strong>Bortezomib:</strong> 1.3 mg/m2 IV, bolus days 1, 4, 8, 11<br><strong>Doxorubicin:</strong> 9 mg/m2 IV, bolus days 1-4<br><strong>Dexamethasone:</strong> 40 mg PO days 1-4, 9-12, 17-20</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (928, null, 'Ibrutinib (Mantle cell lymphoma)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Ibrutinib:</strong> &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If mild impairment (Child-Pugh class A): Decrease dose to 140 mg PO daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If moderate impairment (Child-Pugh class B): Decrease dose to 70 mg PO daily. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (Child-Pugh class C): Do not use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Ibrutinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Dosage adjustment not provided by the manufacturer.</li></ul>', 'Low', null, null, null, '<p><strong>Ibrutinib:</strong> 560 mg/day PO<br>Continue treatment until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (929, null, 'Acalabrutinib (Mantle cell lymphoma)', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Acalabrutinib:</strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If mild to moderate impairment (Child-Pugh class A and B): &nbsp; 100% dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If severe impairment (Child-Pugh class C): Do not use.</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Acalabrutinib:</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Dosage adjustment not provided by the manufacturer.</li></ul>', 'Low', null, null, null, '<p><strong>Acalabrutinib: </strong>100 mg PO bid<br>Continue treatment until disease progression.</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (532, 'Cisplatin: CDDP<br>Vinorelbine: Navelbine', 'Cisplatin/Vinorelbine', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Vinorelbine</strong>:<br>If bilirubin 2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin:</strong><br><strong>&nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Vinorelbine</strong>:<br>No dose reduction</li></ul>', 'High', '', 'Pignata S, Silvestro G, et al. Phase II study of cisplatin and vinorelbine as first-line chemotherapy in patients with carcinoma of the uterine cervix. J Clin Oncol. 1999;17(3):756-60.', '', '<p><strong>Cisplatin:</strong>&nbsp;80 mg/m2&nbsp;IV on day 1<br><strong>Vinorelbine:</strong>&nbsp;25 mg/m2&nbsp;IV on days 1 and 8</p><p>Repeat cycle every 21 days.</p>', 18);
