insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (948, null, 'CODOX-M/IVAC', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If bilirubin 3-5 mg/dL or SGOT &gt; 180 mg/dL, reduce dose by 25%&nbsp;<br>If bilirubin &gt;5 mg/dL, do not use</li><li><strong>Doxorubicin</strong>:<br>If bilirubin 1.2-3 mg/dL, reduce dose by 50%<br>If bilirubin 3.1-5 mg/dL, reduce dose by 75%<br>If bilirubin &gt; 5 mg/dL, do not use</li><li><strong>Cytarabine:</strong><br>If bilirubin 1.5-3 mg/dL, reduce dose by 25%<br>If bilirubin &gt; 3 mg/dL, reduce dose by 50%</li><li><strong>Etoposide:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li><li><strong>Ifosfamide:</strong><br>No dose reduction</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>If bilirubin 1.5-3 mg/dL or SGOT 60-180 mg/dL, reduce dose by 50%<br>If bilirubin &gt; 3 mg/dL or SGOT &gt; 180 mg/dL, do not use</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cyclophosphamide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Doxorubicin</strong>:<br>No dose reduction</li><li><strong>Cytarabine:</strong><br>If CrCl 46 to 60 mL/min: 60% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/minute: 50% of normal dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Consider use of alternative drug.</li><li><strong>Etoposide:</strong><br>If CrCl 10-50 mL/min, reduce dose by 25%&nbsp;<br>If CrCl &lt; 10 mL/min, reduce dose by 50%</li><li><strong>Ifosfamide</strong>:<br>&nbsp;If CrCl ≥10 mL/min: 100% normal dose &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;10 mL/min: 75% normal dose.</li><li><strong>Leucovorin:</strong><br>No dose reduction</li><li><strong>Mesna:</strong><br>No dose reduction</li><li><strong>Vincristine:</strong><br>No dose reduction</li></ul>', 'High', null, null, null, '<p><i><strong>CODOX-M</strong></i> (Cycles 1 and 3; cycles begin when ANC &gt;1,000/mm3)</p><p><strong>Cyclophosphamide:</strong> 800 mg/m2 IV on day 1, followed by 200 mg/m2/day IV on days 2 to 5</p><p><strong>Vincristine:</strong> 1.5 mg/m2/day IV (no maximum dose) days 1 and 8 (cycle 1) and days 1, 8, and 15 (cycle 3; day 15 in cycle 3 only if no neuropathy)</p><p><strong>Doxorubicin: </strong>40 mg/m2 IV on day 1</p><p><strong>Methotrexate:</strong> 1,200 mg/m2 IV (loading dose) over 1 hour day 10, followed by 240 mg/m2/hour IV for 23 hours (total of 5,520 mg/m2 over 23 hours) day 10</p><p><strong>Leucovorin Calcium:</strong> 192 mg/m2 &nbsp;IV on day 11 (begin 36 hours after the start of methotrexate infusion), followed by 12 mg/m2 IV every 6 hours until methotrexate level &lt;5 x 10-8 micromolar</p><p><strong>CNS Prophylaxis:</strong></p><p><strong>Cytarabine: </strong>Intrathecal: 70 mg/day (adjust to age-appropriate dose if ≤3 years of age) days 1 and 3</p><p><strong>Methotrexate:</strong> Intrathecal: 12 mg (adjust to age-appropriate dose if ≤3 years of age) day 15</p><p><strong>Sargramostim:</strong> 7.5 mcg/kg/day SC beginning day 13, continue until ANC &gt;1,000/mm3</p><p><strong>Note:</strong> If CNS involvement is present, administer additional intrathecal treatment in cycle 1: Cytarabine 70 mg (adjust to age-appropriate dose if ≤3 years of age) day 5 and methotrexate 12 mg (adjust to age-appropriate dose if ≤3 years of age) day 17</p><p><i><strong>IVAC</strong></i> (Cycles 2 and 4; cycles begin when ANC &gt;1,000/mm3)</p><p><strong>Ifosfamide:</strong> 1,500 mg/m2/day IV on days 1 to 5</p><p><strong>Mesna:</strong> 360 mg/m2 IV every 3 hours days 1 to 5</p><p><strong>Etoposide:</strong> 60 mg/m2/day IV on days 1 to 5</p><p><strong>Cytarabine:</strong> 2,000 mg/m2 &nbsp;IV every 12 hours, for 4 doses, days 1 and 2</p><p><strong>Methotrexate:</strong> Intrathecal: 12 mg day 5</p><p><strong>Sargramostim:</strong> 7.5 mcg/kg/day SC beginning day 7, continue until ANC &gt;1000/mm3</p>', null);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (568, 'Gemcitabine: Gemzar<br>Carboplatin: Paraplatin', 'Gemcitabine/Carboplatin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>No dose reduction</li><li><strong>Gemcitabine</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Carboplatin</strong>:<br>Dosing based on <strong>mg/m2</strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &gt;50 mL/min: 100 % dosage. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR 10 to 50 mL/min: 50% of the usual dose. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; If GFR &lt;10 mL/min: 25% of the usual dose.</li><li><strong>Gemcitabine</strong>:<br>No dose reduction</li></ul>', 'High', '', 'Thigpen T. The role of gemcitabine in first-line treatment of advanced ovarian carcinoma. Semin Oncol. 2006;33(2 Suppl 6):S26-32.', '', '<p><strong>Gemcitabine:</strong>&nbsp;1000 mg/m2&nbsp;IV on days 1 and 8<br><strong>Carboplatin:</strong>&nbsp;AUC of 4, IV on day 1</p><p>Repeat cycle every 21 days.</p>', 99);
insert into public.regimen_detail (id, brand_names, disp_name, dosage_modifications, emetogenic_potential, name, reference, regimen_type, schedule, subcancer_type3_id) values (567, 'Gemcitabine: Gemzar<br>Cisplatin: CDDP', 'Gemcitabine/Cisplatin', '<p><strong>Hepatic Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<br>No dose reduction</li><li><strong>Gemcitabine</strong>:<br>No dose reduction</li></ul><p><strong>Renal Impairment</strong></p><ul><li><strong>Cisplatin</strong>:<strong> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong>If CrCl 46 to 60 mL/min: 75% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl 31 to 45 mL/min: 50% dosage &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;If CrCl &lt;30 mL/min: Use alternative drug</li><li><strong>Gemcitabine</strong>:<br>No dose reduction</li></ul>', 'High', '', 'Nagourney RA, Brewer CA, et al. Phase II trial of gemcitabine plus cisplatin repeating doublet therapy in previously treated, relapsed ovarian cancer patients. Gynecol Oncol. 2003;88(1):35-9.', '', '<p><strong>Gemcitabine:</strong>&nbsp;800-1000 mg/m2&nbsp;IV on days 1 and 8<br><strong>Cisplatin:</strong>&nbsp;30 mg/m2&nbsp;IV on days 1 and 8</p><p>Repeat cycle every 21 days.</p>', 99);
